precision highp float;


// Constants
const vec3 UP = vec3(0.0, 1.0, 0.0);

const int MAX_STEPS = 200;
const float MIN_DIST = 0.0;
const float MAX_DIST = 30.0;
const float EPSILON = 1e-4;

const float MIN_DIST_SHADOW = 0.0;
const float MAX_DIST_SHADOW = 5.5;

const vec3 CAMERA_TARGET = vec3(0.0, -0.8, 0.0);
const float CAMERA_FOV = 60.0;

const vec3 LIGHT_CAMERA_TARGET = vec3(0.0, -0.5, 0.0);
const float LIGHT_CAMERA_SIZE = 2.3;

const float MAX_ROUGHNESS = 60.0;


// Need to set time in buffers - iTime is undefined in the Common tab
float time;


// Distance functions

float torusSDF(vec3 p, vec2 t)
{
	vec2 q = vec2(length(p.xz)-t.x,p.y);
	return length(q)-t.y;
}

float sphereSDF(vec3 p, float r)
{
    return length(p) - r;
}

float planeSDF(vec3 p, vec3 n)
{
    return length(p) * dot(normalize(p), n);
}

float capsuleSDF(vec3 p, float h, float r)
{
	p.y -= clamp(p.y, 0.0, h);
	return length(p) - r;
}


// Gotta fix those for my drivers
#define TAU 6.28318530718
float sinf(float arg) { return sin(mod(arg, TAU)); }
float cosf(float arg) { return cos(mod(arg, TAU)); }


// Rotations

vec3 rotX(vec3 p, float angle) {
    return mat3(
        vec3(1.0, 0.0, 0.0),
        vec3(0.0, cosf(angle), -sinf(angle)),
        vec3(1.0, sinf(angle), cosf(angle))
    ) * p;
}

vec3 rotY(vec3 p, float angle) {
    return mat3(
        vec3(cosf(angle), 0.0, sinf(angle)),
        vec3(0.0, 1.0, 0.0),
        vec3(-sinf(angle), 0.0, cosf(angle))
    ) * p;
}

vec3 rotZ(vec3 p, float angle) {
    return mat3(
        vec3(cosf(angle), -sinf(angle), 0.0),
        vec3(sinf(angle), cosf(angle), 0.0),
        vec3(1.0, 0.0, 1.0)
    ) * p;
}


// Structs

struct Material
{
    vec3 albedo;
    float id;
};

struct Hit
{
    float dist;
    int id;
};


// Tracing stuff

Hit minHit(Hit a, Hit b)
{
    if (a.dist < b.dist)
        return a;

    return b;
}

Hit sceneSDF(vec3 p)
{
    p -= vec3(-0.3, -0.2, 0.2);
    Hit scene = Hit(planeSDF(p + vec3(1.5), vec3(0.0, 1.0, 0.0)), 1);

    scene = minHit(scene, Hit(sphereSDF(p - vec3(-0.5, 0.2, 0.0), 0.7), 2));
    scene = minHit(scene, Hit(torusSDF(p - vec3(0.5, -0.4, 1.1), vec2(0.45, 0.3)), 3));
    scene = minHit(scene, Hit(capsuleSDF(p - vec3(1.1, -0.2, -0.7), 0.7, 0.5), 4));

    return scene;
}

Hit shortestDistanceToSurface(vec3 eye, vec3 marchingDirection, float start, float end) {
    Hit finalHit = Hit(start, -1);

    for (int i = 0; i < MAX_STEPS; i++) {
        Hit hit = sceneSDF(eye + finalHit.dist * marchingDirection);

        finalHit.dist += hit.dist;
        finalHit.id = hit.id;

        if (hit.dist < EPSILON) {
			break;
        }

        if (finalHit.dist >= end) {
            finalHit = Hit(end, -1);
            break;
        }
    }

    return finalHit;
}

vec3 getEye()
{
    return rotY(vec3(2.5, 2.5, 2.0), -time * TAU * 0.05);
}



vec4 getRegion(int i)
{
    const vec4[5] regions = vec4[]
    (
        vec4(0.0, 0.0, 0.2, 1.0),
        vec4(0.2, 0.0, 0.4, 0.2),
        vec4(0.4, 0.0, 1.0, 0.2),
        vec4(0.8, 0.2, 1.0, 1.0),
        vec4(0.2, 0.2, 0.8, 1.0)
    );

    return regions[i];
}

vec4 getRegion()
{
    int i = int(mod(time + 4.0, 5.0));

    return getRegion(i);
}


// Math stuff

// vec3 rayDirection(float fieldOfView, vec2 size, vec2 fragCoord) {
vec3 rayDirection(vec2 size, vec2 fragCoord) {
    vec2 xy = fragCoord - size / 2.0;
    float z = size.y / tan(radians(CAMERA_FOV) / 2.0);
    return normalize(vec3(xy, -z));
}

mat4 viewMatrix(vec3 eye, vec3 center, vec3 up) {
    // Based on gluLookAt man page
    vec3 f = normalize(center - eye);
    vec3 s = normalize(cross(f, up));
    vec3 u = cross(s, f);
    return mat4(
        vec4(s, 0.0),
        vec4(u, 0.0),
        vec4(-f, 0.0),
        vec4(0.0, 0.0, 0.0, 1)
    );
}


// Light stuff

const float a = 0.95;

float h()
{
    return clamp(0.0, 1.0, (mod(time - a * 0.5, 1.0) - (1.0 - a)) / a);
}

float g(float x)
{
    return floor(mod(x, 4.0)) * 0.2;
}

vec3 lightPosition()
{
    vec3 dir = vec3(0.0, 2.0, 0.0);

    dir = rotX(dir, -0.15 * TAU);

    float t = -0.4 + mix(g(time - a * 0.5), g(time + a * 0.5), smoothstep(0.0, 1.0, h()));

    dir = rotY(dir, t * TAU);

    return dir;
}

// This is a directional light - the direction is the same for each world position
vec3 lightDirection()
{
    return -normalize(lightPosition());
}

vec3 lightColor()
{
    return vec3(1.0, 1.0, 0.9);
}

mat4 lightToWorldMatrix()
{
    return viewMatrix(lightPosition(), LIGHT_CAMERA_TARGET, UP);
}

vec3 worldPointToLightPoint(vec3 worldPoint, out float lightDist)
{
    vec3 lightDir = lightDirection();
    vec3 lightPos = lightPosition();

    // Plane SDF allows easy distance calculation to ortographic plane.
    // -lightPos = lightDir * length(lightPos)
    lightDist = planeSDF(worldPoint, -lightPos);

    mat4 worldToLight = inverse(lightToWorldMatrix());

    // Quick maths
    vec3 lightPoint = worldPoint + lightDist * lightDir;
	lightPoint = (worldToLight * vec4(lightPoint, 0.0)).xyz;
    lightPoint = (lightPoint + LIGHT_CAMERA_SIZE) / (2.0 * LIGHT_CAMERA_SIZE);

    return lightPoint;
}


// Packing/encoding stuff

float map(in float val, in float startIn, in float endIn, in float startOut, in float endOut)
{
    float norm = (val - startIn) / (endIn - startIn);
    return norm * (endOut - startOut) + startOut;
}

vec3 quantize(vec3 value, float steps)
{
    return floor(value * steps) / steps;
}

float packVec3(vec3 vec, float steps)
{
    vec3 quantized = quantize(vec, steps);

    float x = quantized.x * steps * steps;
    float y = quantized.y * steps;
    float z = quantized.z;

    return (x + y + z) / (steps * steps);
}

vec3 unpackVec3(float packed, float steps)
{
    vec3 vec = vec3(0.0);

    vec.x = fract(packed);
    vec.y = fract(packed * steps);
    vec.z = fract(packed * steps * steps);

    return vec;
}

void packGbuffer(out vec4 packed, in float depth, in vec3 normal, in Material material)
{
    packed = vec4(0.0);

    // values equal to 1.0 are clipped, so have to lower them a bit
    packed.r = packVec3(normal * 0.49 + 0.49, 256.0);
    packed.g = packVec3(material.albedo * 0.999, 256.0);
    packed.b = packVec3(vec3(float(material.id) / 5.0), 5.0);
    packed.a = map(depth, MIN_DIST, MAX_DIST, 0.0, 1.0);
}

void unpackGbuffer(in vec4 packed, out float depth, out vec3 normal, out Material material)
{
    vec3 matParams = unpackVec3(packed.b, 100.0);

    depth = map(packed.a, 0.0, 1.0, MIN_DIST, MAX_DIST);
    normal = 2.0 * unpackVec3(packed.r, 256.0) - 1.0;

    material.id = matParams.r * 5.0;

    material.albedo = unpackVec3(packed.g, 256.0);
}

void packShadow(out vec4 packed, in float depth)
{
    packed = vec4(map(depth, MIN_DIST_SHADOW, MAX_DIST_SHADOW, 0.0, 1.0));
}

void unpackShadow(in vec4 packed, out float depth)
{
    depth = map(packed.r, 0.0, 1.0, MIN_DIST_SHADOW, MAX_DIST_SHADOW);
}

