// Buffer A: Render the G buffer.

// This tab has knowledge of materials, no other tab does.


Material materials[4] = Material[]
(
    //            albedo          id
    Material(vec3(0.6),           0.0), // floor
    Material(vec3(0.9, 0.3, 0.0), 1.0), // sphere
    Material(vec3(0.5, 1.0, 0.0), 2.0), // torus
    Material(vec3(0.5, 0.2, 1.0), 3.0)  // capsule
);

vec3 normal(vec3 p) {
    return normalize(vec3(
        sceneSDF(vec3(p.x + EPSILON, p.y, p.z)).dist - sceneSDF(vec3(p.x - EPSILON, p.y, p.z)).dist,
        sceneSDF(vec3(p.x, p.y + EPSILON, p.z)).dist - sceneSDF(vec3(p.x, p.y - EPSILON, p.z)).dist,
        sceneSDF(vec3(p.x, p.y, p.z + EPSILON)).dist - sceneSDF(vec3(p.x, p.y, p.z - EPSILON)).dist
    ));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    time = iTime;

	vec3 viewDir = rayDirection(iResolution.xy, fragCoord);

    vec3 eye = getEye();
    mat4 viewToWorld = viewMatrix(eye, CAMERA_TARGET, UP);
    vec3 worldDir = (viewToWorld * viewDir.xyzz).xyz;

    Hit hit = shortestDistanceToSurface(eye, worldDir, MIN_DIST, MAX_DIST);

    Material material;

    if (hit.id > 0)
    {    
        vec3 normal = normal(eye + hit.dist * worldDir);
        Material material = materials[hit.id - 1];

		packGbuffer(fragColor, hit.dist, normal, material);
    }

}

