// Buffer B: Render the shadow buffer.

// This is an ortographic camera oriented around the center.
// Parameters chosen for best quality.


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    time = iTime;

    vec2 coord = (-1.0 + 2.0 * fragCoord / iResolution.xy) * LIGHT_CAMERA_SIZE;

    vec3 eye = lightPosition();
    vec3 viewDir = vec3(0.0, 0.0, -1.0);

    mat4 lightToWorld = lightToWorldMatrix();

    // Identical direction, changed position, it's an ortographical projection  (bad raps I know)
    eye += (lightToWorld * vec4(coord.x, coord.y, 0.0, 0.0)).xyz;

    vec3 worldDir = (lightToWorld * vec4(viewDir, 0.0)).xyz;

    Hit hit = shortestDistanceToSurface(eye, worldDir, MIN_DIST_SHADOW, MAX_DIST_SHADOW);

    packShadow(fragColor, hit.dist);
}

