// Buffer C: Render the scene.

// This tab knows how to light the scene, no other tab does.


#define G_BUFFER (iChannel0)
#define SHADOW_BUFFER (iChannel1)
#define THIS_BUFFER (iChannel2)


vec3 lighting(in vec3 position, in vec3 normal, in vec3 viewDir, in Material material, in float shadow)
{
    vec3 lightDir = lightDirection();
    vec3 lightCol = lightColor();

    // Diffuse
    float ndotl = dot(normal, -lightDir);
    float diffuseStep = step(0.01, ndotl); // toon shading

    // Final terms
    float ambient = 0.05;
    float diffuse = diffuseStep * shadow;

    return (ambient * material.albedo + mix(material.albedo, lightCol, diffuse * 0.5)) * lightCol;
}

float shadow(in vec3 position)
{
    float lightDist;
	vec3 lightPoint = worldPointToLightPoint(position, lightDist);

    float shadowDist;
    unpackShadow(texture(SHADOW_BUFFER, lightPoint.xy), shadowDist);

    // Compare real distance to light with distance captured by the light "camera".
    // If they differ, this means that something is on the way.
    // This means one thing.
    // Shadow.
    // (Mapping.)
    float shadow = shadowDist - lightDist;
    shadow = step(0.0, shadow);

    if (clamp(lightPoint.x, 0.0, 1.0) != lightPoint.x
     || clamp(lightPoint.y, 0.0, 1.0) != lightPoint.y
     || shadowDist >= MAX_DIST_SHADOW - 1.0)
        shadow = 1.0;

    return shadow;
}

float outline(in vec2 coord, in float id)
{
    float depth;
    vec3 normal;
    Material material;


	float shouldOutline = 0.0;

    // Check circular neighbourhood
    for (float i = 0.0; i < TAU; i += TAU / 10.0)
    {
        vec2 offset = vec2(cos(i), sin(i) * iResolution.x / iResolution.y) * 0.002;
	    unpackGbuffer(texture(G_BUFFER, coord + offset), depth, normal, material);

        if (id != material.id)
        {
            shouldOutline = 1.0;
        }
    }

    return shouldOutline;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    time = iTime;

    vec2 coord = fragCoord / iResolution.xy;

    vec4 region = getRegion();

    // If not the current region and not the first frame,
    if (!(coord.x > region.x && coord.x < region.z && coord.y > region.y && coord.y < region.w) && iFrame > 0)
    {
        // Return previous frame's texel
        fragColor = texture(THIS_BUFFER, coord);
    }
    else
    {
        vec3 viewDir = rayDirection(iResolution.xy, fragCoord);

        vec3 eye = getEye();
        mat4 viewToWorld = viewMatrix(eye, CAMERA_TARGET, UP);
        vec3 worldDir = (viewToWorld * vec4(viewDir, 0.0)).xyz;

        float depth;
        vec3 normal;
        Material material;

        unpackGbuffer(texture(G_BUFFER, coord), depth, normal, material);


        vec3 position = eye + depth * worldDir;

        float shadow = shadow(position);

        fragColor.rgb = lighting(position, normal, worldDir, material, shadow);
		fragColor.a = 1.0;

        fragColor.rgb = mix(fragColor.rgb, vec3(0.0), outline(coord, material.id));
    }
}

#undef G_BUFFER
#undef SHADOW_BUFFER
#undef THIS_BUFFER

