// Tetrahedra tessellation

// also check original on shaderoo:
//  https://www.shaderoo.org/?shader=zh8I1X
//  ...for geometry and more detailed info


// check Buff_A for actual algorithm...

//##IMG##...stripped inline images away for size ( check on https://www.shaderoo.org/?shader=zh8I1X )

// Volume -> cubes -> tetrahedra -> cut by distance field

// scan the volume in small cubic cells (like marchin cubes)
// ...subdivide every cube into tetrahedra (simpler to cut that cubes) 
// ...cut tetrahedra by distance (simpler to cut that cubes)
// ...create cutting triangles

// you can also export the object by pressing (Geom->.OBJ) button

#define Res vec2(iResolution.xy)

void march(inout vec3 pos, vec3 dir)
{
    float eps=.001;
    for(int i=0;i<80;i++)
    {
        float d=dist(pos);
        pos+=dir*d*.7;
        if (d<eps) break;
    }
}

uniform float showRaymarch;

// do some raymarching here for comparison to original distance field

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 pos,dir;
    getTrafo(pos,dir,fragCoord, Res, iMouse, iTime);
    march(pos,dir);
    vec3 g=getGrad(pos,.001);
    vec3 n=normalize(g);
    vec3 colM=n*.5+.5;
    if(length(g)<.0001) colM=vec3(0);
    vec3 colG=texture(iChannel0,fragCoord/Res).xyz;

    fragColor.xyz=mix(colG,colM+.5-.5*showRaymarch,0.3);
    fragColor.w=1.;
    //fragColor+=.3*texture(iChannel1,fragCoord/Res);
}

