// created by florian berger (flockaroo) - 2020
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.

// Tetrahedra tessellation

// the first pixel in Buff_A stores:
// pixel.x.....actual number of processed tatrahedron
// pixel.y.....actual number of triangles

// ...the following TetraPerPass*4 pixels in Buff_A are used for finding new 
// tatrahedra which contain distance field 0-passes
// (4 pixels per tetrahedron)

// ...the pixels after TetraPerPass*4 are the final triangles (3 pixels per triangle) 

// 2 modes: 12 or 5 tetrahedra per cube cell
#define TETRA_PER_CUBE 5
//#define TETRA_PER_CUBE 12

// enable this to start searching triangles in different regions of the volume
// otherwise volume would just be stepped through sequentially from 0,0,0 to 1,1,1
#define MULTI_SEARCHPOINTS

//#include Include_A.glsl
//#include Include_B.glsl

#define Res0 vec2(textureSize(iChannel0,0))

int getPixelIdx(vec2 c)
{
    ivec2 res=textureSize(iChannel0,0);
    return int(c.x)+int(c.y)*res.x;
}


// check cut plane through tetrahedron
//##IMG##...stripped inline images away for size ( check on https://www.shaderoo.org/?shader=zh8I1X )
void checkTetra(vec3 p[4], int vidx, inout vec3 v)
{
    vec3 t[4]; int ti=0;
    t[0]=vec3(0);t[1]=vec3(0);t[2]=vec3(0);t[3]=vec3(0);
    //{0,1, 1,2, 2,0, 0,3, 1,3, 2,3}
    int idxn[4];
    int idxp[4];
    for(int i=0;i<6;i++)
    {
        int i1=i%3;
        int i2=(i+1)%3; if(i>=3) i2=3;
        float d1=dist(p[i1]), d2=dist(p[i2]);

        // if one point has negative and the other positive distance
        if(d1*d2<0.) { 

            // store index of negative/positive distance point
            idxn[ti] = (d1<0.) ? i1 : i2 ;
            idxp[ti] = (d1<0.) ? i2 : i1 ;

            // simple mix...
            t[ti] = mix(p[i1],p[i2],abs(d1/(d1-d2)));
            //t[ti]=mix(p[i1],p[i2],0.);

            // ...refine by marching to dist=0
            vec3 dir = normalize(p[i2]-p[i1])*sign(d1);
            for(int j=0;j<8;j++) t[ti] += dist(t[ti])*dir;

            ti++; 
        }
    }

    //FIXME 4th point needs to be checked specially - which 2 other points of tri?
    if(t[3]!=vec3(0)) // FIXME: !=vec3(0) is not the best detection 
    {
        #ifdef QUADSTRIP_ORDER
        // quadstrip order:
        // p1- to p1+,    p1- to p2+,    p2- to p1+,    p2- to p2+
        int idx[4] = int[4](0,1,2,3);
        for(int i=1;i<4;i++) if(idxn[i]==idxn[0] && idxp[i]!=idxp[0]) { idx[1]=i; break; }
        for(int i=1;i<4;i++) if(idxn[i]!=idxn[0] && idxp[i]==idxp[0]) { idx[2]=i; break; }
        for(int i=1;i<4;i++) if(idxn[i]!=idxn[0] && idxp[i]!=idxp[0]) { idx[3]=i; break; }
        #else
        // quad order:
        // p1- to p1+,    p1- to p2+,    p2- to p2+,    p2- to p1+
        int idx[4] = int[4](0,1,2,3);
        for(int i=1;i<4;i++) if(idxn[i]==idxn[0] && idxp[i]!=idxp[0]) { idx[1]=i; break; }
        for(int i=1;i<4;i++) if(idxn[i]!=idxn[0] && idxp[i]!=idxp[0]) { idx[2]=i; break; }
        for(int i=1;i<4;i++) if(idxn[i]!=idxn[0] && idxp[i]==idxp[0]) { idx[3]=i; break; }
        vec3 n = cross(t[idx[1]]-t[idx[0]],t[idx[2]]-t[idx[0]]);
        if (dot(n,getGrad(t[idx[0]],.01))<=0.) { vidx=3-vidx; } 
        #endif
        v=t[idx[vidx]];
    }
    else
    {
        vec3 n = cross(t[1]-t[0],t[2]-t[0]);
        if (dot(n,getGrad(t[0],.01))<=0.) { vec3 d=t[2];t[2]=t[1];t[1]=d; } 
        v=t[vidx];
        //v=vec3(0);
    }
}



//##IMG##...stripped inline images away for size ( check on https://www.shaderoo.org/?shader=zh8I1X )
void checkTetra12(int cIdx, int tIdx, int vIdx, ivec3 cnum, vec3 csize, inout vec3 vert)
{
    // this is just a trick to start searching triangles in different regions of the volume
    // otherwise volume would just be stepped through sequentially from 0,0,0 to 1,1,1
    #ifdef MULTI_SEARCHPOINTS
    int subdiv=8;
    int cl=(cnum.x*cnum.y*cnum.z)/subdiv;
    cIdx=(cIdx%subdiv)*cl+cIdx/subdiv;
    #endif

    ivec3 cIdx3=ivec3(cIdx%cnum.x,(cIdx/cnum.x)%cnum.y,(cIdx/(cnum.x*cnum.y))%cnum.z);
    vec3 p[4];
    vec3 cm=csize*vec3(cIdx3-cnum/2);
    p[3]=cm;
    for(int i=0;i<3;i++)
    {
        p[i]=(vec3(i%2,i/2,0)-.5)*csize;
        if (tIdx%2==1) p[i].xy*=-1.;
        //if (tIdx>=6) p[i].zx*=-1.;
        if ((tIdx/2)%3==1) p[i]=p[i].zxy;
        if ((tIdx/2)%3==2) p[i]=p[i].yzx;
        if (tIdx>=6) p[i]*=-1.;
        //if (tIdx/2>3) p[i]=p[i].zxy;
        //if (tIdx/2>4) p[i]=p[i].zxy;
        p[i]+=cm;
    }
    //if(tIdx==11)
    checkTetra(p,vIdx,vert);
}

//##IMG##...stripped inline images away for size ( check on https://www.shaderoo.org/?shader=zh8I1X )
// beware: those cells have to be flipped every other cell to fit together (see cellPar below)
void checkTetra5(int cIdx, int tIdx, int vIdx, ivec3 cnum, vec3 csize, inout vec3 vert)
{
    // this is just a trick to start searching triangles in different regions of the volume
    // otherwise volume would just be stepped through sequentially from 0,0,0 to 1,1,1
    #ifdef MULTI_SEARCHPOINTS
    int subdiv=8;
    int cl=(cnum.x*cnum.y*cnum.z)/subdiv;
    cIdx=(cIdx%subdiv)*cl+cIdx/subdiv;
    #endif

    ivec3 cIdx3=ivec3(cIdx%cnum.x,(cIdx/cnum.x)%cnum.y,(cIdx/(cnum.x*cnum.y))%cnum.z);
    int cellPar=(cIdx3.x+cIdx3.y+cIdx3.z)&1;
    vec3 p[4];
    vec3 cm=csize*vec3(cIdx3-cnum/2);

    // center tetrahedron
    for(int i=0;i<4;i++) 
        p[i]=cm+.5*csize*vec3(vec2(i&1)*2.-1.,1)*vec3(1,vec2((i/2)&1)*2.-1.)*vec3(float(cellPar)*2.-1.,1,1);

    if(tIdx<4)  // for edge tatrahedra just mirror one edge point
        p[tIdx]=-(p[tIdx]-cm)+cm;

    //if(tIdx==11)
    checkTetra(p,vIdx,vert);
}

int sortInTriangles(int pixIdx, int triCnt, inout vec4 c)
{
    int pix_ti=(pixIdx-sort0)/3-triCnt;
    int pix_vi=(pixIdx-sort0)%3;
    int ti=-1;
    for(int i=0;i<TetraPerPass;i++)
    {
        vec3 p[4];
        p[3]=getPixel(march0+i*4+3).xyz;
        p[2]=getPixel(march0+i*4+2).xyz;
        p[1]=getPixel(march0+i*4+1).xyz;
        p[0]=getPixel(march0+i*4+0).xyz;
        if(p[0]!=vec3(0))
        {
            ti++;
            if (pix_ti==ti) { c=vec4(p[pix_vi],1); }
            if(p[3]!=vec3(0))
            {
                ti++;
                if (pix_ti==ti) { c=vec4(p[(pix_vi+2)%4],1); }
            }
        }
    }
    return ti+1;
}

//##IMG##...stripped inline images away for size ( check on https://www.shaderoo.org/?shader=zh8I1X )
void mainImage( out vec4 fragColor, vec2 fragCoord )
{
    // store actual cell index in ll pixel
    vec3 fullSize=vec3(2.5,5.,1.8);
    vec3 csize=vec3(.085);
    ivec3 cnum=ivec3(fullSize/csize);
    int tetraCnt=int(getPixel(0).x);
    int triCnt=int(getPixel(0).y);
    // 4 pixels per tetrahedron
    ivec2 res=textureSize(iChannel0,0);
    int pixIdx=getPixelIdx(fragCoord);
    fragColor=getPixel(pixIdx);
    if ( tetraCnt>(cnum.x*cnum.y*cnum.z)*TETRA_PER_CUBE && iFrame!=0 ) return;

//##IMG##...stripped inline images away for size ( check on https://www.shaderoo.org/?shader=zh8I1X )
    // check all cells in volume
    if(pixIdx-march0>=0 && pixIdx-march0<TetraPerPass*4)
    {
        int pIdx=pixIdx-march0+tetraCnt*4;
        int cellIdx =(pIdx/4)/TETRA_PER_CUBE;
        int tetraIdx=(pIdx/4)%TETRA_PER_CUBE;
        int vertIdx =pIdx%4;
        vec3 vert=vec3(0);
        #if TETRA_PER_CUBE == 12
        checkTetra12(cellIdx,tetraIdx,vertIdx,cnum,csize,vert);
        #endif
        #if TETRA_PER_CUBE == 5
        checkTetra5(cellIdx,tetraIdx,vertIdx,cnum,csize,vert);
        #endif
        fragColor=vec4(vert,1);
    }

    // store triangles in upper buffer region (blue in above image)
    //#ifdef SORT_IN_TRIANGLES
    if(pixIdx-sort0>=triCnt*3 && pixIdx-sort0<(triCnt+TetraPerPass*2)*3)
    {
        fragColor=getPixel(pixIdx);
        sortInTriangles(pixIdx,triCnt,fragColor);
        //fragColor=col;
    }
    //#endif

    // store temporary tetrahedra in first buffer region (green in above image)
    if(pixIdx==0) 
    {
        int tcnt=sortInTriangles(pixIdx,triCnt,fragColor);
        // store counters in 1st pixel (red in above image)
        fragColor=vec4(tetraCnt+TetraPerPass,triCnt+tcnt,0,1);
    }
    if(iFrame==0) fragColor=vec4(0);
}

