// created by florian berger (flockaroo) - 2020
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.

// rendering triangles from Buffer A

// ...not really fast or useful to raymarch triangles
// ...but maybe it serves some "educational" purpose (at least for myself ;-)

#define TriPerFrame 10

#define Res vec2(iResolution.xy)

int tcnt=0;

float dot2( vec3 v ) { return dot(v,v); }
float udTriangle( vec3 p, vec3 a, vec3 b, vec3 c )
{
  vec3 ba = b - a; vec3 pa = p - a;
  vec3 cb = c - b; vec3 pb = p - b;
  vec3 ac = a - c; vec3 pc = p - c;
  vec3 nor = cross( ba, ac );

  return sqrt(
    (sign(dot(cross(ba,nor),pa)) +
     sign(dot(cross(cb,nor),pb)) +
     sign(dot(cross(ac,nor),pc))<2.0)
     ?
     min( min(
     dot2(ba*clamp(dot(ba,pa)/dot2(ba),0.0,1.0)-pa),
     dot2(cb*clamp(dot(cb,pb)/dot2(cb),0.0,1.0)-pb) ),
     dot2(ac*clamp(dot(ac,pc)/dot2(ac),0.0,1.0)-pc) )
     :
     dot(nor,pa)*dot(nor,pa)/dot2(nor) );
}

float triDist(vec3 p)
{
    float d=10000.;
    int triNum=int(getPixel(0).y);
    for(int i=0;i<TriPerFrame;i++)
    {
        int i3=-i*3;
        i3+=sort0+((iFrame*TriPerFrame)%triNum)*3;
        vec3 v1=getPixel(i3+0).xyz+vec3(1,0,1)*.0;
        vec3 v2=getPixel(i3+1).xyz+vec3(0,1,1)*.0;
        vec3 v3=getPixel(i3+2).xyz;
        d=min(d,udTriangle(p,v1,v2,v3));
    }
    return d;
}

vec3 getTriGrad(vec3 p, float eps) 
{ 
    vec2 d=vec2(eps,0); 
    float d0=triDist(p);
    return vec3(triDist(p+d.xyy)-d0,triDist(p+d.yxy)-d0,triDist(p+d.yyx)-d0)/eps; 
}

void march(inout vec3 pos, vec3 dir)
{
    float eps=.001;
    for(int i=0;i<80;i++)
    {
        float d=triDist(pos);
        pos+=dir*d*.9;
        if (d<eps) break;
    }
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 pix0=texelFetch(iChannel1,ivec2(0,0),0);
    int tcnt=int(pix0.y);
    if(iMouse.xy!=pix0.xy) 
    {
     	tcnt=0;
        fragColor=vec4(0,0,0,100000);
    }
    else
    {
    	vec3 pos,dir, pos0;
    	getTrafo(pos,dir,fragCoord, Res, iMouse, iTime);
        pos0=pos;
    	march(pos,dir);
    	vec3 g=getTriGrad(pos,.02);
    	vec3 n=normalize(g);
    	vec3 colM=n*.5+.5;
        //colM+=vec3(0,0,1);
    	if(length(g)<.0001) colM=vec3(0);
		vec4 newCol=vec4(colM,length(pos-pos0));
        vec4 oldCol=texture(iChannel1,fragCoord/Res);
        fragColor=(newCol.w<oldCol.w)?newCol:oldCol;
        //fragColor=newCol;
     	tcnt+=TriPerFrame;
    }
    if(iFrame<10) 
    {
     	tcnt=0;
        fragColor=vec4(0,0,0,100000);
    }
    if(ivec2(fragCoord)==ivec2(0))
    	fragColor = vec4(iMouse.xy,tcnt,1.0);
}

