#define PIH 1.57079632679

#define ROTM(ang) mat2(cos(ang-vec2(0,PIH)),-sin(ang-vec2(0,PIH)))

float tanFOVh=.6;

void getTrafo(inout vec3 pos, inout vec3 dir, vec2 fc, bool inv, vec2 Res, vec4 iMouse, float iTime)
{
    vec2 sc=(fc-Res*.5)/Res.x*2.;
    dir=normalize(vec3(0,0,-1./tanFOVh)+vec3(sc,0));

    float ph = iMouse.x/Res.x*10.;
    if (iMouse.x<.5) ph=2.13;
    float th = iMouse.y/Res.y*10.+1.4;
    float camDist=5./**exp(-iMouseData.z/5000.)*/;
    //th=0.;

    if(inv)
    {
        pos=vec3(0,0,camDist);
        pos.yz=ROTM(th)*pos.yz;
        dir.yz=ROTM(th)*dir.yz;
        pos.xy=ROTM(ph)*pos.xy;
        dir.xy=ROTM(ph)*dir.xy;
    }
    else
    {
        pos.xy=ROTM(-ph)*pos.xy;
        pos.yz=ROTM(-th)*pos.yz;
        pos.z-=camDist;
    }
}

void getTrafo(inout vec3 pos, inout vec3 dir, vec2 fc, vec2 Res, vec4 iMouse, float iTime)
