// Pallete from Inigo Quilez
// pModPolar from hg-sdf library
// Antialiasing thx to Fabrice Neyret


#define rot(x) mat2(cos(x),-sin(x),sin(x),cos(x))
#define pal(a,b,c,d,e) ((a) + (b)*sin((c)*(d) + (e)))
#define pmod(p,x) mod(p,x) - 0.5*x
#define PI acos(-1.)
float pModPolar(inout vec2 p, float repetitions) {
	float angle = 2.*PI/repetitions;
	float a = atan(p.y, p.x) + angle/2.;
	float r = length(p);
	float c = floor(a/angle);
	a = mod(a,angle) - angle/2.;
	p = vec2(cos(a), sin(a))*r;
	// For an odd number of repetitions, fix cell index of the cell in -x direction
	// (cell index would be e.g. -5 and 5 in the two halves of the cell):
	if (abs(c) >= (repetitions/2.)) c = abs(c);
	return c;
}
vec4 valueNoise(vec2 t, float w){
    vec2 fr = fract(t);
	return 
        mix(
            mix( 
                texture(iChannel0,vec2(floor(t.x), floor(t.y))/256.),
                texture(iChannel0,vec2(floor(t.x), floor(t.y) + 1.)/256.),
            	smoothstep(0.,1.,fr.y)
            ),
            mix( 
                texture(iChannel0,vec2(floor(t.x) + 1.,floor(t.y))/256.),
                texture(iChannel0,vec2(floor(t.x) + 1.,floor(t.y) + 1.)/256.),
            	smoothstep(0.,1.,fr.y)
            ),
            smoothstep(0.,1.,pow(fr.x, w)));
}
vec4 fbm(vec2 uv){
	vec4 n = vec4(0);


    n += valueNoise(uv*500.,0.1);
    //n += valueNoise(uv*1000., 0.1)*0.5;
    n += valueNoise(uv*1700.,0.1)*0.5;

    n -= valueNoise(uv*10.,1.)*1.;
    n -= valueNoise(uv*20.,0.5)*0.5;


    n = max(n, 0.);
    return n;
}

vec3 get(vec2 fragCoord){

    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.y ;

    vec3 col = vec3(0);

    vec2 uvn = uv;

    float mm = 4.;
    uvn *= rot(-PI*mm*0.5);

    // random IFS stuff
    for(int i = 0; i < 26; i++){
    	uvn = abs(uvn);
        uvn -= 0.25;
    	uvn.y += 0.299991;
        if(uvn.x < 0.){
        	uvn.x -= 1.*uvn.y;
        }
        if(uvn.y < 0.){
        	//uvn.y -= 1.*uvn.y;
        }
        uvn *= rot(0.5 *PI);
    }

    float j = pModPolar(uvn, mm);
    float modD = 0.05;
    vec2 id = vec2(j,floor(uvn.x/modD));
    uvn.x = pmod(uvn.x, modD);

    vec3 c = pal(0.5,0.5,vec3(1.,0.7,0.5), 0.6 + id.x, id.y + iTime*0.5);

    c = max(c, 0.);

    col += c;
    col -= fbm(uv).x*0.1;
    col += fbm(uv + 4.).x*0.1;



    // vignette
    col = pow(col, vec3(1. + dot(uv,uv)*0.1));
    col *=1. -  dot(uv,uv)*0.7;
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(0);

    // float(min(iFrame,0)) hack for faster compilation (stops loop unrolling)
    for(float i =0.; i < 9. + float(min(iFrame,0)); i++){
    	col += get(fragCoord + vec2(mod(i,3.),floor(i/3.))*0.1);
    }
    col /= 9.;


    fragColor = vec4(col,1.0);
}

uint[] jfig_bitfield = uint[]( 0x0u,0x0u,0x0u,0xf97800u,0x90900u,0xc91800u,0x890900u,0xf90900u,0x180u,0x0u,0x30e30e0u,0x4904900u,0x49e49e0u,0x4824820u,0x31e31e0u,0x0u,0x0u,0x0u );
bool jfig(in uint x, in uint y) {
    if (x>=32u || y>=18u) return false;
    uint id = x + (17u-y)*32u;
    return 0u != (jfig_bitfield[id/32u] & (1u << (id&31u)));
}

const float sphere_radius   = 1.5;
const float noise_amplitude = 1.0;

float hash(const float n) {
    float x = sin(n)*43758.5453;
    return x-floor(x);
}

float noise(in vec3 x) {
    vec3 p = floor(x);
    vec3 f = x - p;
    f = f * dot(f, vec3(3.0,3.0,3.0)-2.0*f);
    float n = dot(p, vec3(1.0, 57.0, 113.0));
    return mix(mix(mix(hash(n +  0.0), hash(n +  1.0), clamp(f.x,0.,1.)),
                   mix(hash(n + 57.0), hash(n + 58.0), clamp(f.x,0.,1.)),
                   clamp(f.y,0.,1.)),
               mix(mix(hash(n + 113.0), hash(n + 114.0), clamp(f.x,0.,1.)),
                   mix(hash(n + 170.0), hash(n + 171.0), clamp(f.x,0.,1.)),
                   clamp(f.y,0.,1.)),
               clamp(f.z,0.,1.));
}

vec3 rotate(in vec3 v) {
    return vec3(dot(v, vec3( 0.0,  0.8,   0.6)),
                dot(v, vec3(-0.8,  0.36, -0.48)),
                dot(v, vec3(-0.6, -0.48,  0.64)));
}

float fractal_brownian_motion(in vec3 x) {
    vec3 p = rotate(x);
    float f = 0.0;
    f += 0.5000*noise(p); p = p*2.32;
    f += 0.2500*noise(p); p = p*3.03;
    f += 0.1250*noise(p); p = p*2.61;
    f += 0.0625*noise(p);
    return f/0.9375;    
}

vec3 palette_fire(in float d) {
    const vec3 yellow   = vec3(2.7, 2.3, 2.0); // note that the color is "hot", i.e. has components >1
    const vec3 orange   = vec3(2.0, 1.6, 0.0);
    const vec3 red      = vec3(1.5, 0.0, 0.0);
    const vec3 darkgray = vec3(0.2, 0.2, 0.2);
    const vec3 gray     = vec3(0.4, 0.4, 0.4);
    float x = max(0.0, min(1.0, d));
    if (x<.25f)
        return mix(gray, darkgray, x*4.f);
    else if (x<.5f)
        return mix(darkgray, red, x*4.f-1.f);
    else if (x<.75f)
        return mix(red, orange, x*4.f-2.f);
    return mix(orange, yellow, x*4.f-3.f);
}

float signed_distance(in vec3 p) {
    float displacement = -fractal_brownian_motion(p*3.4 + vec3(0.1, 0.1, 0.1)*iTime)*noise_amplitude;
    return length(p) - (sphere_radius + displacement) * (0.1 + 0.9*sin(mod(iTime*(2./3.1416), 3.1416/2.)));
}

bool sphere_trace(in vec3 orig, in vec3 dir, out vec3 pos) {
    if (dot(orig,orig) - dot(orig,dir)*dot(orig,dir) > sphere_radius*sphere_radius)
        return false;
    pos = orig;
    for(int i = 0; i < 128; i++) {
        float d = signed_distance(pos);
        if (d < 0.0) return true;
        pos = pos + dir*max(d*0.1, 0.01);
    }
    return false;
}

vec3 distance_field_normal(in vec3 pos) {
    const float eps = 0.1;
    float d  = signed_distance(pos);
    float nx = signed_distance(pos + vec3(eps, 0.0, 0.0)) - d;
    float ny = signed_distance(pos + vec3(0.0, eps, 0.0)) - d;
    float nz = signed_distance(pos + vec3(0.0, 0.0, eps)) - d;
    return normalize(vec3(nx, ny, nz));
}

vec3 rotate_camera(in vec3 orig, in vec3 dir, in vec3 target) {
    vec3 zAxis = normalize(orig - target);
    vec3 xAxis = normalize(cross(vec3(0., 1., 0.), zAxis));
	vec3 yAxis = normalize(cross(zAxis, xAxis));
   	mat4 transform = mat4(vec4(xAxis, 0.), vec4(yAxis, 0.), vec4(zAxis, 0.), vec4(orig, 1.));
    return (transform * vec4(dir, 0.)).xyz;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    const float fov = 3.1416 / 3.;
    vec2 uv = ( 2.*fragCoord - iResolution.xy ) / iResolution.y*tan(fov/2.); 

    vec3 orig = vec3(-sin(iTime)*3., 0., cos(iTime)*3.);
    vec3 dir = normalize(vec3(uv, -1));
	dir = rotate_camera(orig, dir, vec3(0.));

    vec3 hit;
    if(sphere_trace(orig, dir, hit)) {
        float noise_level = (sphere_radius - length(hit))/noise_amplitude;
        vec3 light_dir = normalize((vec3(10.0, 10.0, 10.0) - hit));
        float light_intensity = max(0.4, dot(light_dir, distance_field_normal(hit)));
        fragColor = vec4(palette_fire(2.0*(-0.2+noise_level))*light_intensity, 1.0);
    } else
        fragColor = texture(iChannel0, dir);
    vec2 coord = fragCoord/iResolution.xy*vec2(32u, 18u)*6.;
    if (jfig(uint(coord.x), uint(coord.y)))
        fragColor.xyz += vec3(.5);
}

////////////////////////////////////////////////////////////////////////////////
//
// Spherical texturing with Mercator projection.
//
// Adding a spiral (a loxodrome in fact, a curve of constant compass bearing.
//
// Matthew Arcus, 2020.
//
////////////////////////////////////////////////////////////////////////////////

const float PI = 3.14159;

vec2 rotate(vec2 p, float t) {
  return p * cos(-t) + vec2(p.y, -p.x) * sin(-t);
}

vec3 tiling(vec2 p) {
  p *= 8.0;
  p = mod(p,2.0)-1.0;
  if (p.x*p.y < 0.0) p = p.yx;
  p = 1.0-2.0*abs(abs(p)-0.5);
  float t = 0.5*iTime;
  vec2 n = vec2(sin(t),cos(t));
  vec2 m = vec2(-n.y,n.x);
  float d = dot(p,n), e = dot(p,m);
  float k = min(abs(d),abs(e));
  if (d < 0.0) k = min(k,abs(p.x-1.0));
  if (e < 0.0) k = min(k,abs(p.y-1.0));
  return smoothstep(0.025,0.05,k)*vec3(0.5,0.5,0);
}

vec3 mirror(vec2 uv) {
  uv.y -= 0.1*iTime;
  uv = mod(uv,2.0);
  uv = min(uv,2.0-uv);
  return texture(iChannel1,uv).rgb;
}

vec3 slider(vec2 uv) {
  uv *= 4.0;
   float t = 0.25*PI;
  uv = rotate(uv,t);
    uv /= cos(t);
 vec2 iv = floor(uv);
  if (true) {
    // Slide around a little
    float t = 0.3*iTime;
    if (mod(t,2.0) < 1.0) {
      uv.x += floor(mod(iv.y,2.0))*fract(t);
    } else {
      uv.y += floor(mod(iv.x,2.0))*fract(t);
    }
  }
  uv = mod(uv,1.0);
  vec2 dv = min(uv,1.0-uv);
  float d = min(dv.x,dv.y);
  vec3 col = texture(iChannel0,uv).rgb;
  col = mix(vec3(0),col,smoothstep(0.01,0.02,d));
  return col;
}

vec3 hsv2rgb(vec3 c) {
  vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
  rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing	
  return c.z * mix( vec3(1.0), rgb, c.y);
}

// From Chris Wellons: https://nullprogram.com/blog/2018/07/31/
uint IHash(uint x) {
    x ^= x >> 16;
    x *= 0x7feb352dU;
    x ^= x >> 15;
    x *= 0x846ca68bU;
    x ^= x >> 16;
    return x;
}

vec3 indexed(vec2 uv) {
  uv *= 8.0;
  uv.y += iTime;
  ivec2 iv = ivec2(floor(uv));
  float rnd = float(IHash((uint(iv.x)<<16) ^ uint(iv.y)))/pow(2.0,32.0);
  uv = mod(uv,1.0);
  vec2 dv = min(uv,1.0-uv);
  float d = min(dv.x,dv.y);
  vec3 col = hsv2rgb(vec3(rnd,1,1));
  col = mix(vec3(0),col,smoothstep(0.01,0.02,d));
  return col;
}

const int ncolors = 4;
vec3 getColor(int i, vec2 uv) {
  return slider(uv);
  i = i%ncolors;
  if (i == 0) return slider(uv);
  if (i == 1) return tiling(uv);
  if (i == 2) return indexed(uv);
  if (i == 3) return mirror(uv);
  return vec3(1,0,0);
}

vec3 getColor(vec3 p) {
  float x = atan(p.x,p.z);       // Longitude
  float y = asin(p.y/length(p)); // Latitude
  y = log(tan(0.25*PI+0.5*y));   // Mercator
  vec2 uv = vec2(x,y);
  uv /= PI;
  float period = 10.0;
  float t = mod(iTime,float(ncolors)*period);
  int i = int(t/period);
  t = mod(t,period);
  return mix(getColor(i,uv),getColor(i+1,uv),smoothstep(period-1.0,period,t));
}

struct Ray {
  vec3 q;               // origin
  vec3 d;               // direction
};

struct Hit {
  float t;      // solution to p=q+t*d
  vec3 n;       // normal
};

struct Sphere {
  float r;      // radius
  vec3 p;       // centre
};

bool intersectSphere(Sphere s, Ray ray, out Hit hit) {
  vec3 p = s.p;
  float r = s.r;
  float c = length(p);
  vec3 q = ray.q, d = ray.d;
  // |q + t*d - p|^2 = r^2
  float B = dot(q-p,d);
  float C = dot(q,q)-2.0*dot(q,p)+(c+r)*(c-r);
  float D = B*B - C;
  if (D < 0.0) return false;
  D = sqrt(D);
  float t,t1;
  if (B >= 0.0) {
    t = -B-D; t1 = C/t;
  } else {
    t1 = -B+D; t = C/t1;
  }
  if (t < 0.0) t = t1;
  if (t < 0.0) return false;
  // Normal is the radial vector of sphere
  hit = Hit(t, (q+t*d-p)/r);
  return true;
}

bool intersectScene(Ray r, out Hit hit) {
  Sphere s = Sphere(1.0, vec3(0));
  if (intersectSphere(s,r,hit)) {
    return true;
  }
  return false;
}

vec3 light;
float ambient;
float diffuse;

vec3 solve(Ray r) {
  Hit hit;
  if (!intersectScene(r,hit)) {
    return vec3(0);
  } else {
    vec3 n = hit.n;
    if (dot(r.d,n) > 0.0) n *= -1.0;
    vec3 baseColor = getColor(hit.n);
    vec3 color = baseColor.xyz*(ambient+diffuse*max(0.0,dot(light,n)));
    float specular = pow(max(0.0,dot(reflect(light,n),r.d)),10.0);
    color += 0.5*specular*vec3(1);
    color *= clamp(1.0 - (hit.t-3.0)/5.0,0.0,1.0);
    return color;
  }
}

vec3 transform(in vec3 p) {
  if (iMouse.x > 0.0) {
    float theta = -(2.0*iMouse.y-iResolution.y)/iResolution.y*PI;
    float phi = -(2.0*iMouse.x-iResolution.x)/iResolution.x*PI;
    p.yz = rotate(p.yz,theta);
    p.zx = rotate(p.zx,-phi);
  }
  if (true) {
    p.yz = rotate(p.yz,iTime * 0.125);
    p.zx = rotate(p.zx,iTime * 0.2);
  }
  return p;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  light = normalize(vec3(0.5,1.0,-1.0));
  light = transform(light);
  ambient = 0.5;
  diffuse = 1.0-ambient;
  vec3 col = vec3(0);
  float AA = 2.0;
  for (float i = 0.0; i < AA; i++) {
    for (float j = 0.0; j < AA; j++) {
      vec2 uv = (2.0*(fragCoord+vec2(i,j)/AA)-iResolution.xy)/iResolution.y;
      vec3 p = vec3(0,0,-6);
      vec3 r = vec3(uv,6);
      p = transform(p);
      r = transform(r);
      r = normalize(r); // Normalize after transform
      col += solve(Ray(p,r));
    }
  }
  fragColor = vec4(pow(col/(AA*AA),vec3(0.4545)),1);
}

