// O.x : cell excitation state (time to be excitable again). 
// O.y : alive state (0 = alive)
// O.z : shows spontaneous excitation (at low proba) area

void mainImage( out vec4 O, vec2 U )
{
    if ( iFrame == 0 ) {    // init
        O = vec4(0);
        if ( U.x < p*R.x /* && hash(U) > 1. - delay * freq */ ) 
            O.x = ceil( delay * hash(U+.5) ), O.z = .3;
        return; 
    }

    O = T(U);                                                    // previous state

    if ( O.y == 0. )                                             // cell alive
      if ( O.x == 0.)                                            // excitable cell
        if ( U.x < p*R.x && hash(U+iTime) > 1.-freq )            // spontaneous impulse
            O.x = delay;
        else 
            for ( int k=0; k<9; k++ ) {                          // if excitation front in neighborhood, propagates  
                vec2 D = vec2(k%3,k/3)-1.;
                if( iFrame%2 < 1 || D.x*D. y== 0. )              // slows diagonal
                if ( T(U + D).x == delay ) { O.x = delay; return; } 
            }
      else O.x--;                                                // refractory period

    if ( iMouse.z > 0. && length(iMouse.xy-U) < 20. ) O.y = 1., O.x = 0.; // paint necrose
}

