////////////////////////////////////////////////////////////////////////////////
//
// Spherical texturing with Mercator projection.
//
// Matthew Arcus, 2020.
//
////////////////////////////////////////////////////////////////////////////////

const float PI = 3.14159;

vec2 rotate(vec2 p, float t) {
  return p * cos(-t) + vec2(p.y, -p.x) * sin(-t);
}

vec3 tiling(vec2 p) {
  p *= 8.0;
  p = mod(p,2.0)-1.0;
  if (p.x*p.y < 0.0) p = p.yx;
  p = 1.0-2.0*abs(abs(p)-0.5);
  float t = 0.5*iTime;
  vec2 n = vec2(sin(t),cos(t));
  vec2 m = vec2(-n.y,n.x);
  float d = dot(p,n), e = dot(p,m);
  float k = min(abs(d),abs(e));
  if (d < 0.0) k = min(k,abs(p.x-1.0));
  if (e < 0.0) k = min(k,abs(p.y-1.0));
  return smoothstep(0.025,0.05,k)*vec3(0.5,0.5,0);
}

vec3 mirror(vec2 uv) {
  uv.y -= 0.1*iTime;
  uv = mod(uv,2.0);
  uv = min(uv,2.0-uv);
  return texture(iChannel1,uv).rgb;
}

vec3 slider(vec2 uv) {
  uv *= 4.0;
  vec2 iv = floor(uv);
  if (true) {
    // Slide around a little
    float t = 0.3*iTime;
    if (mod(t,2.0) < 1.0) {
      uv.x += floor(mod(iv.y,2.0))*fract(t);
    } else {
      uv.y += floor(mod(iv.x,2.0))*fract(t);
    }
  }
  uv = mod(uv,1.0);
  vec2 dv = min(uv,1.0-uv);
  float d = min(dv.x,dv.y);
  vec3 col = texture(iChannel0,uv).rgb;
  col = mix(vec3(0),col,smoothstep(0.01,0.02,d));
  return col;
}

vec3 hsv2rgb(vec3 c) {
  vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
  rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing	
  return c.z * mix( vec3(1.0), rgb, c.y);
}

// From Chris Wellons: https://nullprogram.com/blog/2018/07/31/
uint IHash(uint x) {
    x ^= x >> 16;
    x *= 0x7feb352dU;
    x ^= x >> 15;
    x *= 0x846ca68bU;
    x ^= x >> 16;
    return x;
}

vec3 indexed(vec2 uv) {
  uv *= 8.0;
  uv.y += iTime;
  ivec2 iv = ivec2(floor(uv));
  float rnd = float(IHash((uint(iv.x)<<16) ^ uint(iv.y)))/pow(2.0,32.0);
  uv = mod(uv,1.0);
  vec2 dv = min(uv,1.0-uv);
  float d = min(dv.x,dv.y);
  vec3 col = hsv2rgb(vec3(rnd,1,1));
  col = mix(vec3(0),col,smoothstep(0.01,0.02,d));
  return col;
}

const int ncolors = 4;
vec3 getColor(int i, vec2 uv) {
  //return indexed(uv);
  i = i%ncolors;
  if (i == 0) return slider(uv);
  if (i == 1) return tiling(uv);
  if (i == 2) return indexed(uv);
  if (i == 3) return mirror(uv);
  return vec3(1,0,0);
}

vec3 getColor(vec3 p) {
  float x = atan(p.x,p.z);       // Longitude
  float y = asin(p.y/length(p)); // Latitude
  y = log(tan(0.25*PI+0.5*y));   // Mercator
  vec2 uv = vec2(x,y);
  uv /= PI;
  float period = 10.0;
  float t = mod(iTime,float(ncolors)*period);
  int i = int(t/period);
  t = mod(t,period);
  return mix(getColor(i,uv),getColor(i+1,uv),smoothstep(period-1.0,period,t));
}

struct Ray {
  vec3 q;               // origin
  vec3 d;               // direction
};

struct Hit {
  float t;      // solution to p=q+t*d
  vec3 n;       // normal
};

struct Sphere {
  float r;      // radius
  vec3 p;       // centre
};

bool intersectSphere(Sphere s, Ray ray, out Hit hit) {
  vec3 p = s.p;
  float r = s.r;
  float c = length(p);
  vec3 q = ray.q, d = ray.d;
  // |q + t*d - p|^2 = r^2
  float B = dot(q-p,d);
  float C = dot(q,q)-2.0*dot(q,p)+(c+r)*(c-r);
  float D = B*B - C;
  if (D < 0.0) return false;
  D = sqrt(D);
  float t,t1;
  if (B >= 0.0) {
    t = -B-D; t1 = C/t;
  } else {
    t1 = -B+D; t = C/t1;
  }
  if (t < 0.0) t = t1;
  if (t < 0.0) return false;
  // Normal is the radial vector of sphere
  hit = Hit(t, (q+t*d-p)/r);
  return true;
}

bool intersectScene(Ray r, out Hit hit) {
  Sphere s = Sphere(1.0, vec3(0));
  if (intersectSphere(s,r,hit)) {
    return true;
  }
  return false;
}

vec3 light;
float ambient;
float diffuse;

vec3 solve(Ray r) {
  Hit hit;
  if (!intersectScene(r,hit)) {
    return vec3(0);
  } else {
    vec3 n = hit.n;
    if (dot(r.d,n) > 0.0) n *= -1.0;
    vec3 baseColor = getColor(hit.n);
    vec3 color = baseColor.xyz*(ambient+diffuse*max(0.0,dot(light,n)));
    float specular = pow(max(0.0,dot(reflect(light,n),r.d)),5.0);
    color += 0.5*specular*vec3(1.0,1.0,1.0);
    color *= clamp(1.0 - (hit.t-3.0)/5.0,0.0,1.0);
    return color;
  }
}

vec3 transform(in vec3 p) {
  if (iMouse.x > 0.0) {
    float theta = -(2.0*iMouse.y-iResolution.y)/iResolution.y*PI;
    float phi = -(2.0*iMouse.x-iResolution.x)/iResolution.x*PI;
    p.yz = rotate(p.yz,theta);
    p.zx = rotate(p.zx,-phi);
  }
  if (true) {
    p.yz = rotate(p.yz,iTime * 0.125);
    p.zx = rotate(p.zx,iTime * 0.2);
  }
  return p;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  light = normalize(vec3(0.5,1.0,-1.0));
  light = transform(light);
  ambient = 0.5;
  diffuse = 1.0-ambient;
  vec3 col = vec3(0);
  float AA = 2.0;
  for (float i = 0.0; i < AA; i++) {
    for (float j = 0.0; j < AA; j++) {
      vec2 uv = (2.0*(fragCoord+vec2(i,j)/AA)-iResolution.xy)/iResolution.y;
      vec3 p = vec3(0,0,-6);
      vec3 r = vec3(uv,6);
      p = transform(p);
      r = transform(r);
      r = normalize(r); // Normalize after transform
      col += solve(Ray(p,r));
    }
  }
  fragColor = vec4(pow(col/(AA*AA),vec3(0.4545)),1);
}

// Shader made for the Cookie Collective live stream (2020/03/19)
// https://www.twitch.tv/videos/570598632
// Added dithering and mouse control for the Shadertoy version.

// Leon Denise aka ponk from Cookie Collective
// Made in 2020/03/19 with the work of the Shadertoy community

const float iterations = 7.;

vec2 mouse;
float random (in vec2 st) { return fract(sin(dot(st.xy,vec2(12.9898,78.233)))*43758.5453123); }
mat2 rot (float a) { float c=cos(a),s=sin(a); return mat2(c,-s,s,c); }
vec3 lookat (vec3 eye, vec3 at, vec2 uv, float fov) {
  vec3 forward = normalize(at-eye);
  vec3 right = normalize(cross(forward, vec3(0,1,0)));
  vec3 up = normalize(cross(right, forward));
  return normalize(forward * fov + right * uv.x + up * uv.y);
}

float map (vec3 pos) {
  float scene = 1.0;
  float range = 2.0;
  float a = 1.0;
  float falloff = 1.2 + mouse.x * 0.6;
  for (float index = iterations; index > 0.; --index) {
    pos.xz *= rot(iTime*.05);
    pos = abs(pos)-range*a;
    scene = min(scene, max(pos.x,max(pos.y,pos.z)));
    a /= falloff;
  }
  scene = max(-scene,0.);
  return scene;
}

vec3 getNormal (vec3 p) {
  vec2 e = vec2(0.0001,0);
  return normalize(vec3(map(p+e.xyy)-map(p-e.xyy), map(p+e.yxy)-map(p-e.yxy), map(p+e.yyx)-map(p-e.yyx)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec3 eye = vec3(0,0.,-2);
    vec3 ray = lookat(eye, vec3(0.,sin(iTime*.2)*.5,0), uv, .5);
    mouse = (iMouse.xy / iResolution.xy);
    float total = 0.0;
    float shade = 0.0;
    float dither = random(uv);
    const float count = 100.;
    for (float index = count; index > 0.; --index) {
        float dist = map(eye+ray*total);
        if (dist < 0.001) {
            shade = index/count;
            break;
        }
        dist *= .9 + 0.1 * dither;
        total += dist;
    }
    vec3 color = vec3(shade);
    vec3 normal = getNormal(eye+ray*total);
    color = vec3(.3)*clamp(dot(normal, normalize(vec3(0,1,-1))),0.,1.);
    color += vec3(.9)*pow(clamp(dot(normal, -ray),0.,1.), 8.);
    color *= shade;
    fragColor = vec4(color,1);
}

#define BUNW 6
#define BUNH 6
#define BUND 4
#define BUNVOXSIZE 0.1
uint[] bunny_bitfield = uint[]( 0xc30d0418u, 0x37dff3e0u, 0x7df71e0cu, 0x004183c3u, 0x00000400u );
bool bunny(in int cubeID) {
    if (cubeID<0 || cubeID>=BUNW*BUNH*BUND) return false;
    return 0u != (bunny_bitfield[cubeID/32] & (1u << (cubeID&31)));
}

#define JFIGW 32u
#define JFIGH 18u
uint[] jfig_bitfield = uint[]( 0x0u,0x0u,0x0u,0xf97800u,0x90900u,0xc91800u,0x890900u,0xf90900u,0x180u,0x0u,0x30e30e0u,0x4904900u,0x49e49e0u,0x4824820u,0x31e31e0u,0x0u,0x0u,0x0u );
bool jfig(in uint x, in uint y) {
    uint id = x + (JFIGH-1u-y)*JFIGW;
    if (id>=JFIGW*JFIGH) return false;
    return 0u != (jfig_bitfield[id/32u] & (1u << (id&31u)));
}

struct Ray {
	vec3 origin;
    vec3 dir;
};

struct Box {
    vec3 center;
    vec3 halfsize;
};

struct Light {
    vec3 position;
    vec3 color;
};

Light[] lights = Light[]( Light(vec3(-15,10,10), vec3(1,1,1)) );

bool box_ray_intersect(in Box box, in Ray ray, out vec3 point, out vec3 normal) {
	for (int d=0; d<3; d++) {
    	if (abs(ray.dir[d])<1e-5) continue; 
		float side = (ray.dir[d] > 0. ? -1.0 : 1.0);
        float dist = (box.center[d] + side*box.halfsize[d] - ray.origin[d]) / ray.dir[d];
		if (dist < 0.) continue;
        point = ray.origin + ray.dir*dist;
        int i1 = (d+1)%3;
        int i2 = (d+2)%3;
        if (point[i1]>box.center[i1]-box.halfsize[i1] && point[i1]<box.center[i1]+box.halfsize[i1] &&
        	point[i2]>box.center[i2]-box.halfsize[i2] && point[i2]<box.center[i2]+box.halfsize[i2]) {
            normal = vec3(0);
            normal[d] = side;
            return true;
        }
	}
    return false;
}

vec3 hs2rgb(vec2 c) {
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

vec3 colorFromIndex(float index) {
    return hs2rgb(vec2(fract((index * 12.0)), index));
}

// A single iteration of Bob Jenkins' One-At-A-Time hashing algorithm.
uint hash( uint x ) {
    x += ( x << 10u );
    x ^= ( x >>  6u );
    x += ( x <<  3u );
    x ^= ( x >> 11u );
    x += ( x << 15u );
    return x;
}

// anti-unroll trick to help the compiler
#define Z min(0,iFrame)

bool bunny_ray_intersect(in Ray ray, out vec3 point, out vec3 normal, out vec3 color) {
    float bunny_dist = 1e10;
    vec3 p = vec3(0), n = vec3(0);
    for (int i=Z; i<BUNW; i++) {
    	for (int j=Z; j<BUNH; j++) {
    		for (int k=Z; k<BUND; k++) {
                int cellID = i+j*BUNW+k*BUNW*BUNH;
				if (!bunny(cellID)) continue;
                Box box = Box(vec3(i-BUNW/2,j-BUNH/2,-k+BUND/2)*BUNVOXSIZE+vec3(.5,.5,-.5)*BUNVOXSIZE, vec3(1.,1.,1.)*BUNVOXSIZE*.45);
                if (box_ray_intersect(box, ray, p, n) && length(p-ray.origin) < bunny_dist) {
                    bunny_dist = length(p-ray.origin);
                    point = p;
                    normal = n;
                    color = colorFromIndex(float(int(hash(uint(cellID)))%144)/144.);
                }
    		}
    	}
    }
	return bunny_dist < 1e3;
}

vec3 cast_ray(in Ray ray) {
    vec3 p = vec3(0), n = vec3(0), c = vec3(0);
    if (!bunny_ray_intersect(ray, p, n, c))
        return texture(iChannel0, ray.dir).xyz;

   	vec3 diffuse_light = vec3(0.);
    for (int i=0; i<lights.length(); i++) {
       	vec3 light_dir = normalize(lights[i].position - p);
        vec3 shadow_orig = dot(light_dir, n) < 0. ? p - n*1e-3 : p + n*1e-3;
	    vec3 shadp, shadn, shadc;
        if (bunny_ray_intersect(Ray(shadow_orig, light_dir), shadp, shadn, shadc)) {
            continue;
        }
       	diffuse_light  += lights[i].color * max(0., dot(light_dir, n));
    }

    return c*(vec3(.7,.7,.7) + diffuse_light);
}

vec3 rotateCamera(in vec3 orig, in vec3 dir, in vec3 target) {
    vec3 zAxis = normalize(orig - target);
    vec3 xAxis = normalize(cross(vec3(0., 1., 0.), zAxis));
	vec3 yAxis = normalize(cross(zAxis, xAxis));
   	mat4 transform = mat4(vec4(xAxis, 0.), vec4(yAxis, 0.), vec4(zAxis, 0.), vec4(orig, 1.));
    return (transform * vec4(dir, 0.)).xyz;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    const float fov = 3.1416 / 4.;
    vec2 uv = ( 2.*fragCoord - iResolution.xy ) / iResolution.y*tan(fov/2.); 

    vec3 orig = vec3(-sin(iTime/4.), 0., cos(iTime/4.));
    vec3 dir = normalize(vec3(uv, -1));
	dir = rotateCamera(orig, dir, vec3(0.));

    vec3 col = cast_ray(Ray(orig, dir));
    float m = max(col.x, max(col.y, col.z));
    if (m>1.) col = col / m;

    vec2 coord = fragCoord/iResolution.xy*vec2(JFIGW, JFIGH);
    if (jfig(uint(coord.x), uint(coord.y)))
        col += vec3(.5);
