int matID = 0;
mat2 rotate(float a){
	return mat2(cos(a),-sin(a),sin(a),cos(a));
}
float hash21(vec2 p){
	return(fract(sin(p.x*22.+p.y*421.)*999.));
}
vec3 randInSphere(vec2 seed){
	vec3 p;
    int i = 0;
    do {
        p = 2.0*vec3(hash21(float(i)*seed+fract(iTime)*22.),hash21(float(i)+seed+fract(iTime)*43.),
                     hash21(seed-float(i)+fract(iTime+float(i))*97.)) - vec3(1,1,1);
    } while (dot(p,p) >= 1.0 && i++ < 500);
    return p;
}
float sdfSphere(vec3 p){
	return length(p)-(sin(p.y*sin(p.x*2.)*3.)*1.3)-1.;
}

float sdfPlane(vec3 p){
    return p.y +1.5;
}
float map(vec3 p){

    float sphere = sdfSphere(p);
    float plane = sdfPlane(p);
    float best = min(sphere,plane);
    if(best == sphere){matID = 2;}
    if(best == plane){matID=3;}
    return best;
}
vec3 normal(vec3 p){
    vec2 e= vec2(0,0.001);
	return normalize(vec3(map(p+e.yxx)-map(p-e.yxx),
                          map(p+e.xyx)-map(p-e.xyx),
                          map(p+e.xxy)-map(p-e.xxy)));
}
vec3 march(vec3 ro, vec3 rd){
    float total = 0.;
    float dist = 0.;
    for(int i = 0; i<300; i++){
        dist = map(ro+rd*total);
        total+=dist;
        if(dist < 0.01){
        	break;
        }
    }
    if(dist>0.01){
    	matID = 1;
    }

    return (ro+rd*total);

}
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    fragColor = texture(iChannel0,fragCoord/iResolution.xy);
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
	vec3 ro = vec3(0,0,-6);
    vec3 rd = normalize(vec3(uv+hash21(uv)/iResolution.y*0.5,1));
    vec3 col = vec3(0);
    vec3 accum = vec3(1);

    for(int i = 0; i<4;i++){


        col = (march(ro,rd));

        if(matID == 1){

           col =  1.-vec3(0.1,0.1,0.1);
           accum*=col;

        }
        if(matID ==2){

           vec3 n = normal(col);
           rd = randInSphere(uv)+n;
           ro = col+rd*0.1;

        }
        if(matID ==3){
            vec3 n = normal(col);
            rd = randInSphere(uv)+n;
            ro = col+rd*0.1;
        }

    }

    fragColor += vec4(1.-accum,1.0);
}

