#define LOD4 ////////////////////////// UNCOMMENT THIS LINE FOR FULL DETAIL (Compile time is ~180s)
#define MAX_STEPS 200
#define MAX_DIST 100.0
#define SURF 0.01
#define EP 0.001
#define PI 3.14159
#define MIN_C(a, b) a.x < b.x ? a : b
#define MIN_C4(a, b, c, d) a.x < b.x ? (a.x < c.x ? (a.x < d.x ? a : d) : (c.x < d.x ? c : d)) : (b.x < c.x ? (b.x < d.x ? b : d) : (c.x < d.x ? c : d))
#define MIN_C3(a, b, c) a.x < b.x ? (a.x < c.x ? a : c) : (b.x < c.x ? b : c)
#define MAX_C(a, b) a.x > b.x ? a : b

mat2 rot(float a) {
    float x = sin(a);
    float y = cos(a);
    return mat2(y, x, -x, y);
}

float sdLens(vec3 p) {
    float l1 = length(p-vec3(0,0,1.7))-2.;
    float l2 = length(p+vec3(0,0,1.7))-2.;
    if (l1 < -0.002 && l2 < -0.002) {
        return min(-l1-0.004, -l2-0.004);
    }
    return max(l1, l2);
}

//Rect with proper internal distances
float sdRectInternal(vec3 p, vec3 s) {
    float a = 0.002;
    if (abs(p.x) < s.x-a && abs(p.y) < s.y-a && abs(p.z) < s.z-a) {
        s-=a*2.;
        return min(s.x-abs(p.x), min(s.y-abs(p.y), s.z-abs(p.z)));
    }
    return max(max(p.x-s.x, p.y-s.y), max(max(p.z-s.z, -p.x-s.x), max(-p.y-s.y, -p.z-s.z)));
}

float sdRect(vec3 p, vec3 s) {
    return max(max(p.x-s.x, p.y-s.y), max(max(p.z-s.z, -p.x-s.x), max(-p.y-s.y, -p.z-s.z)));
}


float sdEllipse(vec3 p, vec3 s) {
    float len = length(p/s)-1.;
    return len*min(s.x,min(s.y,s.z));
}

float pseudoHash(float v) {
    return mod(sin(35693.14159*v)*147.34+147.34, 2.)-1.;
}

float pseudoHash(vec3 v) {
    return pseudoHash(v.x + pseudoHash(v.z));
    return pseudoHash(v.z + pseudoHash(v.y + pseudoHash(v.x)));
}


float sdPlaneBumpy(vec3 p) {
    float b = 0.05*snoise(vec3(p.x,iTime,p.z));
    return abs(p.y-b)-.002;
}

vec2 getDist(vec3 p) {
    float sphere = length(p-vec3(2,0,-2)) - 1.;
    float plane = sdPlaneBumpy(p-vec3(0,0,0));
    vec3 cubePos = p - vec3(-2, 1, -1);
    cubePos.xy *= rot(PI/7.);
    cubePos.yz *= rot(PI/8.);
    float cube = sdRectInternal(cubePos, vec3(1));
    vec3 cubePos2 = p - vec3(2,0,1);
    cubePos2.xy *= rot(-PI/7.);
    float cube2 = sdRectInternal(cubePos2, vec3(.1, 2, .1));
    float container = sdRect(p-vec3(0), vec3(4,1,4));
    float lens = sdLens(p-vec3(0,1,2));
    vec2 lens_c = vec2(lens, 3);
    vec2 water_c = vec2(max(plane, container), 0);
    vec2 cube_c = vec2(min(cube, cube2), 1);
    vec2 sphere_c = vec2(sphere, 2);
    return MIN_C4(cube_c, water_c, sphere_c, lens_c);
}

vec2 getRayDist(vec3 rp, vec3 rd) {
    float d = 0.;
    vec3 p = rp;
    vec2 ret;
    for(int i = 0; i < MAX_STEPS; i++) {
        p = rp + rd*d;
        ret = getDist(p);
        d += ret.x;
        if(ret.x <= SURF || d > MAX_DIST) break;
    }
    ret.x = d;
    return ret;
}

vec3 getNorm(vec3 p) {
    float c = getDist(p).x;
    vec2 ep = vec2(EP,0);
    vec3 norm;
    vec3 eps[3] = vec3[3](ep.xyy, ep.yxy, ep.yyx);
    for (int i = min(0,iFrame); i < 3; i++) {
        norm[i] = getDist(p+eps[i]).x-c;
    }
    //vec3 norm = vec3(getDist(p+ep.xyy).x-c, getDist(p+ep.yxy).x-c, getDist(p+ep.yyx).x-c);
    return normalize(norm);
}

vec3 reflectRay(vec3 ray, vec3 norm) {
    float d = dot(ray, norm);
    vec3 side = ray - d*norm;
    return -d*norm + side;
}

vec3 lookupColor(float v) {
    if (v == 3.) {
        return vec3(1); //lens
    }
    if (v == 2.) {
        return vec3(.5, 1, .5); //ball
    }
    if (v == 1.) {
        return vec3(1, 0, 0); //box
    }
    return vec3(0.3, 0.5, 1); //water
}

// x: reflectivity, y: transparency, z: refractive index
vec3 lookupReflectivity(float v) {
    if (v == 3.) {
        return vec3(0.0, 0.5, 1.1);
    }
    if (v == 2.) {
        return vec3(0.5, 0.0, 1.0);
    }
    if (v == 1.) {
        return vec3(0.0, 0.3, 1.1);
    }
    return vec3(0.3, 0.3, 1.4);
}

vec3 getColor4(vec3 rp, vec3 rd) {
    vec2 ref_ray = getRayDist(rp, rd);
    float refD = ref_ray.x;
    vec3 refP = rp + rd*refD;
    vec3 refCol = vec3(0);
    if (refD > 99.) {
        return vec3(0);
    }
    float refShade = clamp(dot(vec3(0,1,0), getNorm(refP)), .3, 1.);
    refCol = refShade*lookupColor(ref_ray.y);
    return refCol;
}

vec3 getColor3(vec3 rp, vec3 rd) {
    vec2 first_ray = getRayDist(rp, rd);
    float d = first_ray.x;
    vec3 p = rp + rd*d;
    vec3 norm = getNorm(p);

    float shade = clamp(dot(vec3(0,1,0), norm), .3, 1.);

    if (d > 99.) {
        return vec3(0);
    }
    vec3 ret = shade*lookupColor(first_ray.y);

    vec3 props = lookupReflectivity(first_ray.y);
    float reflectivity = props.x;
    float transparency = props.y;
    float refractivity = props.z;
    vec3 refl_dir = reflectRay(rd,norm);
    vec3 refl_rp = rp + rd*(d-SURF*2.);

	vec3 refr_dir = refract(rd,norm,1.0/refractivity);
    float dotnorm = dot(norm, rd);
    vec3 refr_rp = rp + rd*(d-SURF*4./dotnorm);

    vec3 refrCol;
    vec3 refCol;
    for (int i = 0; i < 2; i++) {
        vec3 p, d;
