//these are the lil red nubs 

float crownBoops(vec2 pos){

	float sphereDiff = 0.9- (length(pos) - 0.1);

	float sphere = (length(pos) - 0.83);

 	float angle=atan(pos.y, pos.x); 

    float boop = sphere- (pow( abs(sin(angle*spokes)),3.)* 0.331);

    boop += noise(angle*60.)*0.09; // make the end a lil noisy

    return  max(boop, sphereDiff); // take the difference so we only get the top part of the sin wave
}

float virus(vec2 pos){

	float sphere = (length(pos) - 0.63 );// the base

 	float angle=atan(pos.y, pos.x); // angle around the circle
    float wave = (pow( abs(sin(angle*spokes)),6.)* 0.31); // goes up and down around the circle
    return sphere- wave + (noise(sin(angle*2.)) * 0.1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord/iResolution.xy - 0.5)   *2.0; // (0,0) is in the center of the screen

    uv *=1.5; // idk make it a lil smaller

	uv.x *= iResolution.x/iResolution.y; // sadly the screen is not square, we must fix uv ratio

    float shape1 = crownBoops(uv);
    float shape2 = virus(uv);

    vec3 col = vec3(smin(shape1,shape2,8.0 )); // smooth min the top nubs and the rest of the virus

    col = ceil(col); // everthing is an SDF which is a gradient but we want hard lines so we ceiling the coloe 

	col = vec3(1)-col; // make it white on black 

    col.yz -= length(pow2D(uv,4.0) * vec2(0.5)); // add the red

    // Output to screen
    fragColor = vec4(col,1.);
}

