float sdSphere( vec3 p, float s )
{
	return length(p)-s;
}

float sdBox(vec3 p, vec3 b)
{
	vec3 q = abs(p) - b;
	return length(max(q, 0.)) + min(max(q.x, max(q.y,q.z)), 0.);
}

mat3 mo;

float mapCube(vec3 p)
{
    p = mo * p * 2.;
    float	radius = 0.45;
    vec3	v = vec3(1,1,1);
    vec3	q = mod(p + v / 2., v) - v / 2.0;
	float	ds = sdSphere(q, radius);
    float	db = sdBox(p, vec3(1. + radius));
    return max(ds, db) / 2.;
}

float sdRoundBox( vec3 p, vec3 b, float r )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0) - r;
}

float map(vec3 p)
{	
    return min(mapCube(p), -sdRoundBox( p, vec3(1.6), 0.3 ));    
}

#define EPS  0.0001

vec3 calcNormal(vec3 pos)
{

    vec3	col;
    vec2	eps = vec2(EPS, 0);
	float d = map(pos);
	return normalize(vec3(map(pos + eps.xyy) - d, map(pos + eps.yxy) - d, map(pos + eps.yyx) - d));
}

void rayMarch(vec3 ro, vec3 rd, out float t, out float d, in float maxd)
{
    t = 0.;
    d = 0.;
    vec3 cp = ro;
    for(int i=0;i<90;++i)
    {
        d = map(cp);
        t += d;
        cp = ro+rd*t;
        if (d < .001 || d > maxd)
            break;
    }
}

vec3 applyFog(vec3 rgb,float distance, vec3 fogColor)
{
    float fogAmount = 1.0 - exp( -distance*0.3 );
    return mix( rgb, fogColor, fogAmount );
}


