vec3 NAME(vec3 ro,vec3 rd)													\
{																			\
    float	t,d;															\
    rayMarch(ro, rd, t, d, DIST);											\
    vec3 p = ro + rd * t;													\
    vec3 n = calcNormal(p);													\
    vec3 color;																\
    if (d < .001)															\
    {																		\
        float fresnel = pow(1.0 - abs(dot(n, rd)), 2.0);					\
        vec3 d = mix(vec3(1), vec3(0.82, 0.45, 0.20), fresnel);				\
