//
// Lagrangian Mechanics
//
// Matthew Arcus, 2020
//
// Double pendulum, simulated by direct numeric solution of Euler-Lagrange
// equations.
//
// Uncomment #define of ORBIT in Common to see elliptical orbit.
//
////////////////////////////////////////////////////////////////////////////////

void mainImage(out vec4 fragColor, vec2 fragCoord) {
  vec2 uv = 2.0*fragCoord.xy/iResolution.y;
  vec3 col = vec3(1);
  float colwidth = 0.025;

  if (uv.x < 5.0*colwidth) {
    vec4 ev = texelFetch(iChannel1,ivec2(0,2),0);
    float etot = dot(ev,vec4(1));
    bool incolumn = false;
    if (uv.x < 1.0*colwidth) incolumn = uv.y < etot;
    else if (uv.x < 2.0*colwidth) incolumn = uv.y < 0.5+ev.x/etot;
    else if (uv.x < 3.0*colwidth) incolumn = uv.y < 0.5+ev.y/etot;
    else if (uv.x < 4.0*colwidth) incolumn = uv.y < 0.5+ev.z/etot;
    else incolumn = uv.y < 0.5+ev.w/etot;
    if (incolumn) col = vec3(0);
  } else {
    vec4 dv = texelFetch(iChannel1,ivec2(fragCoord),0);
    col = vec3(smoothstep(0.0,0.01,dv.x));
    // Proper screen coordinates
    uv -= iResolution.xy/iResolution.y;
    uv *= scale;
    // Get pv values
    vec4 pv = texelFetch(iChannel1,ivec2(0,0),0);
    // and coordinates
    vec4 coords = getcoords(pv.xy,pv.zw);
    vec2 p0 = vec2(0), p1 = coords.zw, p2 = coords.xy;
    float d = min(segment(uv,p0,p1),segment(uv,p1,p2));
    col = mix(vec3(0),col,smoothstep(0.0,0.01,d));
    d = min(distance(uv,p0),min(distance(uv,p1),distance(uv,p2)));
    col = mix(vec3(1,0,0),col,smoothstep(0.02,0.03,d));
  }
  if (alert) col = vec3(1,0,0);
  fragColor = vec4(col,1);    
}

