void mainImage(out vec4 t, vec2 uv) {
  int i = int(uv.x), j = int(uv.y); // Texel coordinates
  t = texelFetch(iChannel1,ivec2(i,j),0); // Get current state     
  if (i == 0 && j == 0) {
    if (iFrame == 0) {
      t = initpv;
    } else {
      // Get last p,v and last time..
      vec4 pv = texelFetch(iChannel1,ivec2(0,0),0);
      vec4 tv = texelFetch(iChannel1,ivec2(0,1),0);
      // .. and update
      t = lagrange(pv.xy,pv.zw,iTime-tv.x);
    }
  } else if (i == 0 && j == 1) {
    // Store last time and screen resolution
    t = vec4(iTime,0,iResolution.xy);
  } else if (i == 0 && j == 2) {
    // Output energy values.
    vec4 pv = texelFetch(iChannel1,ivec2(0,0),0);
    t = E(pv.xy,pv.zw);
  } else if (iFrame == 0 || texelFetch(iChannel1,ivec2(0,1),0).zw != iResolution.xy) {
    // If first frame or screensize has changed, reset min distance.
    t = vec4(1e8);
  } else {
    // Otherwise, update minimum distance to current coords
    uv = (2.0*uv-iResolution.xy)/iResolution.y;
    uv *= scale;
    vec4 pv = texelFetch(iChannel1,ivec2(0,0),0);
    vec4 coords = getcoords(pv.xy,pv.zw);
    t.x = min(t.x,distance(uv,coords.xy));
    t.y = min(t.y,distance(uv,coords.zw));
  }
}

