float segment(vec2 p, vec2 q, vec2 r) {
  vec2 pa = p-q;
  vec2 ba = r-q;
  float h = clamp(dot(pa,ba)/dot(ba,ba),0.0,1.0);
  float d = length(pa-h*ba);
