// The Maths
////////////////////////////////////////////////////////////////////////////////

// The energy function for the Lagrangian
vec4 E(vec2 p, vec2 v);

float L(vec2 p, vec2 v) {
  vec4 e = E(p,v);
  float ke0 = e.x,ke1 = e.y,pe0 = e.z,pe1 = e.w;
  return (ke0+ke1)-(pe0+pe1);
}

// Using Richardson & fixed step Runge-Kutta gives reasonable
// accuracy, but slower compilation.
// Variable step RK doesn't always help.
#if defined QUALITY
#define pdiffLp pdiffLp1
#define pdiffLv pdiffLv1
#define derivs derivs1
#else
#define pdiffLp pdiffLp0
#define pdiffLv pdiffLv0
#define derivs derivs0
#endif
#define step rk4
//#define step rk4var
//#define step single

// Basic partial differentiation wrt p.
float pdiffLp0(vec2 p, vec2 v, int i,float eps) {
  float pi = p[i];
  p[i] = pi-eps;
  float x0 = L(p,v);
  p[i] = pi+eps;
  float x1 = L(p,v);
  p[i] = pi;
  return (x1-x0)/(2.0*eps);
}

float pdiffLv0(vec2 p, vec2 v, int i,float eps) {
  float vi = v[i];
  v[i] = vi-eps;
  float x0 = L(p,v);
  v[i] = vi+eps;
  float x1 = L(p,v);
  v[i] = vi;
  return (x1-x0)/(2.0*eps);
}

// Richardson extrapolation
float pdiffLv1(vec2 p, vec2 v,int i,float eps) {
  float d1 = pdiffLv0(p,v,i,eps);
  float d2 = pdiffLv0(p,v,i,2.0*eps);
  return d1+(d1-d2)/3.0;
}

float pdiffLp1(vec2 p, vec2 v,int i,float eps) {
  float d1 = pdiffLp0(p,v,i,eps);
  float d2 = pdiffLp0(p,v,i,2.0*eps);
  return d1+(d1-d2)/3.0;
}

// NB: this modifies parameters p and v
void evaluate(inout vec2 p, inout vec2 v, float eps);

// Time derivatives
void derivs0(vec2 p, vec2 v, out vec2 dp, out vec2 dv, float eps) {
  vec2 p1 = p, v1 = v;
  vec2 p2 = p, v2 = v;
  evaluate(p1,v1,eps);
  evaluate(p2,v2,-eps);
  dp[0] = (p1[0]-p2[0])/(2.0*eps);
  dp[1] = (p1[1]-p2[1])/(2.0*eps);
  dv[0] = (v1[0]-v2[0])/(2.0*eps);
  dv[1] = (v1[1]-v2[1])/(2.0*eps);
}

// It's that Richardson again.
// https://en.wikipedia.org/wiki/Lewis_Fry_Richardson
void derivs1(vec2 p, vec2 v, out vec2 dp, out vec2 dv, float eps) {
  vec2 p1,p2,v1,v2;
  derivs0(p,v,p1,v1,eps);
  derivs0(p,v,p2,v2,2.0*eps);
  dp[0] = p1[0] + (p1[0]-p2[0])/3.0;
  dp[1] = p1[1] + (p1[1]-p2[1])/3.0;
  dv[0] = v1[0] + (v1[0]-v2[0])/3.0;
  dv[1] = v1[1] + (v1[1]-v2[1])/3.0;
}

// d(df/dxj)/dxi - components of Hessian (Jacobian for PDs).
float hessian(int i,int j,vec2 p,vec2 v,float eps) {
  //return pdiffHess(function(a) { return pdiff(f,a,j,eps);}, a,i,eps);
  float vi = v[i];
  v[i] = vi-eps;
  float x0 = pdiffLv(p,v,j,eps);
  v[i] = vi+eps;
  float x1 = pdiffLv(p,v,j,eps);
  v[i] = vi;
  return (x1-x0)/(2.0*eps);
}

// Vector of partial derivs
vec2 pdiffsLp(vec2 p,vec2 v,float eps) {
  return vec2(pdiffLp(p,v,0,eps),pdiffLp(p,v,1,eps));
}

vec2 pdiffsLv(vec2 p,vec2 v,float eps) {
  return vec2(pdiffLv(p,v,0,eps),pdiffLv(p,v,1,eps));
}

void single(inout vec2 p, inout vec2 v, float dt, float eps) {
  vec2 dp,dv;
  derivs(p,v,dp,dv,eps);
  p[0] += dt*dp[0];
  p[1] += dt*dp[1];
  v[0] += dt*dv[0];
  v[1] += dt*dv[1];
}

void rk4(inout vec2 p, inout vec2 v, float dt, float eps) {
  vec2 p1, v1,p2,v2,p3,v3,p4,v4;
  derivs(p,v,p1,v1,eps);
  derivs(p+0.5*dt*p1,
         v+0.5*dt*v1,
         p2,v2,eps);
  derivs(p+0.5*dt*p2,
         v+0.5*dt*v2,
         p3,v3,eps);
  derivs(p+dt*p3,
         v+dt*v3,
         p4,v4,eps);
  p[0] += dt*(p1[0] + 2.0*p2[0] + 2.0*p3[0] + p4[0])/6.0;
  p[1] += dt*(p1[1] + 2.0*p2[1] + 2.0*p3[1] + p4[1])/6.0;
  v[0] += dt*(v1[0] + 2.0*v2[0] + 2.0*v3[0] + v4[0])/6.0;
  v[1] += dt*(v1[1] + 2.0*v2[1] + 2.0*v3[1] + v4[1])/6.0;
}

// Variable step Runge-Kutta.
int steps = 1;
void rk4var(inout vec2 p,inout vec2 v,float dt, float eps) {
  while (true) {
    vec2 p1 = p, v1 = v, p2 = p, v2 = v;
    rk4(p1,v1,dt/float(steps),eps);
    rk4(p2,v2,dt/float(2*steps),eps);
    vec2 p3 = p2,v3 = v2;
    rk4(p3,v3,dt/float(2*steps),eps);
    //Sum the squares of the errors
    vec2 errorvec = v3-v1;
    float error = dot(errorvec,errorvec);
    if (error > 1e-5 && steps < 100) {
      steps *= 2;
    } else if (error < 1e-7 && steps > 1) {
      steps /= 2;
      //p = p2; v = v2; // FIXME: this is wrong!
      rk4(p,v,dt/float(steps),eps); // Recalculate for smaller step
      break;
    } else {
      p = p3; v = v3;
      break;
    }
  }
  for (int i = 1; i < steps; i++) {
    rk4(p,v,dt/float(steps),eps);
  }
}

// NB: this modifies parameters p and v
void evaluate(inout vec2 p, inout vec2 v, float eps) {
  //var pp = pdiffs(function(p) { return L(p,v,t) }, p,eps);
  //var pv = pdiffs(function(v) { return L(p,v,t) }, v,eps);
  //p0 = p; v0 = v;
  vec2 pp = pdiffsLp(p,v,eps);
  vec2 pv = pdiffsLv(p,v,eps);
  p[0] += v[0]*eps;
  p[1] += v[1]*eps;
  // Compute new values of pv (apply Euler-Lagrange)
  pv[0] += pp[0]*eps;
  pv[1] += pp[1]*eps;
  // pv is PD of L at t+eps
  // Now find new v' st pdiff(Lp,v') = pv
  // Initial guess is v, use Hessian to refine guess
  // F(v') = F(v+dv) = F(v) + (JF)(dv), here F is the partial
  // derivatives of L at v.
  // Then v' = v + dv where dv = inv(JF)(F(v')-F(v))
  // Doing this step 2 or 3 times seems to be enough
  // I'm surprised it works at all really.
  for (int k = 0; k < 3; k++) {
    vec2 pv2 = pdiffsLv(p,v,eps);
    vec2 t = pv-pv2;
    float a = hessian(0,0,p,v,eps);
    float b = hessian(0,1,p,v,eps);
    float c = b; // Symmetry
    float d = hessian(1,1,p,v,eps);
    float det = a*d - b*c; // If det is too small, we'll just diverge here
    v[0] += (d*t[0] - b*t[1])/det;
    v[1] += (-c*t[0] + a*t[1])/det;
  }
}

