// Lagrangian for Double Pendulum
float G = 1.0; // Gravity
float M0 = 1.0, M1 = 1.0; // Bob weight
float R0 = 0.5, R1 = 0.618; // Pendulum section length.
vec4 initpv = vec4(0.5*PI,0.5*PI,0.01,0);

vec4 E(vec2 p, vec2 v) {
  float y0,y1,ke0,ke1,pe0,pe1;
  vec2 v0,v1;
  y0 = R0*sin(p[0]);
  y1 = y0 + R1*sin(p[1]);
  v0 = vec2(-R0*sin(p[0])*v[0], R0*cos(p[0])*v[0]);
  v1 = vec2(-R1*sin(p[1])*v[1], R1*cos(p[1])*v[1]);
  ke0 = 0.5*M0*(dot(v0,v0));
  ke1 = 0.5*M1*(dot(v0+v1,v0+v1));
  pe0 = G*M0*y0;
  pe1 = G*M1*y1;
  return vec4(ke0,ke1,pe0,pe1);
}

// Convert into euclidean coordinates.
vec4 getcoords(vec2 p,vec2 v) {
  float x0,y0,x1,y1;
  x0 = R0*cos(p[0]);
  y0 = R0*sin(p[0]);
  x1 = x0 + R1*cos(p[1]);
  y1 = y0 + R1*sin(p[1]);
  return vec4(x1,y1,x0,y0);
}
