float maxstep = 5e-3;
vec4 lagrange0(vec2 p, vec2 v,float dt) {
  float eps = 5e-3;
  while(dt > 0.0) {
    //var f = function(p,v,t,eps) { Lagrange.evaluate(system.L,p,v,t,eps); }
    float delta = min(maxstep,dt);
    step(p,v,delta,eps);
    dt -= delta;
  }
  return vec4(p,v);
}
vec4 lagrange(vec2 p, vec2 v,float dt) {
  step(p,v,dt,5e-3);
  return vec4(p,v);
}

