vec2 cmul (vec2 a, vec2 b) {
	return vec2(a.x * b.x - a.y * b.y, a.x * b.y + b.x * a.y);
}
vec2 cexp (vec2 z) {
	return exp(z.x) * vec2(cos(z.y), sin(z.y));
}
vec2 clog (vec2 z) {
	return vec2(log(length(z)), atan(z.y, z.x));
}
vec2 cpow (vec2 z, vec2 a) {
