float sdSegment( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

vec4 grad(ivec2 p)
{
    ivec4 sp = get(p);
    ivec4 spx = get(p+ivec2(1,0));
    ivec4 spy = get(p+ivec2(0,1));
    return tanh(100.*(abs(vec4(spx - sp)) + abs(vec4(spy - sp))));
}

ivec4 getve(ivec2 p)
{
    return floatBitsToInt(texel(ch2, p));
}

float edge_distance(vec2 spos, vec2 pos)
{
    ivec2 p = ivec2(spos);

    ivec4 ve = getve(p);//edge side particles

    //if invalid
    if(any(equal(ve.xy, ve.zw)) || any(equal(ivec4(-1), ve))) return 1e10;

    vec4 p0 = getParticle(ve.z);
    vec4 p1 = getParticle(ve.w);

    vec4 pe0 = getParticle(ve.x);
    vec4 pe1 = getParticle(ve.y);

    vec2 edge0 = circumcenter(p0.xy, p1.xy, pe0.xy);
    vec2 edge1 = circumcenter(p0.xy, p1.xy, pe1.xy);

    return sdSegment(pos, edge0, edge1);
}

float sdTriangle( in vec2 p, in vec2 p0, in vec2 p1, in vec2 p2 )
{
    vec2 e0 = p1-p0, e1 = p2-p1, e2 = p0-p2;
    vec2 v0 = p -p0, v1 = p -p1, v2 = p -p2;
    vec2 pq0 = v0 - e0*clamp( dot(v0,e0)/dot(e0,e0), 0.0, 1.0 );
    vec2 pq1 = v1 - e1*clamp( dot(v1,e1)/dot(e1,e1), 0.0, 1.0 );
    vec2 pq2 = v2 - e2*clamp( dot(v2,e2)/dot(e2,e2), 0.0, 1.0 );
    float s = sign( e0.x*e2.y - e0.y*e2.x );
    vec2 d = min(min(vec2(dot(pq0,pq0), s*(v0.x*e0.y-v0.y*e0.x)),
                     vec2(dot(pq1,pq1), s*(v1.x*e1.y-v1.y*e1.x))),
                     vec2(dot(pq2,pq2), s*(v2.x*e2.y-v2.y*e2.x)));
    return -sqrt(d.x)*sign(d.y);
}

vec4 getEdge(ivec2 p, out ivec4 nb, out mat4 edgep)
{
    nb = getve(p);//edge side particles

    vec4 p0 = getParticle(nb.z);
    vec4 p1 = getParticle(nb.w);
    edgep[0] = p0;
    edgep[1] = p1;

    vec4 pe0 = getParticle(nb.x);
    vec4 pe1 = getParticle(nb.y);
    edgep[2] = pe0;
    edgep[3] = pe1;

    vec2 edge0 = circumcenter(p0.xy, p1.xy, pe0.xy);
    vec2 edge1 = circumcenter(p0.xy, p1.xy, pe1.xy);
	return vec4(edge0, edge1);
}

