vec2 find_next_point(ivec2 nb, vec2 c0, vec2 c1, vec2 cc)
{
    vec2 p; 
    ivec4 cp;
    vec2 dx = normalize(mix(c0,c1,0.5) - cc);
    vec2 dy = normalize(c0 - cc);
    for(float y = 0.; y < 2.; y++)
    {
        for(float x = 1.; x < 4.; x++)
        {
            p = cc + dx*x + dy*y;
            cp = getve(ivec2(p+1.));
            if(all(equal(cp.zw,nb.xy)) ) break;
        }
        if(all(equal(cp.zw,nb.xy)) ) break;
    }
    return p;
}

int exclude(ivec2 ij, int i)
{
    return (i == ij.x)?ij.y:ij.x;
}

float sample_border(vec2 spos, vec2 pos)
{
    ivec2 p = ivec2(spos);

    ivec4 cp = getve(p).zwzw;//closest particles 
    vec4 p0 = getParticle(cp.x);


    ivec4 nb; mat4 edgep;
    int prev = -1; int next = -1;
    float de = 1e10;
    for(int i = 0; i < 10; i++) //loop around
    {
    	vec4 edge = getEdge(p, nb, edgep);
        de = min(de,sdSegment(pos, edge.xy, edge.zw));

        //go to next edge
        next = nb.y;
        prev = exclude(nb.zw, cp.x);
        //p = ivec2(round( edge.zw + 2.*normalize(0.5*(p0.xy + getParticle(next).xy) - edge.zw) ));
        p = ivec2(find_next_point(ivec2(cp.x, prev), p0.xy, edgep[3].xy, edge.zw));
        de = min(de, length(edgep[3].xy - pos));

        if(next == cp.y || next == cp.x) break;
    }
    return de;
}

