//voronoi edge tracking

//neighbors 
vec4 nbxy; //positions
ivec2 nbu; //ids

//side particles
vec2 d;
ivec2 u;

bool isSame(ivec3 a, ivec3 b) //is the id triplet the same
{
    return any(equal(a.xxx,b)) && any(equal(a.yyy,b)) && any(equal(a.zzz,b));
}

bool isEdgeParticle(int id, vec2 pxy)
{
    //edge point guess
    vec2 cc = circumcenter(nbxy.xy, nbxy.zw, pxy);
    //check the voronoi diagram at the guess
    if(cc.x < 0. || cc.y < 0. || cc.x > size.x || cc.y > size.y) return false;
    ivec4 t = get(ivec2(cc));
    //the first 3 closest particles at the edge point pretty much always are the particles that produce the edge
    return isSame(t.xyz, ivec3(nbu.xy, id));
}

vec4 getParticle(int id)
{
    return texel(ch1, i2xy(id));
}

ivec4 getve(ivec2 p)
{
    return floatBitsToInt(texel(ch2, p));
}

float particleDistance(int id, vec2 p)
{
    return distance(getParticle(id).xy, p);
}

void sort(int utemp, ivec2 p)
{
    vec2 pos = vec2(p);
    if(any(equal(ivec2(utemp),nbu)) || utemp == -1) return; //if particle is in the edge pair
    vec4 partic = getParticle(utemp);
    float dtemp = distance(partic.xy, pos);
    if(isEdgeParticle(utemp, partic.xy))
    {
        //sorting
        if(d.x >= dtemp)
        {
            if(d.x == dtemp) return;
            //insert
            d = vec2(dtemp, d.x);
            u = ivec2(utemp, u.x);
        }
        else if(d.y >= dtemp)
        {
            if(d.y == dtemp) return;
            //insert
            d = vec2(d.x, dtemp);
            u = ivec2(u.x, utemp);
        }   
    }
}

void upd(ivec2 p, ivec2 dx)
{
    ivec4 t = get(p+dx);
    sort(t.x, p);
    sort(t.y, p);
    sort(t.z, p);
    sort(t.w, p);
    ivec4 tt = getve(p+dx);
    sort(tt.x, p);
    sort(tt.y, p);
}

void updrad(ivec2 p, int R)
{
    int r = 1<<R;
    upd(p, ivec2(r, 0));
    upd(p, ivec2(-r, 0));
    upd(p, ivec2(0, r));
    upd(p, ivec2(0, -r));
}

void update(ivec2 p)
{
    upd(p, ivec2(0));
    updrad(p, 0);
    updrad(p, 1);
    updrad(p, 2);
}

void mainImage( out vec4 U, in vec2 pos )
{
    ivec2 p = ivec2(pos);

    ivec4 t = get(p);

    nbu = t.xy; //edge pair
    nbxy = vec4(getParticle(t.x).xy,getParticle(t.y).xy);

    //particle indecies
    ivec4 prev = getve(p); 
    u = ivec2(-1);
    d = vec2(1e10); 

    update(p);

    if(any(equal(u,ivec2(-1)))) //if not found the edge use the old data
    {
        u = prev.xy;
        if(any(equal(nbu, prev.xx))) u.x = prev.z;
        if(any(equal(nbu, prev.yy))) u.y = prev.w;
    }

    U = vec4(intBitsToFloat(u), intBitsToFloat(nbu));
}

