/*
	Showing that {rotation around one point} and {translation from one point} can appear similar.

*/

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float twoPi = 8. * atan(1.);

    float radius = .005;
    float speed = .2;
    int numberOfParticles = 35;

    vec2 uv = fragCoord/iResolution.xy;
    float proportions = iResolution.x / iResolution.y;

    vec3 col;
 	vec2 placeTranslation, placeRotation;
    float translation, rotation;
    float dist;
    for (int particle = 0; particle < numberOfParticles; particle++) {
        float particleQuota = float(particle) / float(numberOfParticles);
        placeTranslation.x = 1. * mod(speed * iTime + particleQuota, 1.) * cos(twoPi * (particleQuota));
        placeTranslation.y = proportions * 1. * (mod(speed * iTime + particleQuota, 1.)) * sin(twoPi * (particleQuota));
        placeTranslation *= .25;
        placeTranslation += vec2(.25, .5);
        dist = length(uv.xy - placeTranslation);
        translation += smoothstep(radius, radius - .005, dist);   	
        placeRotation.x = cos(twoPi * (-speed * iTime + particleQuota));
        placeRotation.y = proportions * sin(twoPi * (-speed * iTime + particleQuota));
        placeRotation *= .25 * particleQuota;
        placeRotation += vec2(.75, .5);
        dist = length(uv.xy - placeRotation);
        rotation += smoothstep(radius, radius - .005, dist);   	
    }
    col.r = translation;
    col.g = rotation;
    fragColor = vec4(col, 1.0);
}

// "Health Check" by dr2 - 2020
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

#define AA  0   // optional antialiasing

#define VAR_ZERO min (iFrame, 0)

float PrRoundBoxDf (vec3 p, vec3 b, float r);
float PrSphDf (vec3 p, float s);
float PrRoundCylDf (vec3 p, float r, float rt, float h);
vec3 HexGrid (vec2 p);
float SmoothBump (float lo, float hi, float w, float x);
mat3 StdVuMat (float el, float az);
vec2 Rot2D (vec2 q, float a);
float Noisefv2 (vec2 p);
vec3 VaryNf (vec3 p, vec3 n, float f);

vec3 qHit, ltDir, vnCylOut;
float dstFar, tCur, bCylRad, bCylHt, dCylOut, tPhs, wDisp, gGap;
int idObj;
const int idBod = 1, idEye = 2, idCen = 3, idCenIn = 4, idCor = 5, idCorLt = 6, idDoor = 7;
const float pi = 3.14159, sqrt3 = 1.73205;

#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float RobDf (vec3 p)
{
  vec3 q;
  float dMin, rAngH, rAngL, rAngA, d, ah, s;
  s = sin (2. * pi * mod (0.5 * tPhs, 1.));
  rAngH = -0.7 * s;
  rAngA = 1.1 * s;
  rAngL = 0.5 * s;
  dMin = dstFar;
  p.y -= 0.25;
  q = p; 
  q.y -= 2.3;
  d = max (PrSphDf (q, 0.85), - q.y - 0.2);
  q = p; 
  q.y -= 1.5;
  d = min (d, PrRoundCylDf (q.xzy, 0.9, 0.28, 0.75));
  q = p;  
  q.x = abs (q.x) - 1.05; 
  q.y -= 2.;
  q.yz = Rot2D (q.yz, rAngA * sign (p.x));
  q.y -= -0.5;
  d = min (d, PrRoundCylDf (q.xzy, 0.2, 0.15, 0.6));
  q = p;
  ah = rAngH;
  q.xz = Rot2D (q.xz, ah);
  q.x = abs (q.x) - 0.3;
  q.y -= 3.1;
  q.xy = Rot2D (q.xy, 0.2 * pi);
  q.y -= 0.25;
  d = min (d, PrRoundCylDf (q.xzy, 0.06, 0.04, 0.3));
  q = p;
  q.x = q.x - 0.4;  
  q.y -= 0.95;
  q.yz = Rot2D (q.yz, - rAngL);
  q.y -= -0.65;
  d = min (d, PrRoundCylDf (q.xzy, 0.25, 0.15, 0.65));
  q = p;
  q.x = - q.x - 0.4;  
  q.y -= 0.95;
  q.yz = Rot2D (q.yz, rAngL);
  q.y -= -0.65;
  d = min (d, PrRoundCylDf (q.xzy, 0.25, 0.15, 0.65));
  DMINQ (idBod);
  q = p;
  q.xz = Rot2D (q.xz, ah);
  q.x = abs (q.x); 
  q -= vec3 (0.4, 2.7, 0.7);  
  d = PrSphDf (q, 0.15);
  DMINQ (idEye);
  return dMin;
}

float ObjDf (vec3 p)
{
  vec3 q, qq;
  float dMin, d, r, a, aa, s;
  dMin = dstFar;
  q = p;
  r = length (p.xz);
  q.xz = Rot2D (p.xz, pi / 6.);
  aa = (r > 0.) ? atan (q.z, - q.x) / (2. * pi) : 0.;
  a = floor (3. * aa + 0.5);
  q.xz = Rot2D (q.xz, 2. * pi * a / 3.);
  qq = q;
  s = sign (a + 0.01);
  q.x = (a >= 0.) ? mod (q.x + wDisp * s + a * gGap, 2. * gGap) - gGap :
     mod (q.x + wDisp * s + 0.5 * gGap, gGap) - 0.5 * gGap;
  q.xz = Rot2D (q.xz, - pi * s / 2.);
  dMin = min (dMin, max (RobDf (q), r - bCylRad));
  q = qq;
  q.x += bCylRad;
  q.y -= 2.4;
  d = PrRoundBoxDf (q, vec3 (1., 2.4, 4.), 0.05);
  DMINQ (idDoor);
  q = qq;
  q.y -= 1.9;
  d = length (q.yz) - 2.4;
  q = p;
  q.y -= 2.4;
  d = max (PrRoundCylDf (q.xzy, 5., 0.05, 2.4), - d);
  DMINQ (idCen);
  d = PrRoundCylDf (q.xzy, 3., 0.03, 2.4);
  DMINQ (idCenIn);
  q.y -= 2.7;
  d = PrRoundCylDf (q.xzy, 3., 0.1, 0.3);
  DMINQ (idCor);
  q.y -= 0.6;
  q.xz = Rot2D (p.xz, 2. * pi * floor (12. * aa + 1.5) / 12.);
  q.x += 3.;
  d = PrSphDf (q, 0.4);
  DMINQ (idCorLt);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 120; j ++) {
    d = ObjDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.0005 || dHit > dstFar) break;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.05;
  for (int j = VAR_ZERO; j < 20; j ++) {
    h = ObjDf (ro + rd * d);
    sh = min (sh, smoothstep (0., 0.04 * d, h));
    d += h;
    if (sh < 0.05) break;
  }
  return 0.7 + 0.3 * sh;
}

void InCylHit (vec3 ro, vec3 rd)
{
  vec3 s;
  float a, b, w, ws, srdy;
  dCylOut = dstFar;
  vnCylOut = vec3 (0.);
  a = dot (rd.xz, rd.xz);
  b = dot (rd.xz, ro.xz);
  w = b * b - a * (dot (ro.xz, ro.xz) - bCylRad * bCylRad);
  if (w > 0.) {
    ws = sqrt (w);
    srdy = sign (rd.y);
    if (a > 0.) {
      dCylOut = (- b + ws) / a;
      s = ro + dCylOut * rd;
    } else s.y = bCylHt;
    if (abs (s.y) < bCylHt) vnCylOut.xz = - s.xz / bCylRad;
    else {
      dCylOut = (- srdy * ro.y + bCylHt) / abs (rd.y);
      vnCylOut.y = - srdy;
    }
  }
}

vec3 ShStagGrid (vec2 p)
{
  vec2 q, sq, ss;
  q = p;
  if (2. * floor (0.5 * floor (q.y)) != floor (q.y)) q.x += 0.5;
  sq = smoothstep (0.05, 0.1, abs (fract (q + 0.5) - 0.5));
  q = fract (q) - 0.5;
  ss = 0.25 * smoothstep (0.3, 0.5, abs (q.xy)) * sign (q.xy);
  if (abs (q.x) < abs (q.y)) ss.x = 0.;
  else ss.y = 0.;
  return vec3 (ss.x, 0.8 + 0.2 * sq.x * sq.y, ss.y);
}

vec3 BgCol (vec3 ro, vec3 rd)
{
  vec3 vn, col, qh, rg;
  vec2 qw;
  float hy, hhy, hw, ww, f, b, aa, sRotH, sRotV, sh;
  float ga = 2.39996; // = pi * (3 - sqrt(5))
  InCylHit (ro + vec3 (0., - bCylHt, 0.), rd);
  if (vnCylOut.y == 0.) {
    ro += dCylOut * rd;
    vn = vnCylOut;
    hy = ro.y / bCylHt - 1.;
    hhy = abs (hy) - 0.4;
    aa = atan (vn.x, - vn.z) / pi;
    sRotH = mod (60. * 0.5 * (1. + aa) + 0.5, 1.) - 0.5;
    hw = 0.29;
    ww = 0.28;
    qw = abs (vec2 (sRotH, hhy));
    if (qw.x < ww && qw.y < hw) {
      qw = abs (qw - 0.5 * vec2 (ww, hw)) - vec2 (0.44 * ww, 0.47 * hw);
      if (max (qw.x, qw.y) < 0.) col = vec3 (0.4, 0.5, 0.6) * (0.8 + 0.2 * cos (0.1 * 2. * pi * tCur));
      else col = vec3 (0.4, 0.5, 0.3) * (0.5 + 0.5 * max (dot (vn, ltDir), 0.)) +
         0.5 * pow (max (dot (normalize (ltDir - rd), vn), 0.), 128.);
    } else {
      col = vec3 (0.7, 0.8, 0.7);
      qw -= vec2 (ww, hw);
      if (abs (hy) > (1. - 0.85/16.) || max (qw.x, qw.y) < 0.02) {
        col *= 0.9;
        vn.xz = Rot2D (vn.xz, - pi * aa);
        if (abs (hy) > (1. - 0.85/16.)) {
          sRotV = (1. - abs (2. * SmoothBump (1. - 0.9/16., 0.97, 0.03, abs (hy)) - 1.));
        } else {
          sRotV = (1. - abs (2. * SmoothBump (hw, hw + 0.02, 0.02, abs (hhy)) - 1.)) * sign (hhy);
          vn.xz = Rot2D (vn.xz, 0.4 * pi * sign (sRotH) *
             (1. - abs (2. * SmoothBump (- (ww + 0.02), ww + 0.02, 0.04, sRotH) - 1.)));
        }
        vn.yz = Rot2D (vn.yz, -0.2 * pi * sRotV * sign (hy));
        vn.xz = Rot2D (vn.xz, pi * aa);
      } else {
        rg = ShStagGrid (16. * vec2 (12. * aa, hy));
        col *= rg.y;
        rg.xz *= sign ((abs (vn.x) > 0.5) ? vn.x : vn.z);
        if (abs (vn.x) > 0.5) {
          if (rg.x == 0.) vn.xy = Rot2D (vn.xy, rg.z);
          else vn.xz = Rot2D (vn.xz, rg.x);
        } else {
          if (rg.x == 0.) vn.zy = Rot2D (vn.zy, rg.z);
          else vn.zx = Rot2D (vn.zx, rg.x);
        }
      }
      col = col * (0.4 + 0.6 * max (dot (vn, ltDir), 0.)) +
         0.1 * pow (max (dot (normalize (ltDir - rd), vn), 0.), 64.);
    }
  } else if (vnCylOut.y > 0.) {
    ro += dCylOut * rd;
    qw = Rot2D (ro.xz, pi / 6.);
    qw = Rot2D (qw, 2. * pi * (floor (3. * ((length (qw) > 0.) ? atan (qw.y, - qw.x) /
       (2. * pi) : 0.) + 0.5) / 3.));
    if (abs (qw.y) > 2.) {
      b = 1. - smoothstep (-0.1, -0.01, rd.y) * smoothstep (0.4, 0.8, dCylOut / (1.6 * bCylRad));
      qh = HexGrid (ro.zx);
      f = max (length (qh.xy) - 0.5, 0.) * b;
      vn = vec3 (0., Rot2D (vec2 (1., 0.), 4. * f * f));
      vn.zx = vn.z * vec2 (qh.x, - qh.y) / length (qh.xy);
      vn = VaryNf (64. * ro, vn, 0.2 * b);
      col = vec3 (0.72, 0.75, 0.72) * (1. - 0.1 * b * Noisefv2 (128. * ro.xz)) *
         (1. - min (0.2 * b * (1. - smoothstep (0.03, 0.06, qh.z)), 0.1));
    } else {
      vn = vec3 (0., 1., 0.);
      col = vec3 (0.65, 0.65, 0.6) * (0.8 + 0.2 * SmoothBump (0.1, 0.9, 0.02, mod (qw.y, 1.)));
    }
    col *= 0.1 + 0.9 * smoothstep (4., 5., length (ro.xz));
    sh = ObjSShadow (ro, ltDir);
    col = col * (0.2 + 0.8 * sh * max (dot (vn, ltDir), 0.)) +
       0.1 * sh * pow (max (dot (normalize (ltDir - rd), vn), 0.), 64.);
  } else {
    ro += dCylOut * rd;
    f = 0.;
    qw = ro.xz / bCylRad;
    for (float n = float (VAR_ZERO); n < 128.; n ++) f += 1. - smoothstep (0.02, 0.025,
       length (qw - sqrt (n / 128.) * sin (n * ga + vec2 (0.5 * pi, 0.))));
    col = mix (vec3 (0.2, 0.2, 0.4), vec3 (1., 1., 0.7), f);
  }
  return col;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 vn, col;
  float dstObj, tCyc, r;
  gGap = 4.;
  tCyc = 4.;
  tPhs = mod (tCur, tCyc);
  wDisp = 2. * (gGap / tCyc) * tCur;
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    r = length (ro.xz);
    if (idObj == idBod) col4 = (ro.z < 0.) ? vec4 (0.7, 0.7, 0.8, 0.1) : 
       ((ro.x < 0.) ? vec4 (0.8, 0.2, 0.2, 0.1) : vec4 (0.2, 0.8, 0.2, 0.1));
    else if (idObj == idEye) col4 = (ro.z < 0.) ? vec4 (0.9, 0.9, 1., -1.) : 
       ((ro.x < 0.) ? vec4 (1., 0.3, 0.3, -1.) : vec4 (0.3, 1., 0.3, -1.));
    else if (idObj == idCen) col4 = vec4 (0.6, 0.6, 0.8, 0.1) * (0.9 + 0.1 *
       SmoothBump (0.1, 0.9, 0.02, mod (60. * atan (ro.z, - ro.x) / (2. * pi), 1.))) *
       (0.5 + 0.5 * smoothstep (3., 3.5, r));
    else if (idObj == idCenIn) col4 = vec4 (1., 1., 0.5, 0.) *
       step (0.45, abs (mod (2. * tCur / tCyc + 0.45, 1.) - 0.5));
    else if (idObj == idDoor) col4 = ((ro.z < 0.) ? vec4 (1., 1., 1., 0.1) : 
       ((ro.x < 0.) ? vec4 (1., 0., 0., 0.1) : vec4 (0., 1., 0., 0.1))) *
       (0.3 + 0.5 * smoothstep (2.9, 3., length (qHit.yz + vec2 (1., 0.))));
    else if (idObj == idCor) col4 = (floor (mod (2. * tCur / tCyc + 0.25, 2.)) == 0.) ?
       vec4 (1., 0.5, 0.5, 0.1) : vec4 (0.5, 1., 0.5, 0.1);
    else if (idObj == idCorLt) col4 = (floor (mod (2. * tCur / tCyc + 0.25, 2.)) == 0.) ?
       vec4 (1., 0., 0., -1.) * (0.7 + 0.3 * sin (8. * pi * tCur)) : vec4 (0., 1., 0., -1.);
    col = (col4.a >= 0.) ? col4.rgb * (0.2 + 0.8 * max (dot (vn, ltDir), 0.)) +
       col4.a * pow (max (dot (normalize (ltDir - rd), vn), 0.), 32.) : col4.rgb * (0.7 - 0.3 * dot (rd, vn));
    if (idObj == idBod) col += 0.2 * BgCol (ro, reflect (rd, vn));
    if (ro.y < 4.5 && idObj != idCenIn) col *= 0.1 + 0.9 * smoothstep (4., 5., r);
  } else col = BgCol (ro, rd);
  return clamp (col, 0., 1.);
}

