vec4 sdf_noisy_sphere(vec3 sphere_center, float sphere_radius, vec3 probe, vec3 color);

vec4 sdf_scene(vec3 probe)
{
    return opSmoothIntersection(sdf_sin_sphere(vec3(0.0, sin(0.7*iTime), -8.200), 1.0, probe, vec3(0.0, 1.0, .8)),
                                sdf_noisy_sphere(vec3(sin(iTime), 0.0, -8.200), 0.8000, probe, vec3(1.0, 1.0, 0.4)),
                                0.5);
}


// Fragment function
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = vec2((fragCoord.x - 0.5*iResolution.x)/iResolution.y , (fragCoord.y - 0.5*iResolution.y)/iResolution.y);

	float eps = 0.0015;
	vec3 light = vec3(-3.0+3.0*sin(iTime), 4.0+3.0*sin(1.3*iTime), 8.0);
	vec3 camera = vec3(0.0, 0.0, 4.0);
	vec3 pixel = vec3(uv, 0.0);
	vec3 ray = normalize(pixel-camera);
	vec3 probe = camera;
	vec4 dist = vec4(0.0, 0.0, 0.0, 2.0*eps);

    const float min_z = -20.0;
    const int max_iterations = 500;

    int final_its = max_iterations;

	for(int its=max_iterations; its>0; its-=1)
	{
	    final_its = its-1;

		dist = sdf_scene(probe);
		probe = probe + (dist.w * ray);
        if(dist.w <= eps || probe.z <= min_z)
        {
            break;
        }
	}

	if (dist.w <= eps)
	{
		vec3 normal = estimate_normal(probe);
		vec3 color = dist.rgb;
		fragColor = phong_shading(probe, normal, light, camera, color);
	}
	else if(final_its <= 0)
	{
		fragColor = vec4(1, 1, 0, 1);
	}
	else if(probe.z <= min_z)
	{
		fragColor = vec4(0, 0, 0.0, 1.0);
	}
	else
	{
		fragColor = vec4(1, 1, 1, 1);
	}
}



// 3D noise function (IQ)
float noise(vec3 p)
{
	vec3 ip=floor(p);
    p-=ip;
    vec3 s=vec3(7,157,113);
    vec4 h=vec4(0.,s.yz,s.y+s.z)+dot(ip,s);
    p=p*p*(3.-2.*p);
    h=mix(fract(sin(h)*43758.5),fract(sin(h+s.x)*43758.5),p.x);
    h.xy=mix(h.xz,h.yw,p.y);
    return mix(h.x,h.y,p.z);
}

float sin_displacement(vec3 p, float frequency)
{
	return sin(frequency*p.x)*sin(frequency*p.y)*sin(frequency*p.z);
}


// smooth intersection (IQ) with added color blend
vec4 opSmoothIntersection(vec4 d1, vec4 d2, float k)
{
    float h = clamp( 0.5 - 0.5*(d2.w-d1.w)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) + k*h*(1.0-h);
}


vec4 sdf_sin_sphere(vec3 sphere_center, float sphere_radius, vec3 probe, vec3 color)
{
	return vec4(color, distance(probe, sphere_center) - sphere_radius + 0.05*sin_displacement(probe, 20.0));
}

vec4 sdf_noisy_sphere(vec3 sphere_center, float sphere_radius, vec3 probe, vec3 color)
{
	vec3 nrml = normalize(probe-sphere_center);
	return vec4(color, distance(probe, sphere_center) - sphere_radius - 0.5*noise(2.6*(nrml+vec3(0.2*iTime, 0.2*iTime, iTime))));
}



vec3 estimate_normal(vec3 p) {
	float eps = 0.001;
    return normalize(vec3(
        sdf_scene(vec3(p.x + eps, p.y, p.z)).w - sdf_scene(vec3(p.x - eps, p.y, p.z)).w,
        sdf_scene(vec3(p.x, p.y + eps, p.z)).w - sdf_scene(vec3(p.x, p.y - eps, p.z)).w,
        sdf_scene(vec3(p.x, p.y, p.z + eps)).w - sdf_scene(vec3(p.x, p.y, p.z - eps)).w
    ));
}


// p: point on surface
// n: surface normal at p
// l: position of light source
// v: position of viewer
// c: diffuse color
vec4 phong_shading(vec3 p, vec3 n, vec3 l, vec3 v, vec3 c)
{
	vec3 light = normalize(l);
	vec3 r     = reflect(light, n);
	vec3 view  = normalize(v-p);
	float diffuse  = max(0.0, dot(light, n));
	float specular = max(0.0, dot(-r, view));
	vec3 color = diffuse * c + pow(specular, 40.0) * vec3(1, 1, 1);
	return vec4(color, 1);
}

