void mainImage(out vec4 O, in vec2 U)
{
    vec2 R = iResolution.xy;
    vec3 col = vec3(1), d = vec3((U - .5 * R) / min(R.x, R.y), .5);
    vec3 p = d.z / dot(vec3(0, -3, 4), -d) * d, n = p;
