#define tau 4.*acos(0.)
#define dB(_x) pow(10.,float(_x)/20.)
float intfract(int s,float n);

float rnd(float t)
{
    float x = t * 5623.123456;
    return fract(x * fract(t + x));
}

vec2 drums(float t)
{
    // kick
    float ft = fract(2. * t);
    float dr = .2 * sin(471. * ft * exp(-ft * 5.))
             + .6 * sin(691. * ft * exp(-ft * 2.));
    // avoid click quadratic envelope
    float a = min(sqrt(sqrt(1. - ft)), 25. * ft);
    dr *= (a*a*a*a); //pow(a, 4.);

    dr *= float(int[](1, 0, 0, 1, 1, 0, 0, 0)[int(2. * t) % 8]);

    // snare
    float s = rnd(t) +
              (.5 * sin(tau * t * 240.)
                         + sin(tau * t * 300.)) * exp(-ft * 3.);
    s *= float(int[](0, 0, 1, 0)[int(2. * t) % 4]);
    // attack decay
    s *= atan(1. - ft, 100. * ft);

    return .4 * vec2(s, .8 * s) + .5 * dr;
}

float eflute(float t)
{
    t = mod(t, 4.);
    float m0 = 0., m1 = 0.;
    float p[] = float[](554., 834., 834., 693.);

    float ap = p[int(t) % 4];

    m0 = abs(.125 - cos(.25 * sin(2. * ap * t * tau) + sin(ap * t * tau)));
    m0 *= min(1., 25. * fract(4. * t)) * max(0., 1. - fract(4. * t));

    m1 = .5 * (sin(ap * t * tau) + sin(2. * ap * t * tau));
    return m1 + m0;
}

float synth(float hz, int samp)
{
    float y = 0., w = 0., a = 0., b = 0., s0 = 0., s1 = 0.;

    s0 = sin(tau * intfract(samp, iSampleRate / hz));

    s1 = s0 * sin(tau * intfract(samp, iSampleRate / (2. * hz)));

    // playing around with one pole filter
    w = tau * clamp(.5 * s1 + sin(s0 * 12. ), -.1, .4);
    a = (1. - sin(w)) / cos(w);
    b = (1. + a) * .5;
    y = (b * s0 + a * s0);

    return y;
}

vec2 mSound(int samp)
{

    //drums and melody work w/ usual float precision err, it's ok.
    float t = (float(samp) / iSampleRate);

    // -------- MELODY
    float mld = eflute(t);
    vec2 rev=vec2(1);
    /*float sum=0.,amp=1.,ivR=0.008376;
    for(float _ii=0.;(_ii<=1.);_ii+=ivR){ //120
        float rvrnd=fract(120.*sin(_ii*1000.));
        amp=exp2(-_ii*((ivR+_ii)+rvrnd));
        rev+=(vec2(eflute(t+6.225*amp),eflute(t+3.5*amp)));
        sum+=amp;
    };rev/=sum;*/
    //song works with less delay on the instrument
    //updated on 12.05.2020 in favor of size&speed
    float[12]ha=float[](.99926,.95837,1.00841,.82124,.86099,1.04333,.71246,.53604,.98669,.43196,.44877,.74675);
    for(int j=0;j<2;++j)
    for(int i=0;i<12;++i)
    rev+=vec2(eflute(t+6.225*ha[i]),eflute(t+3.5*ha[i]));
    rev*=.042;
    // -------- DRUMS
    vec2 dr = drums(t);
    if(samp > 44100*45)
        dr += drums(.25 + t);

    // -------- SYNTH
    vec2 v = vec2(0);
    int nd = 0, nl = 0;

    int x[] = int[](0, 0, 0, 138, 0, 0,0, 78, 0, 0, 175, 0,0, 0, 0, 0, 174, 0,261, 279, 0, 0, 554,0, 0, 0, 313, 69, 0,0, 349, 0, 0, 0, 234, 0, 415, 0, 0, 207, 0,0, 0, 0, 0, 0, 558);
    x[0] = int[](349, 311)[int(t*.125) % 2];
    x[12] = int[](92, 116, 138, 58)[int(t*.25) % 4];
    x[45] = 2*x[12];

    // delay time
    int dt[]= int[](2971, 823, 607, 523, 457);

    for (int i = 1; i <=x.length(); ++i)
    {
        if(samp > 44100*40)
            nd =  abs(int(t*4.)%8-i/10);

        nl = (i-nd)%x.length();

        v.x += (synth(float(x[nl]), (dt[i%4]*2 ) + samp));
        v.y += (synth(float(x[nl]), (dt[i%3]/2 ) + samp));

    };
    v*= (1./float(x.length()));

    // fade in
    v *= min(1., t/20.);

    // stereo image l/r pan
    const float angle = .436332;
    const float sqh2 =  .707106;
    vec2 r = vec2(cos(angle) + sin(angle), cos(angle) - sin(angle)) * v.x * sqh2;
    vec2 l = vec2(cos(angle) - sin(angle), cos(angle) + sin(angle)) * v.y * sqh2;

    // -------- out
    if (samp > 44100 * 60)
    return dB(-5) * vec2(r.x + l.x, l.y + r.y)
         + dB(-5.) * dr
         + dB(-12) * (dB(-5) * rev + dB(-8) * mld);

    return dB(-4.6) * vec2(r.x + l.x, l.y + r.y)
         + dB(-3) * dr;
}

float intfract(int s,float n)
{
    //Explanation and copyright : shadertoy.com/view/4ltfRN
    int sn=int(n),a=s%sn,b=(s/sn);
    const float MAXF=float(uint(-1))+1.;
    return fract((float(a)/n)+float((uint(b)*uint(MAXF*float(sn)/n)))/MAXF);
}

vec2 mainSound( in int samp,float time)
{
    // see ttg comment 2020-12-10
    //int block = int((iBlockOffset+0.5)*iSampleRate/(512.*512.));
    //int s = 512*512*block + 512*int(gl_FragCoord.y) + int(gl_FragCoord.x);
    return mSound(samp);
}

