void drawRect(in vec2 pos, in vec4 color, in vec2 size)
{
    if(in_coord.x >= pos.x && (in_coord.x) <= pos.x + size.x && in_coord.y >= pos.y && (in_coord.y) < pos.y + size.y)
        out_color = color;
}

vec2 multiplyComplex(vec2 a, vec2 b)
{
	return vec2(a.x * b.x - a.y * b.y, a.x * b.y + a.y * b.x);
}

vec4 pipeColor(float value)
{
	//0.25,0.50,0.75,1.0f
	vec4 ret = COLOR_BLACK;

    ret = vec4(value);

    ret.y=0.0f;
    ret.z=0.0f;

    return ret;

    if(value > 0.75f)
    {
        value -= 0.75f;

      	ret = vec4(1.0f, 1.0f, value, 1.0f);
    }else if(value > 0.5f)
    {
     	value -= 0.5f;

        ret = vec4(1.0f, value, 0.0f, 1.0f);
    }else if(value > 0.25f)
    {
     	value -= 0.25f;

        ret = vec4(value, 0.0f, 0.0f, 1.0f);
    }

    //ret=vec4(1.0f)-ret;
    return ret;
}

float convertWindowToLinearCoord(float x, float y, float norm)
{
    float length = distance(x,y);

    return norm * length + x;
}

float invert(float t)
{
 	return 1.0f/t;   
}

float lockdown(float x, float c)
{
    return -5.0f * pow(0.5,x) + c;
}

float slowdown(float x)
{
 	return 16.0 + 2.0f * log(x);
}

void drawMandelBrot()
{
	vec2 norm_coord = (in_coord.xy/WH - vec2(0.5)) * vec2(2.0f);

    float scalingFactor = 0.05f;

    vec4 vp = vec4(-2,1,-1,1);
    vec2 point = vec2(-0.75,-0.1);

    point=vec2(-0.761574,-0.0847596);
    point=vec2(0.42884,-0.231345);

    vec4 vpnorm = vec4(distance(point.x,vp.x), distance(point.x,vp.y),
                       distance(point.y,vp.z),distance(point.y,vp.w));

    vec4 ratios = vpnorm;

    //debugging
    float fakeTime = 20.0f;
    fakeTime = slowdown(iTime);

    ratios = ratios * (scalingFactor * fakeTime);    

    vp = vec4(vp.x + ratios.x, vp.y - ratios.y, vp.z + ratios.z, vp.w - ratios.w);

    if(vp.x > point.x)
        vp.x=lockdown(vp.x, point.x);

    if(vp.y < point.x)
        vp.y=lockdown(vp.y, point.x);

    if(vp.z > point.y)
        vp.z=lockdown(vp.z, point.y);

    if(vp.w < point.y)
        vp.w=lockdown(vp.w, point.y);

    norm_coord.x = convertWindowToLinearCoord(vp.x, vp.y, in_coord.x/W);
    norm_coord.y = convertWindowToLinearCoord(vp.z, vp.w, in_coord.y/H);

    float scalar = 3.0f;

    vec2 value = vec2(0);
    int index=0;
    for(int i=0;i<iter_c;++i)
    {
        value = multiplyComplex(value,value) + norm_coord;

        if(length(value) > 2.0f)
        {
            out_color = COLOR_WHITE * pipeColor(float(i)/float(iter_c));
            return;
        }
        index=i;
    }
    out_color = COLOR_BLACK;
}

void drawMain()
{
	drawMandelBrot();
}

