float pi = acos(-1.);
float glo = 0.;

mat3 rotateY(float rad)
{
    float c = cos(rad);
    float s = sin(rad);
    return mat3(
        c, 0., s,
        0., 1., 0.,
        -s, 0., c
    );
}

mat3 rotateZ(float rad)
{
    float c = cos(rad);
    float s = sin(rad);
    return mat3(
        c, s, 0.,
        -s, c, 0.,
        0., 0., 1.
    );
}

float sphere(vec3 pos, float radius)
{
    return length(pos) - radius;
}

float map(vec3 p)
{   
   	p = rotateZ(.15 * iTime) * rotateY(.17 * iTime) * p;
    p += -.4 * vec3(cos(abs(p.x)*15. + 1.3*iTime),
                    sin(p.y*15. + iTime),
                    cos(abs(p.z)*15. + 1.5*iTime) * sin(p.x*p.y*12. + 1.7*iTime) * 2.2);
    float m = sphere(p, .4*sin(.43*iTime) + .6);
    glo += .18 / (.18 + m*m);
    return m;
}

float tr(vec3 ro, vec3 rd)
{
	float  h,t = .1;
	for(int i = 0; i < 256; i++)
	{
		h = map(ro + rd*t) * .1;
		if(h < .001 || t > 120.)
			break;
		t += h;
  	}
	return t;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 ro = vec3(0., 0., -2.5);
	vec3 camTarget = vec3(0.);
	vec3 up = vec3(0., 1., 0.);
	vec3 camDir = normalize(camTarget - ro);
	vec3 camRight = normalize(cross(up, ro));
	vec3 camUp = normalize(cross(camDir, camRight));
    vec3 lightPos = vec3(-2., -2., -15.);

	vec2 screenPos = -1. + 2. * gl_FragCoord.xy / iResolution.xy;
	screenPos.x *= iResolution.x / iResolution.y;

	vec2 eps = vec2(0., .05);
	vec3 rd = normalize(camRight*screenPos.x + camUp*screenPos.y + camDir);

	float t = tr(ro, rd);

    vec3 colRot = sin(vec3(.151, .227, .317) * iTime) + 1.2;

    if (t < 120.)
    {
        vec3 hit = ro + rd*t;
        vec3 lightDir = normalize(lightPos - hit);

        vec3 norm = normalize(map(hit) - vec3(map(hit - eps.yxx),
                              map(hit - eps.xyx),
                              map(hit - eps.xxy)));


        float diff = max(.2, dot(lightDir, norm));
        float spec = pow(max(dot(rd, reflect(norm, lightDir)), 0.), 50.);
        float ao = clamp(map(t + norm*.5) / .5, 0., 1.);

        vec3 col =  .2*colRot * .2*ao;
        col += .3 * diff * colRot.yzx;
        col += .5 * spec * vec3(1., 1., 1.);

        col += glo*.0105 * colRot.yxz;
        fragColor = vec4(col, 1.);        
    }
    else
    	fragColor = vec4(glo*.0105*colRot.yzx, 1.);
}

