float gauss(float x, float r)
{
    x/=r;
    return exp(-x*x);
}

float sdSegment( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

vec4 grad(ivec2 p)
{
    ivec4 sp = get(p);
    ivec4 spx = get(p+ivec2(1,0));
    ivec4 spy = get(p+ivec2(0,1));
    return tanh(100.*(abs(vec4(spx - sp)) + abs(vec4(spy - sp))));
}

ivec2 getve(ivec2 p)
{
    return floatBitsToInt(texel(ch2, p).xy);
}

float edge_distance(vec2 spos, vec2 pos)
{
    ivec2 p = ivec2(spos);
    ivec4 sp = get(p);

    vec4 p0 = getParticle(sp.x);
    vec4 p1 = getParticle(sp.y);

    ivec2 ve = getve(p);//edge side particles
    vec4 pe0 = getParticle(ve.x);
    vec4 pe1 = getParticle(ve.y);

    vec2 edge0 = circumcenter(p0.xy, p1.xy, pe0.xy);
    vec2 edge1 = circumcenter(p0.xy, p1.xy, pe1.xy);

    return (!any(equal(ivec2(-1), ve)))?sdSegment(pos, edge0, edge1):1e10;
}

