// radial blur and chromatic abberation in this buffer
// thx iq for pallette, mercury for hg-sdf, balkhan for glow


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord/iResolution.xy;
	vec2 uvn = (fragCoord - 0.5*iResolution.xy)/iResolution.xy;


    //float m = pow(abs(sin(p.z*0.03)),10.);

    // Radial blur
    float steps = 20.;
    float scale = 0.00 + pow(length(uv - 0.5),3.4)*0.1;
    //float chromAb = smoothstep(0.,1.,pow(length(uv - 0.5), 0.3))*1.1;
    float chromAb = pow(length(uv - 0.5),1.)*1.1;
    vec2 offs = vec2(0);
    vec4 radial = vec4(0);
    for(float i = 0.; i < steps; i++){

        scale *= 0.98;
        vec2 target = uv + offs;
        offs -= normalize(uvn)*scale/steps;
    	radial.r += texture(iChannel0, target + chromAb*1./iResolution.xy).x;
    	radial.g += texture(iChannel0, target).y;
    	radial.b += texture(iChannel0, target - chromAb*1./iResolution.xy).z;
    }
    radial /= steps;

    fragColor = radial*5.5; 

    fragColor *= 40.;


    fragColor = mix(fragColor,smoothstep(0.,1.,fragColor), 0.34);
    //fragColor *= 1.;
    fragColor = max(fragColor, 0.);

    fragColor *= 1.7;
    fragColor.g *= 1.1;
    fragColor.r *= 0.95 + uvn.x*0.7;
    fragColor.g *= 0.95 + uvn.y*0.3;
    fragColor = pow(fragColor, vec4(0.6545 + dot(uvn,uvn)*1.6));

    //fragColor *= 1. - dot(uvn,uvn)*0.7;
}

