vec3 getFishEye(vec2 uv, float level) {

    float len = length(uv);
    float a = len * level;
    vec3 fishUV = vec3(uv / len * sin(a), cos(a));

    vec3 camForward = 		loadValue(CAM_FORWARD).xyz;
    vec3 camRight = normalize(cross(camForward, vec3(0.0, 1.0, 0.0))); 
    vec3 camUp = normalize(cross(camRight, camForward));

    return (fishUV.x * camRight) + (fishUV.y * camUp) + (fishUV.z * camForward);
}


