   }


    // Volumetric lights 

    const float VOL_STEPS = 16.;

    const float VOL_STEP_SIZE = 2. * VOLUME_SCALE/ VOL_STEPS;

    const float FOG_DENSITY = .1/VOL_STEPS;

    vec4 ambientLight = vec4(0., 0., 0., FOG_DENSITY);

    float volDist = VOL_STEP_SIZE;

    for (float i=0.; i<VOL_STEPS; i+=1.)
    {
