        float addStep = VOL_STEP_SIZE * (1. + i/VOL_STEPS);

        float smoothAdd = smoothstep(dist, dist-addStep, volDist);

        float adding = FOG_DENSITY * smoothAdd;

        ambientLight.rgb += volCol * adding / ambientLight.a;

        ambientLight.a += adding;

        volDist += VOL_STEP_SIZE * (1. + i/VOL_STEPS); 

        i+= (1. - smoothAdd) * 100.;
    }

