// CONFIGS

#define MAX_STEPS 30
#define MAX_DIST 500.
#define SURF_DIST .001
//#define DEBUG
#define MARCH_VOLUME_SHADOW
//___ MOVEMENT
const float PLAYER_SPEED = .6;
const float PLAYER_RUN_SPEED = 1.;
const float MOUSE_SENSITIVITY = 3.;
const float PLAYER_SIZE = .1;
const float SLOWDOWN_RANGE = 5.;

const vec4 SCENE_OFFSET = vec4(0.,0.,20.,0.);

//__ RENDERING
const float  SHADOW_RADIUS = 6.;

vec3 skyColor= vec3(.1,.1, .7);
vec3 eqColor = vec3(.5,.4,.4);
vec3 floorColor = vec3(.1,.3,.1);

vec3 SkyBox(vec3 rayDirection)
{
    return  floorColor * max(0., -rayDirection.y)
        +   eqColor * (1.-abs(rayDirection.y)) 
        +	skyColor * (max(0., rayDirection.y));
}


// *************** PERSISTANCE

const ivec2 CAM_POS_PRSST       	= ivec2(1, 1);
const ivec2 CAM_ROT_PRSST       	= ivec2(3, 1);
const ivec2 CAM_MOUSE_PREV_PRSST    = ivec2(7, 1);
const ivec2 CAM_ROTATION_PRSST      = ivec2(9, 1);
const ivec2 CAM_FORWARD       		= ivec2(11, 1);
const ivec2 GAME_STATE       		= ivec2(13, 1);

const float PRSST_MAX_Y = 2.;



// ************** MATH

float sharpstep(float a, float b, float x){
    return (x-a)/(b-a);
}

vec2 Rot(vec2 uv, float angle){
	float si = sin(angle);
	float co = cos(angle);
    return vec2(co * uv.x - si* uv.y, si * uv.x + co * uv.y);
}

// ************** VOLUME

const float VOLUME_SCALE = 10.;
const vec4 VOLUME_POSITION_N_SIZE = vec4(0, -40., 0, 1./VOLUME_SCALE) + SCENE_OFFSET; // w = 1/size
const float VOLUME_HEIGHT = 16.;
const float DE_VOL_HEIGHT = 1./VOLUME_HEIGHT;


vec4 GetVolumeHSlices(float resolutionY)
{
  float sliceWidth = (resolutionY - VOLUME_HEIGHT * 2.) * DE_VOL_HEIGHT;       
  return vec4(VOLUME_HEIGHT, sliceWidth* .5, 1./sliceWidth, DE_VOL_HEIGHT);
}

vec4 SampleVolume(vec3 worldPos, vec4 VOLUME_H_SLICES, out float hmix) {

    vec4 outputUV = vec4(0.);

	vec3 bsPos = (worldPos.xyz - VOLUME_POSITION_N_SIZE.xyz) * VOLUME_POSITION_N_SIZE.w;

	bsPos.xz = clamp((bsPos.xz + VOLUME_H_SLICES.y)* VOLUME_H_SLICES.z, 0., 1.)*VOLUME_H_SLICES.w;
	float h = min(max(0., bsPos.y), VOLUME_H_SLICES.x*VOLUME_H_SLICES.x - 1.);

	float sectorY = floor(h * VOLUME_H_SLICES.w);
	float sectorX = floor(h - sectorY * VOLUME_H_SLICES.x);

    vec2 uv_low = vec2(sectorX, sectorY) * VOLUME_H_SLICES.w;

	vec2 sector = clamp(uv_low, 0., 1.)+ bsPos.xz;

    outputUV.xy = sector;

	h += 1.;

	sectorY = floor(h * VOLUME_H_SLICES.w);
	sectorX = floor(h - sectorY * VOLUME_H_SLICES.x);

	sector = clamp(vec2(sectorX, sectorY)*VOLUME_H_SLICES.w, 0., 1.) + bsPos.xz;

    outputUV.zw = sector;

	hmix = fract(h); 

	return outputUV;
}

// **************  RAYMARCHING

float sdCross(vec3 p) {
	p = abs(p);
	vec3 d = vec3(max(p.x, p.y),
				  max(p.y, p.z),
				  max(p.z, p.x));
	return min(d.x, min(d.y, d.z)) - (1.0 / 3.0);
}

float sdCrossRep(vec3 p) {
	vec3 q = mod(p + 1.0, 2.0) - 1.0;
	return sdCross(q);
}

float sdCrossRepScale(vec3 p, float s) {
	return sdCrossRep(p * s) / s;	
}


float smAdd(float d1, float d2, float smth)
{
	float h = max(smth - abs(d1 - d2), 0.0) / (smth + 0.0001);
	return min(d1, d2) - h * h*h*smth*(1.0 / 6.0);
}

float smMinus(float d1, float d2, float k) {

	float h = clamp((1. - (d2 + d1) / (k + 0.0001))*0.5, 0.,1.);

	return mix(d1, -d2, h) + k * h * (1. - h);
}

float Minus(float d1, float d2){
	return max(d1, -d2);
}

// ****************** SHAPES

float sdSphere(vec4 sphere, vec3 pos) {
	return length(pos-sphere.xyz)-sphere.w; 
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

float sdGyroid(vec3 p, float scale, float thickness, float bias) {
	p *= scale;
    return abs(dot(sin(p), cos(p.zxy))+bias)/scale - thickness;
}

float distToBox(in vec2 pos, in vec2 boxMin, in vec2 boxMax, in float expand)
{
    boxMin -= vec2(expand,expand);
    boxMax += vec2(expand,expand);
    float bMax = max(pos.x - boxMax.x, pos.y - boxMax.y);
    float bMin = max(boxMin.x - pos.x, boxMin.y - pos.y);
    return max(bMax, bMin);
}


// ****************** RAY CASTING

float planeLineIntersect(vec3 o,vec3 d,vec3 pn,vec3 pp)
{
    return dot(pp-o,pn)/dot(d,pn);
}

// Box:             https://www.shadertoy.com/view/ld23DV
void iBox( in vec3 ro, in vec3 m, in vec3 boxSize, inout vec2 nearestCast) 
{ 

    vec3 n = m*ro;
    vec3 k = abs(m)*boxSize;

    vec3 t1 = -n - k;
    vec3 t2 = -n + k;

	float tN = max( max( t1.x, t1.y ), t1.z );
	float tF = min( min( t2.x, t2.y ), t2.z );

    float isNear = step(tN, tF) * step(0., tF) * step(tN, nearestCast.x);

    nearestCast = mix(nearestCast, vec2(tN, tF), isNear);
}

void IntersectSphere(vec3 rayOrigin, vec3 rayDirection, vec3 pos, float radius, inout vec2 nearestCast)
{
    float t = max(0.,dot(pos - rayOrigin, rayDirection));
    vec3 p = rayOrigin + rayDirection * t;
    float y = length(pos - p);

    float hit = step(y, radius);

    float x = sqrt(mix(1., radius * radius - y * y,  hit));

    float isNearest = hit * step(t-x, nearestCast.x);

    nearestCast = mix(nearestCast, vec2(t-x,t+x), isNearest);
}


// ***************** TEST SCENE


const vec4 lightSource_0 = vec4(30., 50., 60., 20.) + SCENE_OFFSET;

const vec3 lightSource_0_Color = vec3(.8, .8, .1);

const vec4 Sphere_0 = vec4(1., 7., 20., 15) + SCENE_OFFSET;
const vec4 Sphere_1 = vec4(55., -40., 30., 30) + SCENE_OFFSET;

const vec4 MinusSphere_0 = vec4(55., -30., 30., 55) + SCENE_OFFSET;
const vec4 Box_0 = 			vec4(55., -40., 30., 50) + SCENE_OFFSET;

const vec4 Box_1 = 			vec4(-50., 64., 0., 50) + SCENE_OFFSET;

vec2 GetRaycastDist(vec3 rayPos, vec3 rayDir)
{
    vec2 nearestCast = vec2(MAX_DIST+1.,MAX_DIST+1.);

    IntersectSphere(rayPos, rayDir, Sphere_0.xyz , Sphere_0.w, nearestCast);

    vec3 precalculateBox = sign(rayDir)/max(abs(rayDir), 1e-8);

    iBox(rayPos - Box_0.xyz, precalculateBox, vec3(Box_0.w), nearestCast);
 	iBox(rayPos - Box_1.xyz, precalculateBox, vec3(Box_1.w), nearestCast);

    return nearestCast;
}


float GetDist(vec3 p) {


    float minDist = sdBox(p - Box_0.xyz, vec3(Box_0.w));

    minDist = min(minDist, sdBox(p - Box_1.xyz, vec3(Box_1.w)));

    minDist = Minus(minDist, sdSphere(MinusSphere_0,p));

    const int MENGER_ITERATIONS = 2;
    float scale = .1;
 	for (int i = 0; i < MENGER_ITERATIONS; i++) {
		minDist = max(minDist, -sdCrossRepScale(p, scale));
		scale *= 3.0;
	}

     minDist = smAdd(minDist, sdSphere(Sphere_0, p), 6.);

     minDist = min(minDist, sdSphere(Sphere_1, p));

    return minDist;
}

vec3 GetNormal(vec3 p, float dist ) {
	float d = GetDist(p);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}


float Shadow(vec3 p, vec3 pos, float radius, float lightPower)
{
	vec3 rayDir = normalize(pos - p);
	float maxDist = length(p - pos);

	// fraction of light visible, starts at one radius (second half added in the end);
	float lf = radius * maxDist;

    float minStep = radius * 0.15;

	// distance traveled
	float dt =minStep;

    float sd = 0.;

    float fullShadow = 0.;

	for (float i = 0.; i < 32.; i+=1.)
	{				
		sd = GetDist(p + rayDir * dt);

		lf = min(lf, sd / dt);

		// move ahead
		dt += max(minStep, abs(sd));

        fullShadow = step(sd, -radius);

        i+= (fullShadow + step(maxDist,dt))* 100.;
	}

	lf = clamp((lf*maxDist + radius) / (2.0 * radius), 0.0, 1.0);
	lf = smoothstep(0.0, 1.0, lf);

	return lf * lightPower / maxDist ;
}



void GetRayMarchDist(vec3 rayPos, vec3 rayDir, float maxDist, inout float dist, out float steps){

    steps = 0.;

    for(int i=0; i<MAX_STEPS; i++) {
        vec3 p = rayPos + rayDir*dist;
        float dS = GetDist(p);
        dist += dS;
        #ifdef DEBUG
        steps += 1.; // DEBUG
        #endif
        if(dist>maxDist || abs(dS)<SURF_DIST*(1.+dist)
          )
        { 
            break;
        }
    }

}

