////////////////////////////////////////////////////////////////////////////////
//
// Clebsch & other Cubics, raymarched using the implicit equation as SDF,
// adjusted by the analytic gradient.
//
// Matthew Arcus, 2020.
//
// <mouse>: rotate
// <up>/<down>: move in/out
// <left>/<right>: fog distance
// <page up>/<page down>: select Clebsch, Caley or morphing Cubic.
// f: fog
// l: light transform
// r: autorotation
//
// A projective transform as in https://www.shadertoy.com/view/llKfDc would
// be nice, but doesn't interact nicely with the SDF approach.
//
////////////////////////////////////////////////////////////////////////////////

const float PI = 3.14159;
const int maxsteps = 100;
const float precis = 0.001;
const float AA = 2.0;

float maxdist = 10.0;
bool dorotate = true; // Autorotate

bool check = false;
void assert(bool b) {
  if (!b) check = true;
}

float K = 1.0;
// Cubic implicit equation, adjusted for gradient.
float getdist(vec3 p) {
   // x^3+y^3+z^3-K*w^3 = t^3 where w = 1, t = x+y+z+w
   float t = dot(p,vec3(1))+1.0;
   float ds = 2.0*length(p*p-t*t); // length of gradient
  return (dot(p*p,p)+K-t*t*t)/ds;
}

// Gradient as a separate function.
vec3 cgrad(vec3 p) {
  float t = dot(p,vec3(1))+1.0;
  return 2.0*(p*p-t*t);
}

float rosign;   // sign of implict equation at ray origin
float march(vec3 ro, vec3 rd) {
  float t = 0.1;
  for(int i = 0; i < maxsteps; i++) {
    //assert(i < 50);
    vec3 p = ro + t*rd;
    float d = rosign*getdist(p);
    // Longer distance estimates are inaccurate,
    // so clamp to avoid overstepping.
    d = min(d,0.5);
    t += d;
    if (t < 0.0 || t > maxdist || abs(d) < t*precis) break;
  }
  return t;
}

vec2 rotate(in vec2 p, in float t) {
  return p * cos(-t) + vec2(p.y, -p.x) * sin(-t);
}

vec3 transform(in vec3 p) {
  if (iMouse.x > 0.0) {
    float phi = (2.0*iMouse.x-iResolution.x)/iResolution.x*PI;
    float theta = (2.0*iMouse.y-iResolution.y)/iResolution.y*PI;
    p.yz = rotate(p.yz,-theta);
    p.zx = rotate(p.zx,phi);
  }
  if (dorotate) {
    p.yz = rotate(p.yz,iTime * 0.125);
    p.zx = rotate(p.zx,iTime * 0.1);
  }
  return p;
}

vec3 hsv2rgb(float h, float s, float v) {
  vec3 rgb = clamp( abs(mod(h*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
  rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing	
  return v * mix( vec3(1.0), rgb, s);
}

int mymod(int n, int m) {
  // glsl % undefined for -ve arguments
  if (n < 0) return m-1-(-n-1)%m;
  else return n%m;
}

