float beval(float a, float A, float B, float z, float w) {
  return -0.5*(2.0*a*a*z + A + B)/(w*w - a*a);
}

float feval(float a, float A, float B, float z, float w) {
  float b = beval(a,A,B,z,w);
  float t = 2.0*(b*b*(-a + w) + a*z*z) + A - B;
