vec2 lineparams(float z, float w) {
  float A = (K*8.0 + z*z*z + w*w*w - 1.0)/3.0;
  float B =  z*z*w-z*w*w;
  float eps = 1e-2;
  float x0 = abs(w) + eps, f0 = feval(x0,A,B,z,w);
  float x1 = x0 + eps, f1 = feval(x1,A,B,z,w);
