  // Secant method doesn't help much here.
  for (int i = 0; i < 10; i++) {
    float x2 = (x0+x1)/2.0;
    float f2 = feval(x2,A,B,z,w);
    if (f2 < 0.0) {
      x0 = x2; f0 = f2;
    } else {
      x1 = x2; f1 = f2;
    }
  }
  float a = (x0+x1)/2.0;
  return vec2(a,beval(a,A,B,z,w));
