void mainImage(out vec4 fragColor, vec2 fragCoord) {
  dorotate = !key(CHAR_R);
  maxdist = 10.0*(exp(0.1*float(keycount(KEY_RIGHT)-keycount(KEY_LEFT))));
  vec3 ro = vec3(0,0,4);
  ro.z += 0.25*float(keycount(KEY_DOWN)-keycount(KEY_UP));
  ro = transform(ro);
  vec3 light = vec3(1,2,3);
  // Leaving the light fixed (untransformed) produces more
  // dramatic lighting.
  if (key(CHAR_L)) light = transform(light);
  light = normalize(light);
  rosign = sign(getdist(ro));
  vec3 aacol = vec3(0);
  int type = mymod(keycount(KEY_PAGE_DOWN)-keycount(KEY_PAGE_UP),3);
  if (type == 0) K = 1.0;
  else  if (type == 1) K = 0.25;
  else if (type == 2) K = 1.0-sin(0.2*iTime);
  for (float i = 0.0; i < AA; i++) {
    for (float j = 0.0; j < AA; j++) {
      vec2 uv = (2.0*(fragCoord+vec2(i,j)/AA)-iResolution.xy)/iResolution.y;
      vec3 rd = vec3(uv,-2);
      rd = transform(rd);
      rd = normalize(rd);
      float t = march(ro, rd);
      vec3 basecol = hsv2rgb(0.1+0.01*iTime,1.0,1.0); //vec3(1,0.5,0);
      vec3 col = mix(vec3(1),basecol,0.75);
      if (t >= 0.0 && t < maxdist) {
        vec3 p = ro+t*rd;
        vec3 n = normalize(cgrad(p));
        if (dot(n,rd) > 0.0) n = -n;
        float diffuse = mix(1.0,dot(n,light),0.5);
        float d = linedist(p);
        float intensity = 0.5+0.5*smoothstep(0.006,0.008,d);
        vec3 c = diffuse*intensity*basecol;
        float fog = key(CHAR_F) ? 0.0 : t/maxdist;
        col = mix(c,col,fog);
      }
      aacol += col;
    }
  }
  aacol = pow(aacol/(AA*AA),vec3(0.4545));	// gamma correction
  //assert(!isnan(dot(aacol,aacol)));
  fragColor = vec4(aacol,1);
  if (check) fragColor.r = 1.0;
}

