//=======================================================
//= Screen Space Specular Antialiasing
//=======================================================
//== Ressources :
//== *- 2016 - Filtering Distributions of Normals for Shading Antialiasing
//== 	by A. Kaplanyan, S. Hill, A. Patney, A. Lefohn (HPG 2016)
//== project page : https://research.nvidia.com/publication/filtering-distributions-normals-shading-antialiasing
//== demo : https://blog.selfshadow.com/sandbox/specaa.html
//==
//== *- 2017 - Error Reduction and Simplification for Shading Anti-Aliasing
//== 	by Y. Tokuyoshi (technical report)
//== project page : http://www.jp.square-enix.com/tech/publications.html
//==
//== *- Improving Geometric Specular Antialiasing
//== 	by A. Kaplanyan & Y. Tokuyoshi
//=======================================================
//== I also used some code from :
//== *- @XT95's "UI easy to integrate" on Shadertoy 	  
//=   	https://www.shadertoy.com/view/ldKSDm				
//== *- @nimitz's "Cheap orthonormal basis" on Shadertoy
//==	https://www.shadertoy.com/view/4sSSW3
//== *- @iq's distance functions ( Sphere + SmoothUnionCubic )
//== 	https://iquilezles.org/articles/distfunctions
//=======================================================

const bool SLIDE_ANIMATION = true;

//-----------------------------------------------------------------------------
//-- Constantes ---------------------------------------------------------------
const float EPSILON             = m_eps_3f;
const float SAMPLES_WORLD       = 32.;
const float SAMPLES_SHADOWS 	= 32.;
const vec3  WORLD_BOUNDING_BOX  = vec3(2.);
const float WORLD_LIGHT_INTENSITY = 5.;
	  vec3  WORLD_LIGHT_DIRECTION = normalize(vec3(-0.5,1.5,0.1));
const vec3  WORLD_BACKGROUND = vec3(0.0);

//-----------------------------------------------------------------------------
//-- Specular AA --------------------------------------------------------------
//-- Snippet code for specular antialiasing
//-- Based on A. Kaplanyan & Y. Tokuyoshi work
//-----------------------------------------------------------------------------
void specular_anti_aliasing(in vec3 half_vector, 
                           	inout float alpha_x,
                            inout float alpha_y)
{

    float sigma = 0.50; 							//- screen space variance
    float Kappa = 0.18;								//- clamping treshold
    vec2  H = half_vector.xy;						
    vec2  footprint_bounding_box = fwidth(H); 		//- abs(dfdx(slope_h)) + abs(dfdy(slope_h))
	vec2  variance = sigma*sigma*footprint_bounding_box*footprint_bounding_box;
    vec2  kernel_roughness = min(vec2(Kappa), 2.*variance); 
    alpha_x = sqrt(alpha_x*alpha_x + kernel_roughness.x);
    alpha_y = sqrt(alpha_y*alpha_y + kernel_roughness.y);
}

//-----------------------------------------------------------------------------
//-- UI -----------------------------------------------------------------------
//-- From XT95's https://www.shadertoy.com/view/ldKSDm
//-----------------------------------------------------------------------------
float 	uiSlider(int id) {return texture(iChannel0, vec2(float(id)+.5,0.5)/iResolution.xy).r;}
vec3 	uiColor(int id)	 {return texture(iChannel0, vec2(float(id)+.5,1.5)/iResolution.xy).rgb;}

//-----------------------------------------------------------------------------
//-- Scene SDF : vec2(SDF,MATID) ----------------------------------------------
vec2 MAP(vec3 P)
{       
    float SDF = 1.;
    SDF = opSUnn(SDF, sdSphere( P - vec3(+0.5,0.,0.), 0.8 ));    
	SDF = opSUnn(SDF, sdSphere( P - vec3(-0.5,0.,0.), 0.8 ));    
    SDF = opSUnn(SDF, sdSphere( P - vec3(0.,0.,+0.5), 0.8 )); 
    SDF = opSUnn(SDF, sdSphere( P - vec3(0.,0.,-0.5), 0.8 )); 
	return vec2(SDF,1.);
}

//-----------------------------------------------------------------------------
//-- Raymarching code ---------------------------------------------------------
float 	world_get_light_intensity()            { return WORLD_LIGHT_INTENSITY; }
vec3 	world_get_light_direction()            { return WORLD_LIGHT_DIRECTION; }
vec3 	world_get_background_color(in vec3 rd) { return WORLD_BACKGROUND; }

vec2 world_intersect(in vec3 ro, in vec3 rd, in float tmax)
{
    float t = 0.01;
    vec2  R = vec2(-1.);
    for(float istep=0.; istep<SAMPLES_WORLD && t<tmax; istep+=1.)
    {
        vec2 H = MAP(ro + t * rd);
        if( H.x < EPSILON )
        {
            R = vec2(t,H.y);
            break;
        }
        t += H.x;
    }
    return R;
}

float world_get_shadow(in vec3 ro, in vec3 rd, in float tmax)
{
    float t = 0.01;
    float r = 0.;
    for(int istep=0; istep<int(SAMPLES_SHADOWS) && t<tmax; istep++)
    {
        vec2 H = MAP(ro + t * rd);
        if( H.x < EPSILON) {break;}
        t += H.x;
    }
    if(t>tmax) r = 1.;
    return r;
}


vec3 world_get_normal( in vec3 pos )
{
    vec2 e = vec2(1.0,-1.0)*0.5773*0.0005;
    return normalize( e.xyy*MAP( pos + e.xyy ).x + 
					  e.yyx*MAP( pos + e.yyx ).x + 
					  e.yxy*MAP( pos + e.yxy ).x + 
					  e.xxx*MAP( pos + e.xxx ).x );  
}

vec3 raymarch(vec3 ro, vec3 rd, vec2 screen, float slide)
{ 
    vec2  hitinfo = world_intersect(ro, rd, 100.);
    float hit_dis = hitinfo.x;
    float hit_obj = hitinfo.y;

    if(hit_dis<0.) 
    {
    	return world_get_background_color(rd);
    }

    vec3 hit_pos = ro+rd*hit_dis;
    vec3 hit_nor = world_get_normal(hit_pos);

    vec3    P = hit_pos;
    vec3    L = world_get_light_direction();
    vec3    N = hit_nor;
    vec3    V = -rd;

    mat3 TBN    = orthonormal_basis(N);
    mat3 TBN_t  = transpose(TBN);
    vec3 wo = normalize(TBN_t * V);
    vec3 wi = normalize(TBN_t * L);
    vec3 wg = normalize(TBN_t * N); // ( should be (0,0,1)^t )
    vec3 wh = normalize(wo+wi);		// ( could check it not zero)

    float wi_dot_wh = clamp(dot(wi,wh),0.,1.); 		// saturate(dot(L,H))
    float wg_dot_wi = clamp(cos_theta(wi),0.,1.); 	// saturate(dot(N,L))

    float roughness_x 	= clamp( 0.01 + uiSlider(0), 0., 0.8);
    float roughness_y 	= clamp( 0.01 + uiSlider(1), 0., 0.8);
    float metallic 		= float(uiSlider(3)>0.5); 		// 0. or 1.
    float reflectance 	= clamp(uiSlider(2),0.35,1.); 	// [0.35;1]

    float alpha_x 		= roughness_x*roughness_x;
    float alpha_y 		= roughness_y*roughness_y;
    vec3  base_color    = uiColor(0);
    vec3 F0 = 0.16*reflectance*reflectance*(1.0-metallic) + base_color*metallic;

    if(screen.x>slide)
    {
        specular_anti_aliasing(wh,alpha_x,alpha_y);
    }

    //-- NDF + G + F
    float lambda_wo = lambda_beckmann_anisotropic(wo,alpha_x,alpha_y);
    float lambda_wi = lambda_beckmann_anisotropic(wi,alpha_x,alpha_y);
    float D = ndf_beckmann_anisotropic(wh,alpha_x,alpha_y); 
    float G = 1. / (1. + lambda_wo + lambda_wi);
	vec3  F = fresnel_schlick(wi_dot_wh,F0);

    //-- Lighting
  	vec3 specular_microfacet = (D * F * G) / ( 4. * cos_theta(wi) * cos_theta(wo) ) ;  
   	vec3 diffuse_lambert = m_i_pi*(1.-metallic)*base_color;
    float lighting = world_get_shadow(P+EPSILON*N,L,2.) * wg_dot_wi * world_get_light_intensity();

    vec3 C = lighting * (diffuse_lambert + specular_microfacet);
    C = C / (C + vec3(1.0)); //-- Reinhard Tonemapping
    C = pow(C,vec3(0.4545)); //-- Gamma Correction  
    return(C);
}

//-----------------------------------------------------------------------------
//-- Render Functions -----------------------------------------------------------
vec3 render(in vec3 ro, in vec3 rd, in vec2 screencoords)
{
    float slide = 0.5+0.5*sin(0.5*iTime);
    if(!SLIDE_ANIMATION){slide = clamp(iMouse.x/iResolution.x,0.,1.);}

    vec3 color = raymarch(ro,rd,screencoords,slide);

    if( (screencoords.x-slide) <= 0.005 && (screencoords.x-slide) >= -0.005) 
        color = vec3(1.,0.,0.);
    return color;
}

//-----------------------------------------------------------------------------
//-- Main ---------------------------------------------------------------------
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    WORLD_LIGHT_DIRECTION = normalize( vec3(cos(iTime),1.5,sin(iTime)) );

    vec2 screen_coords = ( 2.*gl_FragCoord.xy - iResolution.xy ) / iResolution.y;
    vec3 ro = vec3( 1., 4.+(sin(iTime)), 1. );
    vec3 rd = compute_camera_ray_direction(screen_coords, ro, vec3(0.), 0.);
    fragColor = vec4( render(ro,rd,fragCoord.xy/iResolution.xy), 1.);

    //-- UI integration -----------------------------------------------
    vec4 ui = texture(iChannel0,gl_FragCoord.xy/iResolution.xy);
    fragColor.rgb = mix(fragColor.rgb,ui.rgb, ui.a*.8);
}

