mat2 rotate2d(float angle)
{
    return mat2(cos(angle), -sin(angle),
                sin(angle), cos(angle));
}

float Circle(vec2 uv, vec2 p, float r, float blur) {
	float d = length(uv-p);
	float c = smoothstep(r, r-blur, d);
	return c;
}

float Band (float t, float start, float end, float blur) {
	float step1 = smoothstep(start-blur, start+blur, t);
	float step2 = smoothstep(end+blur, end-blur, t);
	return step1*step2;
}

float Rect (vec2 uv, vec2 position, float rot, float height, float width, float blur) {

    uv -= position; // translating coordinates system
    uv *= rotate2d(rot); // rotating coordinates system
	float band1 = Band(uv.x, -(width/2.0), width/2.0, blur);
	float band2 = Band(uv.y, -(height/2.0), height/2.0, blur);
	return band1*band2;
}

float Logo(vec2 uv, float width, float height, float blur){

    float maskK = Rect(uv, vec2(-width/2.0 + width/6.5, 0.0), 0.0, height-height/2.5, height/10.0, blur);
    maskK += Rect(uv, vec2(-width/2.0 + height/2.77777, -height/10.0), 15.0, height*0.5, height/16.77777, blur);
    maskK += Rect(uv, vec2(-width/2.0 + height/2.85714, height/5.8823529), 120.0, height/3.333333, height/16.77777, blur);
    maskK += Rect(uv, vec2(-width/2.0 + height/1.4285714, 0.0), 0.0, height-(height/5.0*2.0), height/10.0, blur);
    maskK += Rect(uv, vec2(-width/2.0 + height/0.90909, 0.0), 0.0, height-(height/5.0*2.0), height/10.0, blur);
    maskK += Rect(uv, vec2(-width/2.0 + height-(height/5.0), width/10.0), 15.0, height/2.631578, height/10.0-(height/50.0*2.0), blur);
    maskK += Rect(uv, vec2(-width/2.0 + height, width/10.0), 120.0, height/2.77777777, height/10.0-(height/50.0*2.0), blur);

    return maskK;
}


float Stamp(vec2 uv, vec2 position, float height, float width, float blur){

    uv -= position; // translating coordinates system

	float mask = Rect(uv, vec2(0.0, 0.0), 0.0, height, width, blur);

    float sr = height/20.0;

    int horN = int(5.0*(width/height));
    float margin = (5.0*(width/height) - float(horN))/(float(horN)+2.0);
    float edgePattern = 0.;

    for(float i = margin; int(i) < horN * 2; i += 1.0+margin){
    	edgePattern += Circle(uv, vec2(-(width/2.0) + sr*(i*2.0+1.0), height/2.0), sr, blur);	
        edgePattern += Circle(uv, vec2(-(width/2.0) + sr*(i*2.0+1.0), -height/2.0), sr, blur);	
    }
    for(float i = 0.5; i < 9.0; i++){
    	edgePattern += Circle(uv, vec2(width/2.0, -(height/2.0) + sr*(i*2.0+1.0)), sr, blur);
        edgePattern += Circle(uv, vec2(-width/2.0, -(height/2.0) + sr*(i*2.0+1.0)), sr, blur);
    }

    float logo = Logo(uv, width, height, blur);

    return mask-edgePattern-logo;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;

    float mask = Stamp(uv, vec2(cos(iTime)*0.65, sin(iTime)*0.3), 0.4, 0.5, 0.005);

    fragColor = vec4(abs(cos(iTime)), 1.0, abs(sin(iTime)), 1.0)*mask;
}

