    uv+=vec2(cos(iTime*.1),sin(iTime*.1));
    uv*=r(iTime*.01);
    vec3 col = vec3(-.0);
    for(float y=-1.;y<=1.;y++){
    for(float x=-1.;x<=1.;x++){
    vec2 offset = vec2(x,y);
