    vec2 gv = fract((uv+offset)*r(length(uv+offset)))-.5;
        gv*=r(cos(length(id)*10.));
    float d = fig(gv);+fig(gv+vec2(sin(ttime+length(id))*.1,cos(iTime)*.1));
    col += vec3(d)/exp(length(gv)*6.);


    }}
    col = mix(vec3(.1,.01,.02),vec3(.8,.4,.2),col);
    fragColor = vec4(col,1.0);
}

vec2 rotate(vec2 uv, float a)
{
    float cosa = cos(a), sina = sin(a);
    return vec2(uv.x*cosa-sina*uv.y,uv.x*sina+uv.y*cosa);
}

bool rect(vec2 p, vec4 dim, vec3 inCol, out vec3 outCol)
{
    bool res = false;
    if(abs(p.x-dim.x)<0.5*dim.z && abs(p.y-dim.y)<0.5*dim.w)
    {
        outCol = inCol;
        res = true;
    }
    return res;
}


bool circle(vec2 p, vec3 circ, vec3 inCol,bool outline, out vec3 outCol)
{
    float c = length(floor(p-circ.xy));
    bool res = false;
    if(c < circ.z)
    {
        res = true;
        if(outline)
        {
            float outline = circ.z-0.8;
            outCol= step(c,outline)*inCol+step(outline, c)*vec3(0);
        }
        else
        {
            outCol = inCol;
        }
    }
    return res;
}

bool hornsAndEyes(vec2 p, vec2 c, out vec3 col)
{
    bool res = abs(abs(p.x-c.x-0.5)-1.0)<0.5 && abs(abs(p.y-c.y-3.5)-1.0)<0.5;
    if(res)
    {
        col = vec3(0);
    }
    return res;
}

bool face(vec2 p, vec2 c, out vec3 col)
{
    bool hit = hornsAndEyes(p,c, col);
    hit = hit || rect(p,vec4(c.x+0.5,c.y+1.0,4.5,6), vec3(0.5),col);
    hit = hit || rect(p, vec4(c.x+0.5,c.y+2.5,8.5,1), vec3(0.5), col);
    hit = hit || rect(p,vec4(c.x+0.5,c.y-2.25,3.5,1.25), vec3(0.5), col);
    return hit;
}

bool legs(vec2 p, vec2 c, vec3 inCol, out vec3 col)
{
    bool hit = rect(p, vec4(c.x+1.5,c.y-6.0,1,2), inCol, col) || rect(p, vec4(c.x-0.5,c.y-6.0,1,2), inCol, col);
    hit = hit || rect(p, vec4(c.x+1.5,c.y-6.5,3,3), vec3(0), col) || rect(p, vec4(c.x-0.5,c.y-6.5,3,3), vec3(0), col);
    return hit;
}

bool body(vec2 p, vec2 c, vec3 inCol, out vec3 col)
{
    bool hit = legs(p, c, inCol, col);
    hit = hit || circle(p, vec3(c.xy,7.5), inCol, true, col);
    return hit;
}

bool sheep(vec2 p, vec2 c, vec3 bodyCol, out vec3 col)
{
    bool hit = face(p, c, col);
    hit = hit || body(p, c, bodyCol, col);
    return hit;
}

bool moon(vec2 p, vec3 circ, vec3 inCol, out vec3 col)
{
    vec3 crecentO = vec3(3,-2,0.2*circ.z);
    bool hit = circle(p, circ,vec3(1.0,0.9,0.0),true, col);
    hit = hit && !circle(p, circ-crecentO,vec3(1.0,0.9,0.0),false, col);
    return hit;
}

vec3 bg(vec2 p, float dayV)
{
    vec3 col;
    vec2 baseP = vec2(40,35);
    vec2 sunP = baseP + vec2(-20,10) + vec2(0,40)*(1.0-dayV);
    vec2 moonP = baseP + vec2(20,-2) + vec2(0,40)*dayV;
    bool hit = circle(p, vec3(sunP,12.75),vec3(1.0,0.9,0.0),false, col);
    hit = hit || moon(p, vec3(moonP, 10.5), vec3(1.0,0.9,0.0), col);
    if(!hit)
    {
    	col = step(p.y,9.0)*mix(vec3(0.06,0.45,0.01),vec3(0.12,0.9,0.2),dayV-0.5)+
        	step(9.0,p.y)*mix(vec3(0.1,0.06,0.45),vec3(0.2,0.12,0.9),dayV);
    }
    return col;
}

vec3 scene(vec2 p)
{
    vec3 col = vec3(0);
    float dayV = 2.0*abs(fract(0.5+0.125*iTime)-0.5);
    dayV = dayV*dayV*(3.0-2.0*dayV);
    bool hit = false;
    for(float i = 0.0;i<5.0;i++)
    {
        vec2 c = vec2(20.0*i +10.0*sin(2.0*iTime), 15.0+2.0*abs(cos(2.0*i+10.0*iTime)));
        vec3 scol = mix(vec3(0.9,0.8,0.7),vec3(1),0.833*fract(0.6*i))*dayV;
        hit = hit || sheep(p, c, scol, col);
    }
    return hit? col : bg(p, dayV);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv*= vec2(80,45);
    vec3 col = scene(uv);
    fragColor = vec4(col,1.0);
}

