// "Moebius, Menger, Spiders" by dr2 - 2020
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

#define VAR_ZERO min (iFrame, 0)

float PrBoxDf (vec3 p, vec3 b);
float PrSphDf (vec3 p, float s);
float PrCylDf (vec3 p, float r, float h);
float PrEETapCylDf (vec3 p, vec3 v1, vec3 v2, float r, float rf);
float PrEllipsDf (vec3 p, vec3 r);
vec3 VaryNf (vec3 p, vec3 n, float f);
float SmoothBump (float lo, float hi, float w, float x);
mat3 StdVuMat (float el, float az);
vec2 Rot2D (vec2 q, float a);

vec3 footPos[8], kneePos[8], hipPos[8], ltDir, qHit;
float dstFar, tCur, mobRad, legLenU, legLenD, bdyHt, spdVel, nSpd;
int idObj;
const int idMob = 1, idBdy = 11, idHead = 12, idEye = 13, idAnt = 14, idLegU = 15, idLegD = 16;
const float pi = 3.14159;

#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float MobiusDf (vec3 p)
{
  vec3 b;
  float sclFac, r, a;
  const int nIt = 4;
  sclFac = 2.6;
  b = (sclFac - 1.) * vec3 (1., 1.125, 0.625);
  r = length (p.xz);
  a = (r > 0.) ? atan (p.z, - p.x) / (2. * pi) : 0.;
  p = vec3 (mod (16. * a + 1., 2.) - 1., Rot2D (vec2 (p.y, r - 32. / (2. * pi)), pi * a));
  for (int n = VAR_ZERO; n < nIt; n ++) {
    p = abs (p);
    p.xy = (p.x > p.y) ? p.xy : p.yx;
    p.xz = (p.x > p.z) ? p.xz : p.zx;
    p.yz = (p.y > p.z) ? p.yz : p.zy;
    p = sclFac * p - b;
    p.z += b.z * step (p.z, -0.5 * b.z);
  }
  return 0.9 * PrBoxDf (p, vec3 (1.)) / pow (sclFac, float (nIt));
}

float SpdDf (vec3 p, float dMin, int tpId)
{
  vec3 q;
  float d, s, len, szFac;
  szFac = 0.2;
  p /= szFac;
  dMin /= szFac; 
  p.y -= bdyHt + 0.7;
  q = p - vec3 (0., -0.15, 0.2);
  d = PrEllipsDf (q, vec3 (0.7, 0.5, 1.3));
  DMINQ (idBdy + tpId);
  q = p - vec3 (0., 0.1, 1.1);
  d = PrEllipsDf (q, vec3 (0.2, 0.4, 0.5));
  DMINQ (idHead + tpId);
  q = p;
  q.x = abs (q.x);
  q -= vec3 (0.15, 0.25, 1.5);
  d = PrSphDf (q, 0.13);
  DMINQ (idEye + tpId);
  q -= vec3 (0., 0.15, -0.3);
  d = PrEETapCylDf (q, 1.3 * vec3 (0.3, 1.1, 0.4), vec3 (0.), 0.07, 0.7);
  DMINQ (idAnt + tpId);
  p.y += bdyHt;
  for (int j = VAR_ZERO; j < 8; j ++) {
    q = p - hipPos[j];
    d = 0.6 * PrEETapCylDf (q, kneePos[j], hipPos[j], 0.25, 0.3);
    DMINQ (idLegU + tpId);
    q = p - kneePos[j];
    d = 0.6 * PrEETapCylDf (q, footPos[j] - vec3 (0.3), kneePos[j] - vec3 (0.3), 0.2, 1.2);
    DMINQ (idLegD + tpId);
  }
  dMin *= szFac;
  return dMin;
}

float ObjDf (vec3 p)
{
  vec3 q;
  float dMin, d, a, aq, na;
  dMin = dstFar;
  d = MobiusDf (p);
  DMINQ (idMob);
  q = p;
  a = tCur * spdVel / (2. * pi * mobRad);
  q.xz = Rot2D (q.xz, a);
  na = floor (nSpd * atan (q.z, - q.x) / (2. * pi));
  aq = 2. * pi * (na + 0.5) / nSpd;
  q.xz = Rot2D (q.xz, aq);
  q.x += mobRad;
  a += aq;
  if (2. * floor (0.5 * na) != na) a += 2. * pi;
  q.xy = Rot2D (q.xy, 0.5 * a);
  q.y -= 0.8;
  if (PrCylDf (q.xzy, 1., 0.7) < dMin) dMin = SpdDf (q, dMin, 0);
  q = p;
  a = - tCur * spdVel / (2. * pi * mobRad);
  q.xz = Rot2D (q.xz, a);
  na = floor (nSpd * atan (q.z, - q.x) / (2. * pi));
  aq = 2. * pi * (na + 0.5) / nSpd;
  q.xz = Rot2D (q.xz, aq);
  q.x += mobRad;
  a += aq;
  if (2. * floor (0.5 * na) != na) a += 2. * pi;
  q.xy = Rot2D (q.xy, 0.5 * a + 0.5 * pi);
  q.y -= 0.8;
  if (PrCylDf (q.xzy, 1., 0.7) < dMin) dMin = SpdDf (vec3 (- q.xz, q.y).xzy, dMin, 10);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 160; j ++) {
    d = ObjDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.0001 || dHit > dstFar) break;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.02;
  for (int j = VAR_ZERO; j < 20; j ++) {
    h = ObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += h;
    if (sh < 0.05) break;
  }
  return 0.4 + 0.6 * sh;
}

void SpdSetup (float gDisp)
{
  vec3 v;
  float a, az, fz, d, ll;
  nSpd = 10.;
  for (int j = VAR_ZERO; j < 4; j ++) {
    a = 0.2 * (1. + float (j)) * pi;
    hipPos[j] = 0.5 * vec3 (- sin (a), 0., 1.5 * cos (a));
    hipPos[j + 4] = hipPos[j];
    hipPos[j + 4].x *= -1.;
  }
  bdyHt = 1.5;
  legLenU = 2.2;
  legLenD = 3.;
  ll = legLenD * legLenD - legLenU * legLenU;
  for (int j = VAR_ZERO; j < 8; j ++) {
    fz = fract ((gDisp + 0.93 + ((j < 4) ? -1. : 1.) + mod (7. - float (j), 4.)) / 3.);
    az = smoothstep (0.7, 1., fz);
    footPos[j] = 5. * hipPos[j];
    footPos[j].x *= 1.7;
    footPos[j].y += 0.7 * sin (pi * clamp (1.4 * az - 0.4, 0., 1.));
    footPos[j].z += ((j < 3) ? 0.5 : 1.) - 3. * (fz - az);
    hipPos[j] += vec3 (0., bdyHt - 0.3, 0.2);
    v = footPos[j] - hipPos[j];
    d = length (v);
    a = asin ((hipPos[j].y - footPos[j].y) / d);
    kneePos[j].y = footPos[j].y + legLenD *
       sin (acos ((d * d + ll) / (2. * d *  legLenD)) + a);
    kneePos[j].xz = hipPos[j].xz + legLenU * sin (acos ((d * d - ll) /
       (2. * d *  legLenU)) + 0.5 * pi - a) * normalize (v.xz);
  }
}

vec3 SpdCol (vec3 vn)
{
  vec3 col, c1, c2;
  if (idObj >= idBdy + 10) {
    idObj -= 10;
    c1 = vec3 (0.5, 1., 0.2);
    c2 = vec3 (0.5, 0.2, 0.2);
  } else {
    c1 = vec3 (1., 0.5, 0.2);
    c2 = vec3 (0.2, 0.2, 0.5);
  }
  if (idObj == idBdy) {
    col = mix (c1, c2, SmoothBump (0.2, 0.7, 0.05, mod (4. * qHit.z, 1.)));
  } else if (idObj == idHead) {
    col = c2;
    if (qHit.z > 0.4) col = mix (vec3 (0.2, 0.05, 0.05), col,
       smoothstep (0.02, 0.04, abs (qHit.x)));
  } else if (idObj == idEye) {
    col = (vn.z < 0.6) ? vec3 (0., 1., 0.) : c1;
  } else if (idObj == idLegU || idObj == idLegD) {
    col = mix (c2, c1,  SmoothBump (0.4, 1., 0.2, fract (3.5 * length (qHit))));
  } else if (idObj == idAnt) {
    col = vec3 (1., 1., 0.3);
  }
  return col;
}

vec3 BgCol (vec3 rd)
{
  float t, gd, b;
  t = tCur * 1.5;
  b = dot (vec2 (atan (rd.x, rd.z), 0.5 * pi - acos (rd.y)), vec2 (2., sin (rd.x)));
  gd = clamp (sin (5. * b + t), 0., 1.) * clamp (sin (3.5 * b - t), 0., 1.) +
     clamp (sin (21. * b - t), 0., 1.) * clamp (sin (17. * b + t), 0., 1.);
  return mix (vec3 (0.35, 0.5, 1.), vec3 (0.1, 0.4, 0.3), 0.5 * (1. - rd.y)) *
     (0.24 + 0.44 * (rd.y + 1.) * (rd.y + 1.)) * (1. + 0.15 * gd);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 vn, col;
  float dstObj, sh;
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += rd * dstObj;
    vn = ObjNf (ro);
    if (idObj == idMob) {
      col4 = vec4 (0.6, 0.6, 0.7, 0.1);
      vn = VaryNf (32. * ro, vn, 1.);
    } else if (idObj >= idBdy) {
      col4 = vec4 (SpdCol (vn), 1.);
    }
    sh = ObjSShadow (ro, ltDir);
    col = col4.rgb * (0.2 + 0.2 * max (- dot (vn, ltDir), 0.) +
       0.8 * sh * max (dot (vn, ltDir), 0.)) +
       step (0.95, sh) * col4.a * pow (max (0., dot (ltDir, reflect (rd, vn))), 32.);
    col = mix (col, BgCol (reflect (rd, vn)), 0.3);
  } else col = BgCol (rd);
  return clamp (col, 0., 1.);
}

#define AA  0  // optional antialiasing

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv;
  float el, az, zmFac, t, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = 0.;
  el = 0.;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += pi * mPtr.y;
  } else {
    t = (floor (0.3 * tCur) + smoothstep (0.8, 1., mod (0.3 * tCur, 1.)));
    az += 0.1 * t;
    el -= 0.1 * pi * (1. - sin (0.06 * t));
  }
  el = clamp (el, -0.3 * pi, 0.3 * pi);
  zmFac = 4.5 - 1.5 * abs (el);
  dstFar = 50.;
  mobRad = 5.;
  spdVel = 1.5;
  SpdSetup (spdVel * tCur);
  vuMat = StdVuMat (el, az);
  ro = vuMat * vec3 (0., 0., -20.);
  ltDir = vuMat * normalize (vec3 (0.5, 1., -1.));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (pow (col, vec3 (0.9)), 1.);
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrEETapCylDf (vec3 p, vec3 v1, vec3 v2, float r, float rf)
{
  vec3 v;
  float s;
  v = v1 - v2;
  s = clamp (dot (p, v) / dot (v, v), 0., 1.);
  return length (p - s * v) - r * (1. - rf * s * s);
}

float PrEllipsDf (vec3 p, vec3 r)
{
  return (length (p / r) - 1.) * min (r.x, min (r.y, r.z));
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec2 e;
  e = vec2 (0.1, 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

////////////////////////////////////////////////////////////////////////////////
//
// Euclidean Honeycombs
//
// Matthew Arcus, 2020.
//
// Using the Wythoff construction: https://en.wikipedia.org/wiki/Wythoff_construction
// to construct honeycombs in 3D space. The fundamental region is a Goursat
// Tetrahedron: https://en.wikipedia.org/wiki/Goursat_tetrahedron and the
// Wythoff construction goes through in a similar way to the planar and spherical
// case - a vertex is selected in the tetrahedron, then reflected in all ways in the
// faces of the tetrahedron (this results in an infinite lattice of points). In each
// image of the region, there is an edge from the vertex image to the vertex in each
// adjacent region, perpendicular to the shared face.
//
// <mouse>: image rotation
// <up>/<down>: move in/out
// <left>/<right>: cycle through the 15 honeycombs of the tetrahedron
// <page up>/<page down>: select centred vertex
// a: show main honeycomb
// d: show dual honeycomb
// c: clip to sphere or cube
// i: invert about origin
// m: monochrome
// o: put viewpoint at origin
// r: autorotation
// v: morph between trilinears
// z: translate on z-axis (nice when inverting)
//
// Each region vertex is the centre of a polyhedron, which may however be of
// zero volume (for example, if the region point coincides with that vertex).
// The dual honeycomb is constructed by connecting the region vertices that
// contain non-zero volume polyhedra.
//
////////////////////////////////////////////////////////////////////////////////

// Select basic tetrahedron here.
#define T0 0 // Basic tetrahedron: (4,2,2,3,2,4)
#define T1 1 // Two T0 tetrahedra joined
#define T2 2 // Two T1 tetrahedra joined
//#define SNUB // Special snub form
// For a uniform snub, the four reflections of the region point should be
// mutually  equidistant, but this is generally not possible, so the snub
// forms are necessarily non-uniform.
#define TYPE T1

bool showdual = true;
bool showmain = true;
bool doinvert = true;
bool dotranslate = false;
bool dorotate = false;
bool doclip = true;
bool domorph = true;
float clipsphere = 5.5;
float clipcube = 2.1;
int centre = 0; // The vertex index to centre

const float maxdepth = 20.0;
const float fogstart = 5.0;

const float sscale = 2.0; // Thickness of edges and vertices
const float swidth = sscale*0.05;
const float swidth2 = swidth*swidth;
const float twidth = sscale*0.02;
const float twidth2 = twidth*twidth;
const float fwidth = sscale*0.005;

int alert = 0;
void assert(bool test) {
  if (!test) alert = 1;
}

const float PI =  3.141592654;

// PQRS is fundamental tetrahedron
// Edges are PQ,PR,PS,QR,QS,RS
#if TYPE==T0
const vec3 P = vec3(0,0,0);
const vec3 Q = vec3(1,0,0);
const vec3 R = vec3(1,1,0);
const vec3 S = vec3(1,1,1);
#elif TYPE==T1
const vec3 P = vec3(0,0,0);
const vec3 Q = vec3(2,0,0);
const vec3 R = vec3(1,1,0);
const vec3 S = vec3(1,1,1);
#elif TYPE==T2
const vec3 P = vec3(0,0,0);
const vec3 Q = vec3(2,0,0);
const vec3 R = vec3(1,1,-1);
const vec3 S = vec3(1,1,1);
#endif

// These are the planes of the tetrahedron.
// Plane pP is opposite P, etc.
// w coordinate is distance of plane from origin
// Normals should face into the tetrahedron.
const float K = 1.414213562;
#if TYPE==T0
const vec4 pP = vec4(-1,0,0,1);
const vec4 pQ = 0.5*K*vec4(1,-1,0,0);
const vec4 pR = 0.5*K*vec4(0,1,-1,0);
const vec4 pS = vec4(0,0,1,0);
#elif TYPE==T1
const vec4 pP = 0.5*K*vec4(-1,-1,0,2);
const vec4 pQ = 0.5*K*vec4(1,-1,0,0);
const vec4 pR = 0.5*K*vec4(0,1,-1,0);
const vec4 pS = vec4(0,0,1,0);
#elif TYPE==T2
const vec4 pP = 0.5*K*vec4(-1,-1,0,2);
const vec4 pQ = 0.5*K*vec4(1,-1,0,0);
const vec4 pR = 0.5*K*vec4(0,1,-1,0);
const vec4 pS = 0.5*K*vec4(0,1,1,0);
#endif

const float lP = length(cross(Q-R,Q-S));
const float lQ = length(cross(R-S,R-P));
const float lR = length(cross(S-P,S-Q));
const float lS = length(cross(P-Q,P-R));

const vec4 tri2bary = vec4(lP,lQ,lR,lS); // The lengths of the unnormalized cross products

vec4 T = vec4(1,1,1,1); // Trilinear coordinates of Wythoff point
vec3 A; // The Wythoff point itself
vec3 pA,qA,rA,sA; // A reflected in pP, pQ etc.

vec4 trymirror(vec4 p4, vec4 m4, inout int flips) {
  float t = dot(p4,m4);
  flips += int(t < 0.0);
  p4.xyz -= 2.0*min(0.0,t)*m4.xyz;
  return p4;
}

vec3 fold(vec3 p, inout int flips) {
  // Translate along z axis. Interesting when inverted.
  if (dotranslate) p.z += 0.1*iTime;
  // Move selected vertex to centre of image.
  // P is the origin & therefore the default.
  if (centre == 1) p += Q;
  else if (centre == 2) p += R;
  else if (centre == 3) p += S;

  // Attempt to optimize folding for the three  tetrahedron types.
#if TYPE==T0
  // Fold in to unit cube
  p = mod(p+1.0,2.0)-1.0;
  flips += int(p.x < 0.0) + int(p.y < 0.0) + int(p.z < 0.0);
  p = abs(p);
  vec4 p4 = vec4(p,1);
  p4 = trymirror(p4,pQ,flips);
  p4 = trymirror(p4,pR,flips);
  p4 = trymirror(p4,pQ,flips);
#elif TYPE==T1
  p = mod(p+2.0,4.0)-2.0;
  flips += int(p.x < 0.0) + int(p.y < 0.0);
  p.xy = abs(p.xy);
  vec4 p4 = vec4(p,1);
  p4 = trymirror(p4,pS,flips);
  for (int i = 0; i < 2; i++) {
    p4 = trymirror(p4,pP,flips);
    p4 = trymirror(p4,pQ,flips);
    p4 = trymirror(p4,pR,flips);
  }
#elif TYPE==T2
  p = mod(p+2.0,4.0)-2.0;
  vec4 p4 = vec4(p,1);
  for (int i = 0; i < 4; i++) {
    p4 = trymirror(p4,pP,flips);
    p4 = trymirror(p4,pQ,flips);
    p4 = trymirror(p4,pR,flips);
    p4 = trymirror(p4,pS,flips);
  }
#endif
  return p4.xyz;
}

vec3 colors[] = vec3[](vec3(0.3),
                       vec3(1,0,0),
                       vec3(0,1,0),
                       vec3(0,0,1),
                       vec3(1,1,0),
                       vec3(0,1,1),
                       vec3(1,1,1),
                       vec3(1,0,1),
                       vec3(0.8,1,0.8)
                      );

// Reflect p in mirror,normal M, distance d from origin.
vec3 mirror(vec3 p, vec4 M) {
  float t = dot(vec4(p,1),M);
  return p - 2.0*t*M.xyz;
}

vec3 mp(vec3 p) {
  return mirror(p,pP);
}
vec3 mq(vec3 p) {
  p = mirror(p,pQ);
  return p;
}
vec3 mr(vec3 p) {
  return mirror(p,pR);
}
vec3 ms(vec3 p) {
  return mirror(p,pS);
}

#if defined SNUB
vec3 snubpoints[12];
int snubcolors[12] = int[](4,4,6,3,2,2,5,1,1,6,3,5);
#endif

void init(vec4 tri) {
  tri *= tri2bary;
  A = tri.x*P + tri.y*Q + tri.z*R + tri.w*S;
  A /= dot(tri,vec4(1));
  pA = mp(A); qA = mq(A);
  rA = mr(A); sA = ms(A);
#if defined SNUB
  // Reflections of a in P,Q,R,S
  // Double reflections of Wythoff point A
  // Currently just for T0 tetrahedron, and no dual
  snubpoints[0] = mp(mq(A));
  snubpoints[1] = mq(mp(A));
  snubpoints[2] = mp(mr(A)); // p,r commute so no rp
  snubpoints[3] = mp(ms(A)); // p,s commute so no sp
  snubpoints[4] = mq(mr(A));
  snubpoints[5] = mr(mq(A));
  snubpoints[6] = mq(ms(A)); // q,s commute so no sp
  snubpoints[7] = mr(ms(A));
  snubpoints[8] = ms(mr(A));
  snubpoints[9] = mr(mp(A)); // For T1, p,r don't commute!
  snubpoints[10] = ms(mp(A));
  snubpoints[11] = ms(mq(A));
#endif
}

float face(vec3 p, vec3 q, vec3 r, vec3 s) {
  p -= q;
#if 0
  r -= q;
  s -= q;
  r = normalize(r);
  s = normalize(s);
#endif
  float rs = dot(r,s), pr = dot(p,r), ps = dot(p,s);
  mat2 m = mat2(1,rs,rs,1);
  vec2 t = inverse(m)*vec2(pr,ps);
  t = max(t,0.0);
  vec3 p1 = t[0]*r+t[1]*s;
  return distance(p,p1);
}

float segment(vec3 p, vec3 a, vec3 b) {
  vec3 pa = p - a;
  vec3 ba = b - a;
  float h = clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
  float d = length(pa - ba * h);
  return d;
}

#if defined SNUB
// A is the region point
// For scene0, A is the vertex, with segments drawn
// from A to the 12 double reflections of A in the
// tetrahedron sides. Some of these segments might
// coincide.
float snubscene0(vec3 p, out int index) {
  float d,d0 = 1e8;
  d = length(p-A)-swidth;
  if (d < d0) d0 = d, index = 0;
  for(int i = 0; i < snubpoints.length(); i++) {
    vec3 r = snubpoints[i];
    d = segment(p,A,r)-twidth;
    if (d < d0) d0 = d,  index = snubcolors[i];
  }
  return d0;
 }

// For scene1, vertices are drawn for the four
// reflections of A in the tetrahedron sides
// and for the six segments connecting those
// four reflections.
float snubscene1(vec3 p, out int index) {
  float d,d0 = 1e8;
  d = segment(p,pA,qA)-twidth;
  if (d < d0) d0 = d,  index = 4;
  d = segment(p,pA,rA)-twidth;
  if (d < d0) d0 = d,  index = 6;
  d = segment(p,pA,sA)-twidth;
  if (d < d0) d0 = d,  index = 3;
  d = segment(p,qA,rA)-twidth;
  if (d < d0) d0 = d,  index = 2;
  d = segment(p,qA,sA)-twidth;
  if (d < d0) d0 = d,  index = 5;
  d = segment(p,rA,sA)-twidth;
  if (d < d0) d0 = d,  index = 1;

  // The vertex spheres of the adjacent regions
  // might overlap with this region.
  d = length(pA-p)-swidth;
  if (d < d0) d0 = d,  index = 0;
  d = length(qA-p)-swidth;
  if (d < d0) d0 = d,  index = 0;
  d = length(rA-p)-swidth;
  if (d < d0) d0 = d,  index = 0;
  d = length(sA-p)-swidth;
  if (d < d0) d0 = d,  index = 0;
  return d0;
}
#endif

void tryvertex(vec3 pos, vec3 P, int i, inout float d0, inout int index) {
  float d = length(pos-P)-swidth;
  if (d < d0) {
    d0 = d; index = i;
  }
}

void tryvertex(vec3 pos, vec3 A, vec3 P, vec3 nQ, vec3 nR, vec3 nS, int i, inout float d0, inout int index) {
#if 1
  float d = length(pos-P)-swidth;
  if (d < d0) {
    d0 = d; index = i;
  }
#else
  float d = d0;
  float t = 0.25;
  //t = 0.5+0.5*sin(iTime);
  //t = 0.8;
  vec3 A1 = t*A + (1.0-t)*P;
  d = min(d,face(pos,A1,nQ,nR)-fwidth);
  d = min(d,face(pos,A1,nR,nS)-fwidth);
  d = min(d,face(pos,A1,nS,nQ)-fwidth);
  if (d < d0) { d0 = d; index = i; }
#endif
}

float dualscene(vec3 pos, out int index) {
  // For diagram: o-o-o-o
  bool mp = bool(T[0]), mq = bool(T[1]);
  bool mr = bool(T[2]), ms = bool(T[3]);
  // Based on consideration of the Coxeter-Dynkin diagram:
  // Dual node A appears if any of the b,c,d mirrors
  // are activated, unless the diagram is 'o o-o', ie.
  // a prism in which case the isolated node must be
  // activated as well as one of the others (as is the
  // case for Q and R in type T0).
  float d = 1e8;
#if TYPE==T0
  // P-Q-R-S
  if (mq || mr || ms) tryvertex(pos,A,P,-pQ.xyz,-pR.xyz,-pS.xyz,1,d,index);
  if (mp && (mr || ms)) tryvertex(pos,A,Q,-pR.xyz,-pS.xyz,-pP.xyz,2,d,index);
  if (ms && (mp || mq)) tryvertex(pos,A,R,-pS.xyz,-pP.xyz,-pQ.xyz,3,d,index);
  if (mp || mq || mr) tryvertex(pos,A,S,-pP.xyz,-pQ.xyz,-pR.xyz,4,d,index);
#elif TYPE==T1
  // P
  //  \
  //   R-S
  //  /
  // Q
  if (mq || mr || ms) tryvertex(pos,A,P,-pQ.xyz,-pR.xyz,-pS.xyz,1,d,index);
  if (mp || mr || ms) tryvertex(pos,A,Q,-pR.xyz,-pS.xyz,-pP.xyz,2,d,index);
  if (ms && mp && mq) tryvertex(pos,A,R,-pS.xyz,-pP.xyz,-pQ.xyz,3,d,index);
  if (mp || mq || mr) tryvertex(pos,A,S,-pP.xyz,-pQ.xyz,-pR.xyz,4,d,index);
#elif TYPE==T2
  // P-R
  // | |
  // S-Q
  if (mq || mr || ms) tryvertex(pos,A,P,-pQ.xyz,-pR.xyz,-pS.xyz,1,d,index);
  if (mp || mr || ms) tryvertex(pos,A,Q,-pR.xyz,-pS.xyz,-pP.xyz,2,d,index);
  if (ms || mp || mq) tryvertex(pos,A,R,-pS.xyz,-pP.xyz,-pQ.xyz,3,d,index);
  if (mp || mq || mr) tryvertex(pos,A,S,-pP.xyz,-pQ.xyz,-pR.xyz,4,d,index);
#endif
  float d0 = d;
  // Again, look at reduced diagram with just 2 nodes -
  // if connected, either mirror will do, if isolated,
  // need both.
#if TYPE==T0
  if (mr || ms) d = min(d,segment(pos,P,Q)-twidth);
  if (mq && ms) d = min(d,segment(pos,P,R)-twidth);
  if (mq || mr) d = min(d,segment(pos,P,S)-twidth);
  if (mp && ms) d = min(d,segment(pos,Q,R)-twidth);
  if (mp && mr) d = min(d,segment(pos,Q,S)-twidth);
  if (mp || mq) d = min(d,segment(pos,R,S)-twidth);
#elif TYPE==T1
  if (mr || ms) d = min(d,segment(pos,P,Q)-twidth);
  if (mq && ms) d = min(d,segment(pos,P,R)-twidth);
  if (mq || mr) d = min(d,segment(pos,P,S)-twidth);
  if (mp && ms) d = min(d,segment(pos,Q,R)-twidth);
  if (mp || mr) d = min(d,segment(pos,Q,S)-twidth);
  if (mp && mq) d = min(d,segment(pos,R,S)-twidth);
#elif TYPE==T2
  if (mr && ms) d = min(d,segment(pos,P,Q)-twidth);
  if (mq || ms) d = min(d,segment(pos,P,R)-twidth);
  if (mq || mr) d = min(d,segment(pos,P,S)-twidth);
  if (mp || ms) d = min(d,segment(pos,Q,R)-twidth);
  if (mp || mr) d = min(d,segment(pos,Q,S)-twidth);
  if (mp && mq) d = min(d,segment(pos,R,S)-twidth);
#endif
  if (d != d0) index = 8;
  return d;
}

float mainscene(vec3 p, out int index) {
  float d,d0 = 1e8;
  d = length(p-A)-swidth;
  if (d < d0) { d0 = d; index = 0; }
  d = segment(p,A,pA)-twidth;
  if (d < d0) { d0 = d; index = 1; }
  d = segment(p,A,qA)-twidth;
  if (d < d0) { d0 = d; index = 2; }
  d = segment(p,A,rA)-twidth;
  if (d < d0) { d0 = d; index = 3; }
  d = segment(p,A,sA)-twidth;
  if (d < d0) { d0 = d; index = 4; }
  d = 1e8;
  if (key(CHAR_0+1)) d = min(d,face(p,A,-pP.xyz,-pQ.xyz)-fwidth);
  if (key(CHAR_0+2)) d = min(d,face(p,A,-pQ.xyz,-pR.xyz)-fwidth);
  if (key(CHAR_0+3)) d = min(d,face(p,A,-pR.xyz,-pP.xyz)-fwidth);
  if (key(CHAR_0+4)) d = min(d,face(p,A,-pP.xyz,-pS.xyz)-fwidth);
  if (key(CHAR_0+5)) d = min(d,face(p,A,-pQ.xyz,-pS.xyz)-fwidth);
  if (key(CHAR_0+6)) d = min(d,face(p,A,-pR.xyz,-pS.xyz)-fwidth);
  if (d < d0) { d0 = d; index = 5; }
  return d0;
}

float getdist(vec3 p, out int index) {
  float iscale = 1.0;
  int flips = 0;
  if (doinvert) {
    flips++;
    // Invert in sphere, radius 2.
    float r2 = 0.5*dot(p,p);
    p /= r2;
    iscale = r2;
  }
  p = fold(p,flips);
#if defined SNUB
  float d = flips%2 == 0 ? snubscene0(p,index) : snubscene1(p,index);
#else
  float d1,d = 1e8;
  int index1;
  if (showmain) {
    d1 = mainscene(p,index1);
    if (d1 < d) { d = d1; index = index1; }
  }
  if (showdual) {
    d1 = dualscene(p,index1);
    if (d1 < d) { d = d1; index = index1; }
  }
#endif
  return iscale*d;
}

float eval(vec3 p) {
  vec3 p0 = p;
  int index;
  float d = getdist(p,index);
  if (doclip) {
    if (doinvert) {
      d = max(d,length(p0)-clipsphere); // Intersect with sphere
    } else {
      // Intersect with cube sides
      d = max(d,abs(p0.x)-clipcube);
      d = max(d,abs(p0.y)-clipcube);
      d = max(d,abs(p0.z)-clipcube);
    }
  }
  return d;
}

//-------------------------------------------------
//From https://www.shadertoy.com/view/XtXGRS#
vec2 rotate(in vec2 p, in float t) {
  return p * cos(-t) + vec2(p.y, -p.x) * sin(-t);
}

vec3 calcNormal(in vec3 p) {
  const vec2 e = vec2(0.001, 0.0);
  return normalize(vec3(eval(p + e.xyy) - eval(p - e.xyy),
                        eval(p + e.yxy) - eval(p - e.yxy),
                        eval(p + e.yyx) - eval(p - e.yyx)));
}

float march(in vec3 ro, in vec3 rd) {
  const float precis = 0.001; // Should be resolution dependent
  float t = 0.25; // Start a little way from the eye - less occlusion.
  for (int i = 0; i < 200 && t < maxdepth; i++) {
    // Do we need a rate limiter?
    float h = eval(ro + rd * t);
    // Rate limit and apply fudge for inversion.
    if (doinvert) h = min(1.0,0.6*h);
    t += h;
    if (h < t*precis) return t;
  }
  return -1.0;
}

vec3 transform(in vec3 p) {
  if (iMouse.x > 0.0) {
    float phi = (2.0*iMouse.x-iResolution.x)/iResolution.x*PI;
    float theta = (2.0*iMouse.y-iResolution.y)/iResolution.y*PI;
    p.yz = rotate(p.yz,theta);
    p.zx = rotate(p.zx,-phi);
  }
  if (dorotate) {
    p.yz = rotate(p.yz,iTime * 0.125);
    p.zx = rotate(p.zx,iTime * 0.1);
  }
  return p;
}

vec3 getcolor(vec3 pos) {
  int index;
  getdist(pos,index);
  assert(index >= 0 && index < colors.length());
  return 0.2+0.6*colors[index];
}

int mymod(int n, int m) {
  // glsl % undefined for -ve arguments
  if (n < 0) return m-1-(-n-1)%m;
  else return n%m;
}

vec4 gettrilinear(int n) {
  n = mymod(n,15);
  return 1.0-vec4(float((n>>0)&1),float((n>>1)&1),float((n>>2)&1),float((n>>3)&1));
}

vec4 gettrilinear() {
  float t = 0.5*iTime;
  int i = int(t);
  doinvert = doinvert != (i/30%2 == 1);
  doclip = doclip != (i/60%2 == 1);
  float k = 0.0;
  if (domorph) k = fract(t);
  return mix(gettrilinear(i/2),gettrilinear((i+1)/2),k);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  showdual = !key(CHAR_D);
  showmain = !key(CHAR_A);
  doclip = key(CHAR_C);
  doinvert = key(CHAR_I);
  dorotate = !key(CHAR_R);
  dotranslate = key(CHAR_Z);
  domorph = !key(CHAR_V);

#if defined TEST
  //doinvert = true;
  showdual = false;
#endif

#if defined SNUB
  T = vec4(1);
#else
  int n = mymod(keycount(KEY_RIGHT)-keycount(KEY_LEFT),16);
  if (n == 0) T = gettrilinear();
  else T = gettrilinear(n-1);
#endif
  centre = mymod(keycount(KEY_PAGE_DOWN)-keycount(KEY_PAGE_UP),4);
  init(T);
  vec2 p = (2.0*fragCoord.xy - iResolution.xy)/ iResolution.y;
  vec3 ro = vec3(0, 0, 6.0);
  //vec3 ro = vec3(0, 0, -0.2*iTime);
  vec3 rd = normalize(vec3(p, -2.0));
  if (key(CHAR_O)) ro = vec3(0);
  ro = transform(ro);
  ro *= exp(0.1*float(keycount(KEY_DOWN)-keycount(KEY_UP)));
  rd = transform(rd);
  vec3 light = normalize(vec3(0.5, 0.8, 3.0));
  light = transform(light);

  float t = march(ro,rd);
  vec3 col = (doclip || doinvert) ? 0.05*(1.0-p.y)*vec3(1,0,1) : vec3(1,1,0.5);
  if (t > 0.001) {
    vec3 pos = ro + t * rd;
    vec3 n = calcNormal(pos);
    vec3 basecol = getcolor(pos);
    float diffuse = clamp(dot(n, light), 0.0, 1.0);
    float specular = pow(max(0.0,dot(reflect(light,n),vec3(rd))),4.0);
    vec3 c = basecol;
    c *= 0.3+0.5*diffuse;
    vec3 speccol = basecol;
    c += 0.3*specular*speccol;
    col = mix(c,col,clamp((t-fogstart)/(maxdepth-fogstart),0.0,1.0));
  }
  if (key (CHAR_M)) col = vec3(0.333*dot(col,vec3(1)));
  col = pow(col, vec3(0.4545));
  if (alert != 0) col = vec3(1,0,0);
  fragColor = vec4(col, 1.0);
}

