float tri(vec2 st, float pct){
  return 1.0 - smoothstep( pct, pct+0.05, st.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;

    // Time varying pixel color
    vec3 color_a = vec3(uv.x * 0.2 + 0.8,0.2,0.0);
    vec3 color_b = vec3(0.0,0.1,uv.y * 0.5 + 0.5);
    vec3 color_c = vec3(0.6,0.5,0.0);
    float s = 10.0;
    uv *= s;
    uv.x += iTime;
    float y1 = mod(uv.x,1.0) + sin(uv.x / s + iTime * 2.0)+ sin(uv.y / s + iTime);
    float y2 = mod(uv.x,1.0) + sin(uv.x / s + iTime + 1.0 * 2.0) + sin(uv.y / s + iTime);
    vec3 col = color_a + vec3(tri(mod(uv,1.0), y1) * color_b + vec3(tri(mod(uv,1.0), y2)) * color_c);
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(col,col.r);
}

vec3 drawGrid(float step, vec3 color, vec2 uv){
    float len = 0.005;
    float blur = 0.01;
    vec3 res = vec3(1.0);


	if(abs(uv.x - floor(uv.x/step) * step) < len 
       || abs(uv.x - ceil(uv.x / step) * step) < len
       || abs(uv.y - floor(uv.y/step) * step) < len 
       || abs(uv.y - ceil(uv.y / step) * step) < len
      )
        res = color;

    //y_axis
    if(abs(uv.x) < len)
        res = vec3(1.0,.0,0.0);

   	//x_axis
    if(abs(uv.y) < len)
        res = vec3(0.0,.0,1.0);

    return res;
}

vec3 drawCircle(vec2 center, float radius, vec2 uv, bool full){
	float e = 0.01;
    float len = length(center - uv);
    if(full)
        return vec3(smoothstep(radius - e, radius, abs(len - radius)));
    return vec3(smoothstep(0.0, e, abs(len - radius))); 
}

vec3 drawSegement(vec2 begin, vec2 end, vec2 uv, float width){
	vec2 dis = end - begin;
    vec2 dis1 = uv - begin;

    float t = clamp(dot(dis1, dis) / dot(dis , dis), 0.0, 1.0);
    float d = length(dis1 - dis * t);
    return vec3(smoothstep(0.0, width, d));


}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 backGroundColor = vec3(1.0, 1.0, 1.0);


    // pixel coordinates (from -1 to 1)
    vec2 uv = fragCoord/iResolution.xy;
   	uv -= 0.5;
    uv *= 2.0;
    uv.x *= iResolution.x / iResolution.y;



    //grid
    float step = 0.2;
    vec3 c = vec3(0.8);
    vec3 gridColor = drawGrid(step, c, uv);

    //circle
    float radius = 0.5;
    vec2 circleCenter = vec2(.0);
    vec3 circleLine = drawCircle(circleCenter, radius, uv, false);

    //rotation/move point
    float pointRadius = 0.05;
    vec2 pointCenter = vec2(cos(iTime), sin(iTime));
    pointCenter *= radius;
    vec3 point = drawCircle(pointCenter, pointRadius, uv, true);

    //segement
    vec2 begin = vec2(.0);
    vec2 end = pointCenter;
    float width = 0.01;
    vec3 segement = drawSegement(begin, end, uv, width);

    vec3 color = backGroundColor * gridColor * circleLine * point *segement;

    fragColor = vec4(color, 1.0);

}

/*
	Shader Sundays! (11/52) 
	"Saturn & Titan!"

    The inspiration for this shader came from the beautiful NASA pictures you see below.
	Specifically this one: 
	https://d.ibtimes.co.uk/en/full/1653195/saturn-rings.jpg
	https://solarsystem.nasa.gov/planets/saturn/overview/

	I added Titan because it has such a nice color and it broke the monotony of the scene a 
	bit. I cannot wait for KSP2 to come out :D!
*/
float
hash(vec2 n){
    return fract(sin(dot(n,vec2(12.9898, 4.1414)))*43758.5453);
}

float
sdRing(vec3 p)
{
    vec3 pos = p - vec3(-0., -0.00, 0.0);

    float r = length(vec2(pos.x, pos.z));
    float ringSize = 2.0;
    float d = sdBox(pos, vec3(ringSize, 0.00002, ringSize));
    if(r > ringSize || r < 1.35) d += 0.001;
    if(r > 1.75 && r < ringSize - 0.18)  d += 0.001;
    if(r > 1.975 && r < ringSize - 0.02)  d += 0.001;

    return d;
}

#define SATURN_ID 0.0
#define MOON_ID 1.0
#define RING_ID 2.0
vec2
Map(vec3 p)
{
    vec2 res = vec2(1e10, -1.0);

    UOP(sdSphere(p, 0.85), SATURN_ID);
    UOP(sdRing(p), RING_ID);

    float ang = -iTime/ 12.0 + M_PI;
    float rad = 1.5;
    UOP(sdSphere(p - vec3(rad*sin(ang), 0.2, rad*cos(ang)), 0.05), MOON_ID);

    return res;
}

#define MAX_DIST 20.0
#define MIN_DIST 0.0001
#define MAX_STEPS 200
vec2
RayMarch(vec3 ro, vec3 rd)
{
    float t = 0.0;
    vec2 res = vec2(-1.0, -1.0);

    for(int i = 0; i < MAX_STEPS && t < MAX_DIST; ++i)
    {
        vec2 hit = Map(ro + t*rd);
        if(abs(hit.x) < t* MIN_DIST)
        {
            res = vec2(t, hit.y);
            break;
        }
        t+= hit.x;
    }

    return res;
}

vec3
CalcNormal(vec3 p)
{
    vec2 e = vec2(0.001, 0.0);
    return normalize(vec3(Map(p + e.xyy).x - Map(p - e.xyy).x,
                          Map(p + e.yxy).x - Map(p - e.yxy).x,
                          Map(p + e.yyx).x - Map(p - e.yyx).x
    ));
}

vec3
Material(float id, vec3 pos)
{
    vec3 col = vec3(1.0);

    if(id == SATURN_ID )
    {
        vec2 uv = vec2(atan(pos.x, pos.z) / (2.0*M_PI) + 0.5, pos.y *0.5 + 0.5);
        col = vec3(0.789, 0.765, 0.43);
        if(uv.y > 0.91) col *= 0.95;
    }

    if(id == RING_ID )
    {
        float r = length(pos.xz);
        r -= 1.0;
        r = 1.0 - r;
        col = mix(vec3(0.875, 0.7, 0.097) * 0.2, vec3(0.875, 0.7, 0.5)*1.0, r);
    }

    if(id == MOON_ID) col = vec3(0.25, 0.6, 0.375);

    return col;
}

float
CalcShadow(vec3 ro, vec3 rd)
{
    for(float t = 0.10; t < MAX_DIST;)
    {
        float h = Map(ro + t*rd).x;

        if(h < MIN_DIST)
            return 0.0;
        t += h;
    }
    return 1.0;
}

vec3
Render(vec3 ro, vec3 rd, vec2 uv)
{
    vec3 col = vec3(0.0);
    vec2 res = RayMarch(ro, rd);
    float t = res.x;
    float id = res.y;

    //Sky
    col += vec3(1.0) * pow(hash(uv * vec2(13.0, 2.2)), 703.58);
    col *= 6.0;

    //Opaque
    if(id >= 0.0)
    {
        //Geometry
        vec3 P = ro + t*rd;
        vec3 N = CalcNormal(P);

        //Material
        col = Material(id, P);

        //Lighting
        vec3 lin = vec3(0.0);
        float ang = M_PI / 4.2 + iTime / 25.0 ;
        vec3 L = normalize(vec3(cos(ang), 0.5, sin(ang)));
        float diff = saturate(dot(L, N));
        float amb = 0.05;

        //Shadowing
        diff *= CalcShadow(P, L);

        //Shading
        float isRing =  (id == RING_ID) ? 0.0 : 1.0;
        isRing *= P.y * 6.0;
        lin += 0.07 * amb  * vec3(1.0) * isRing;
        lin += 1.2 * diff * vec3(1.0, 1.0, 1.0);
        col *= lin;
    }
    return saturate(col);
}

#define AA 2
void
mainImage(out vec4 fragColor, in vec2 fragPos)
{
    float nearP = 1.8;
    float roll = 0.0;
    vec3 tot = vec3(0.0);

#if AA > 1
    for(int i =0; i < AA; ++i)
    for(int j =0; j < AA; ++j)
    {
        vec2 offset = 0.5 - vec2(i, j)/ float(AA);
        vec2 uv = ((fragPos + offset) - 0.5*iResolution.xy)/iResolution.y;
#else
        vec2 uv = ((fragPos) - 0.5*iResolution.xy)/iResolution.y;
#endif

        vec3 ta = vec3(0.0, 0.00, 0.0);
        vec3 ro = ta + vec3(0.0, 1.7, -4.5);
        mat3 cam = SetCamera(ro, ta, roll);
        vec3 rd = cam * normalize(vec3(uv, nearP));
        vec3 col = Render(ro, rd, uv);

        GAMMA(col);
        tot += col;
#if AA > 1
    }
    tot /= float(AA*AA);
#endif

    fragColor = vec4(tot, 1.0);
}

