////////////////////////////////////////////////////////////////////////////////
//
// Diamond Cubic structure
//
// Each 2x2x2 region is divided into 8 cubic cells, alternately type 0 and type 1
// Type 0 has an atom at alternating corners and at the centre, the central
// atom has a bond with each corner atom. Type 1 has an atom at the other set
// of alternating corners and is otherwise empty. The corner atoms overlap
// between cells.
//
// Controls:
// <mouse>: rotate
// <up>/<down>: move in/out
// <left>/<right>: fog distance
// i: apply inversion
// s: swap cell types
// r: show fundamental region
//
// For further details, see:
// https://en.wikipedia.org/wiki/Diamond_cubic
//
// Another nice diamond cubic shader:
// https://www.shadertoy.com/view/lsG3RG by kqyrt
//
// This shader uses some code from;
// https://www.shadertoy.com/view/WdsyzH[/url] by Axioplase
//
////////////////////////////////////////////////////////////////////////////////

const float PI = 3.14159;
int maxsteps = 200;
float maxdist = 15.0;
float pradius = 0.3;  // Point radius
float lradius = 0.1;  // Line radius
bool dorotate = true; // Autorotate
bool doinvert = true;
bool doswap = false;
bool showregion = false;

float segment(vec3 p, vec3 a, vec3 b) {
  vec3 pa = p - a;
  vec3 ba = b - a;
  float h = clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
  float d = length(pa - ba * h);
  return d;
}

float point(vec3 p, vec3 q) {
  return distance(p,q);
}

float cell0(vec3 p, out int type) {
  vec3 p0 = vec3(0);
  vec3 p1 = vec3(1,1,1);
  vec3 p2 = vec3(-1,-1,1);
  vec3 p3 = vec3(1,-1,-1);
  vec3 p4 = vec3(-1,1,-1);
  type = 0;
  float d = 1e8, d0 = d;
  d = min(d, point(p,p0)-pradius);
  d = min(d, point(p,p1)-pradius);
  d = min(d, point(p,p2)-pradius);
  d = min(d, point(p,p3)-pradius);
  d = min(d, point(p,p4)-pradius);
  if (d < d0) { d0 = d; type = 1; }
  d = min(d, segment(p,p0,p1)-lradius);
  d = min(d, segment(p,p0,p2)-lradius);
  d = min(d, segment(p,p0,p3)-lradius);
  d = min(d, segment(p,p0,p4)-lradius);
  if (d < d0) { d0 = d; type = 2; }
  return d;
}

float cell1(vec3 p, out int type) {
  vec3 p1 = vec3(-1,1,1);
  vec3 p2 = vec3(1,-1,1);
  vec3 p3 = vec3(1,1,-1);
  vec3 p4 = vec3(-1,-1,-1);
  type = 0;
  float d = 1e8, d0 = d;
  d = min(d, point(p,p1)-pradius);
  d = min(d, point(p,p2)-pradius);
  d = min(d, point(p,p3)-pradius);
  d = min(d, point(p,p4)-pradius);
  if (d < d0) { d0 = d; type = 1; }
  return d;
}

float getdist(vec3 p, out int type) {
  float k = 1.0;
  if (doinvert) {
    k = 0.3*dot(p,p);
    p /= k;
  }
  if (!showregion) {
    // Space division into 4x4x4 cubes
    p = mod(p+1.0,4.0)-2.0; // [-1,+3] -> [-2,+2]
  }
  // Parity of cell index
  int celltype = (int(p.x < 0.0)+int(p.y < 0.0)+int(p.z < 0.0)) % 2;
  if (doswap) celltype = 1-celltype;
  // mod not used for next step to preserve fundamental region.
  p += 1.0-2.0*step(0.0,p); // x -> x < 0 ? x+1 : x-1
  return k*(celltype == 0 ? cell0(p,type) : cell1(p,type));
}

float getdist(vec3 p) {
  int type;
  return getdist(p,type);
}

float march(vec3 ro, vec3 rd) {
  float t = 0.0;
  float precis = 0.0005;
  for(int i = 0; i < maxsteps; i++) {
    vec3 p = ro + t*rd;
    float d = getdist(p);
    // Longer distance estimates with inversion are
    // inaccurate, so clamp to avoid overstepping.
    if (doinvert) d = min(d,1.0);
    t += 0.6*d;
    if (t > maxdist || abs(d) < t*precis) break;
  }
  return t;
}

vec3 getnormal(vec3 p) {
  float d = getdist(p);
  vec2 e = vec2(0.001, 0);
  vec3 n = d - vec3(getdist(p-e.xyy),
                    getdist(p-e.yxy),
                    getdist(p-e.yyx));
  return normalize(n);
}

vec2 rotate(in vec2 p, in float t) {
  return p * cos(-t) + vec2(p.y, -p.x) * sin(-t);
}

vec3 transform(in vec3 p) {
  if (iMouse.x > 0.0) {
    float phi = (2.0*iMouse.x-iResolution.x)/iResolution.x*PI;
    float theta = (2.0*iMouse.y-iResolution.y)/iResolution.y*PI;
    p.yz = rotate(p.yz,-theta);
    p.zx = rotate(p.zx,phi);
  }
  if (dorotate) {
    p.yz = rotate(p.yz,iTime * 0.125);
    p.zx = rotate(p.zx,iTime * 0.1);
  }
  return p;
}

vec3 hsv2rgb(float h, float s, float v) {
  vec3 rgb = clamp( abs(mod(h*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
  rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing	
  return v * mix( vec3(1.0), rgb, s);
}

const int CHAR_A = 65;
const int CHAR_B = 66;
const int CHAR_C = 67;
const int CHAR_D = 68;
const int CHAR_I = 73;
const int CHAR_M = 77;
const int CHAR_O = 79;
const int CHAR_Q = 81;
const int CHAR_R = 82;
const int CHAR_S = 83;
const int CHAR_X = 88;
const int CHAR_Z = 90;

const int KEY_PAGE_UP = 33;
const int KEY_PAGE_DOWN = 34;
const int KEY_LEFT = 37;
const int KEY_UP = 38;
const int KEY_RIGHT = 39;
const int KEY_DOWN = 40;

bool key(int code) {
  return texelFetch(iChannel0, ivec2(code,2),0).x != 0.0;
}

vec4 store(int i,int j) {
  return texelFetch(iChannel1, ivec2(i,j),0);
}

int keycount(int key) {
  return int(store(0,key).x);
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
  vec2 uv = (2.0*fragCoord-iResolution.xy)/iResolution.y;
  vec2 m = iMouse.xy/iResolution.y;
  vec3 ro = vec3(0,0,-4);
  vec3 rd = normalize(vec3(uv,2));
  vec3 light = normalize(vec3(1,2,3));
  doinvert = key(CHAR_I);
  showregion = key(CHAR_R);
  doswap = key(CHAR_S);
  maxdist = 10.0*(exp(0.1*float(keycount(KEY_RIGHT)-keycount(KEY_LEFT))));
  ro.z += 0.25*float(keycount(KEY_UP)-keycount(KEY_DOWN));
  ro = transform(ro);
  rd = transform(rd);
  float t = march(ro, rd);
  vec3 basecol = hsv2rgb(0.1+0.01*iTime,1.0,1.0); //vec3(1,0.5,0);
  vec3 col = mix(vec3(1),basecol,0.75);
  if (t < maxdist) {
    vec3 p = ro+t*rd;
    int type;
    getdist(p,type);
    vec3 n = getnormal(p);
    float diffuse = mix(1.0,dot(n,light),0.5);
    float intensity = type == 1 ? 0.5 : 1.0;
    vec3 c = diffuse*intensity*basecol;
    float fog = t/maxdist;
    col = mix(c,col,fog);
  }

  col = pow(col,vec3(0.4545));	// gamma correction
  fragColor = vec4(col,1);
}

